#backdrop {background-color: var(--shadow3);}
#disclaimer-dialog {background: var(--post-bg); border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--border), 0 6px 16px var(--shadow3); overflow: hidden;}
#disclaimer-dialog .hd {background: var(--alt-post-bg); color: var(--text);}
#disclaimer-dialog .container-close, .closebutton {background: none; color: var(--text); font-size: 0;}
#disclaimer-dialog .container-close:hover, .closebutton:hover {color: var(--link);}
#disclaimer-dialog .container-close::before, .closebutton::before {content: "\f00d"; font: 14px/1 FontAwesome;}

/* News */
.content {color: var(--text);}
.content h3 {background: var(--post-bg); border-color: var(--border); border-radius: 3px; color: var(--text);}
.center img {border-color: var(--border);}
img[style="display: block; margin: auto; border: 1px dashed;"] {border-color: var(--border) !important;}
em[style="color: grey;"], em[style="color: grey; text-align: center;"] {color: var(--alt-text) !important;}

hr {background: transparent; border-top-color: transparent; color: var(--border);}

.redb {color: var(--red);}

/* Blog */
#masthead {background: var(--background);}
#content article.post {background: var(--post-bg);}
#content article.post .post-meta {background: var(--post-bg); border-top-color: var(--border);}
#content article.post .post-meta a {color: var(--alt-text);}
h1, h2, h3, h4, h5, h6 {color: var(--text);}
.post-inner ol {color: var(--alt-text);}
.post-inner blockquote, .post-inner blockquote p {color: var(--alt-text);}
.post-inner blockquote {border-left-color: var(--border);}

#sidebar {background: var(--background); color: var(--alt-text);}
#sidebar h1, #sidebar h2, #sidebar h3, #sidebar h4, #sidebar h5, #sidebar h6 {color: var(--text);}
#sidebar .widget {border-bottom-color: var(--border);}

/* FAQ */
.boxcontent {color: var(--text); line-height: unset;}
.boxcontent a {color: var(--link);}

.left-box {background: var(--post-bg); color: var(--text);}
.left-box .boxbar {background: var(--alt-post-bg);}
.boxcontent>ul {color: var(--text);}

.right-box {background: var(--post-bg); color: var(--text);}
.right-box .boxbar {background: var(--alt-post-bg); color: var(--text);}
.right-box .boxbar a {color: var(--text) !important;}
