@-moz-document domain("baidu.com") {
.s_form {}
#head {
    /*     box-shadow: 0 0 15px #00000024; */
    /*         backdrop-filter: saturate(180%) blur(20px); */
    /*     background: rgba(250,250,250,1); */
    /*     background: rgba(250, 250, 250, .49); */
    transition: all .5s;
}
#head.s_down {
    box-shadow: 0 0 15px #00000024;
    /*         backdrop-filter: saturate(180%) blur(20px); */
    background: rgba(250, 250, 250, 1);
    /*     background: rgba(250, 250, 250, .49); */
    transition: all .5s;
}
.fm {
    background: #fff;
    border-radius: 17px 0 0 17px;
    border: none;
}
.s_ipt_wr {
    border: 1px solid #e7e7e7;
    border-radius: 17px 0 0 17px;
    /* border-color: #7b7b7b #b6b6b6 #b6b6b6 #7b7b7b; */
    background: #fff;
    display: inline-block;
    vertical-align: top;
    width: 539px;
    margin-right: 0;
    border-right-width: 0;
    /*     border-color: #b8b8b8 transparent #ccc b8b8b8; */
    /*     border: none; */
    overflow: hidden;
    height: 33px;
}
.s_btn {
    width: 100px;
    height: 35px;
    color: #fff;
    letter-spacing: 1px;
    background: #09f;
    /*     border-bottom: 1px solid #2d78f4; */
    border-bottom: none;
    outline: medium;
    *border-bottom: 0;
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    border-radius: 0 17px 17px 0;
}
#s_tab {
    background: #fff;
    line-height: 36px;
    height: 38px;
    padding: 55px 0 0 121px;
    float: none;
    zoom: 1;
}
#s_tab b {
    border-bottom: 0px solid #d9d9d9;
    font-weight: 700;
    /*     font-size: 20px; */
    color: #0099ff;
}
.c-index {
    /*     display: inline-block; */
    padding: 0px 0;
    color: #fff;
    display: flex;
    flex-flow: column nowrap;
    width: 20px;
    align-items: center;
    justify-content: center;
    height: 20px;
    border-radius: 100%;
    line-height: 100%;
    font-size: 12px;
    text-align: center;
    /*     background-color: #8eb9f5; */
}
.t a,
.c-row a,
.c-gap-icon-right-small + a,
#u a {
    cursor: pointer;
    text-decoration: none;
    text-decoration-line: none;
    text-decoration-style: initial;
    text-decoration-color: initial;
    transition: all .65s;
}
.t a,
.c-row a,
.c-gap-icon-right-small + a {
    color: #0066cc;
}


.t a:hover,
.c-row a:hover {
    color: #000;
    /*     font-size: 20px; */
    cursor: pointer;
    text-decoration: none;
    text-decoration-line: none;
    text-decoration-style: initial;
    text-decoration-color: initial;
    transition: all .45s;
}
.t a em,
.c-row a em {
    font-style: normal;
    text-decoration: none;
    text-decoration-line: none;
    color: #bb0073;
    font-weight: 700;
}
#content_left .result-op,
#content_left .result {
    border-bottom: 1px solid #00000017;
    margin-bottom: 20px;
    padding-bottom: 20px;
    border-collapse: collapse;
}

.c-gap-top-small {
    margin-top: 10px;
}
.c-border {
    width: 518px;
    padding: 9px;
    border: 1px solid #0000000d;
    border-radius: 10px;
    /*     border-bottom-color: #e0e0e0; */
    /*     border-right-color: #ececec; */
    box-shadow: 0px 5px 10px rgba(0, 0, 0, .13);
    /*     -webkit-box-shadow: 1px 2px 1px rgba(0,0,0,.072);
    -moz-box-shadow: 1px 2px 1px rgba(0,0,0,.072);
    -o-box-shadow: 1px 2px 1px rgba(0,0,0,.072); */
}
.c-img {
    display: block;
    min-height: 1px;
    border-radius: 10px;
    box-shadow: 0 5px 7px 1px #00000021;
    border: 0 0;
}
.general_image_pic a {
    /*     background: #fff no-repeat center center;
    text-decoration: none;
    display: block; */
    overflow: visible;
    /*     text-align: left; */
}
.opr-toplist1-table .c-index {
    min-width: 14px;
    width: 20px;
    position: absolute;
    /*     left:-10px */
}
.container_l .opr-toplist1-cut {
    max-width: 247px;
    /*     margin-left:25px; */
    padding-left: 25px;
}
}

@-moz-document domain('skyscrapercity.com') {
    .structItem-title {
        width: calc(100% - 250px);
        display: inline-block;
    }
    .structItem-minor {
        text-align: right;
        width: 250px;
        display: inline-block;
    }
    .structItem-cell--main {
        min-height: 45px;
        display: flex;
        align-items: center;
    }
    ul.structItem-parts {
        display: none;
    }
    .truncate-username-text {
        display: none;
    }
}

