@-moz-document domain("bilibili.com") {
/* 全局色 */
#app,
.bb-comment,
#internationalHeader,
body,
.mini-header {
  background: black !important;
}
.mini-header .nav-link a,
.mini-header .nav-user-center span {
  color: white !important;
}
.mini-header .nav-user-center div:nth-child(3) .mini-upload {
  background-color: #f90 !important;
  color: black !important;
}
ul[class="nav-link-ul mini"] {
  margin-left: 70px;
}
.nav-link-item a[href="//www.bilibili.com"]::before {
  background: url(https://i.loli.net/2020/03/23/pTaXQMB6yN9WHZL.png) no-repeat -8px 0 !important;
  width: 70px;
  height: 32px;
  content: "" !important;
  background-size: 120%!important;
  padding-right: 5px;
}
.nav-link-item i {
  display: none !important;
}
/* 修改滚动条 */
.nav-search-box {
  width: 18vw !important;
  position: relative;
  float: left;
  margin: 0 154px;
}
.nav-search-box form {
  background: #363636;
  padding: 0 0 0 7px;
  border-radius: 3px;
  border: 0 !important;
}
.nav-search-box form:hover {
  border: #555555 1px solid !important;
}
.nav-search-box .nav-search {
  padding-top: 2px !important;
}
.nav-search-box .nav-search #nav_searchform {
  height: 28px !important;
  background: #363636 !important;
}
.nav-search-box input {
  height: 28px !important;
  color: #757575 !important;
  font-style: italic;
}
.nav-search-box .nav-search-btn {
  height: 28px  !important;
  width: 4vw;
  top: 2px;
  left: 100%;
  line-height: 28px;
  background-color: #f90 !important;
}
.nav-search-box .nav-search-btn button {
  color: black !important;
  top: 5px !important;
  left: calc(50% - 8px);
}
.nav-search-box .bilibili-search-suggest {
  background-color: #e3e1e1;
}
.nav-search-box .bilibili-search-suggest li em {
  font-weight: bold;
  color: black;
}
.nav-search-box .bilibili-search-suggest li:hover {
  background-color: #b4b3b3;
}

}

