        --ic-brand-Login-Content-border-color: none;
        --ic-brand-Login-Content-inner-bgd: #161616;
        --ic-brand-Login-Content-inner-border: #242424;
        --ic-brand-Login-Content-inner-body-bgd: #161616;
        --ic-brand-Login-Content-inner-body-border: none;
        --ic-brand-Login-Content-label-text-color: #3b73b4;
        --ic-brand-Login-Content-password-text-color: #3b73b4;
        --ic-brand-Login-footer-link-color: #ddd;
        --ic-brand-Login-footer-link-color-hover: #ddd;
        --ic-brand-Login-instructure-logo: #ddd;
    }

    ::-webkit-scrollbar {
        width: 12px;
    }

    ::-webkit-scrollbar-track {
        background: #202020;
        -webkit-box-shadow: inset 0 0 2px rgba(255, 255, 255, 0.2);
    }

    ::-webkit-scrollbar-thumb {
        background: #101010;
        -webkit-box-shadow: inset 0 0 2px rgba(255, 255, 255, 0.2);
    }

    a.ic-app-header__logomark {
        opacity: .85;
    }

    .discussion-section.discussion-assignment-section.alert.alert-info {
        background-color: #222;
    }

    span.discussion-points {
        color: #9E8B65;
    }

    code,
    pre {
        font-family: Monaco, Menlo, Consolas, "Courier New", monospace;
        font-size: 0.9rem;
        border-radius: 6px;
        background-color: #F5F5F517;
        border: 1px solid #C7CDD138;
    }

    pre {
        color: #C7BEAF;
        display: block;
        padding: 9px;
        margin: 0 0 12px;
        word-break: break-all;
        word-wrap: break-word;
        white-space: pre;
        white-space: pre-wrap;
    }

    h1.page-title {
        color: #C7BEAF !important;
    }

    .read .discussion-read-state {
        background: #ffffff99;
    }

    a.avatar {
        opacity: .86;
    }
}
