@-moz-document url-prefix("http:///*[[address]]*/:/*[[port]]*/") {
/* 
 * Name:    NZBGet Dark
 * Author:  enren
 * Version: 20203003
 */

:root {
  /*** Backgrounds ***/
  --main-bg-color: #111516;
  --secondary-bg-color: #181E23;
  --tertiary-bg-color: #202426;
  --highlight-bg-color: #343A3C;
	
  --inverse-bg-color: #909394;
  --inverse-h-bg-color: #A6A8A9;
	
  --btn-bg-color: #41494C;
  --btn-h-bg-color: var(--highlight-bg-color);
  --btn-primary-bg-color: rgba(70, 130, 180, 0.65);
  --btn-primary-h-bg-color: rgba(70, 130, 180, 0.45);
	
  --input-addon-bg-color: var(--highlight-bg-color);
	
  --dropdown-bg-color: var(--tertiary-bg-color);
  --dropdown-h-bg-color: var(--highlight-bg-color);
	
  --navbar-bg-color: #292E30;
	
  --menu-h-bg-color: #41494C;
	
  --table-bg-color: rgb(26, 31, 34);
  --table-h-bg-color: rgba(26, 31, 34, 0.2);
  --table-odd-bg-color: rgb(35, 42, 45);
  --table-checked-bg-color: #1E1D1A;
  --table-checked-h-bg-color: #181714;
  --table-sel1-bg-color: rgba(195, 203, 113, 0.25);
  --table-sel2-bg-color: rgba(195, 203, 113, 0.15);
	
  --progress-bg-color: #090B0C;
  --progress-checked-bg-color: #090908;
  --progress-success-bg-color: #559E83;
	
  --drag-bg-color: #41494C;
  --drag-badge-bg-color: rgb(174, 90, 65);
	
  --badge-active-bg-color: var(--btn-bg-color);
  --badge-info-bg-color: var(--btn-primary-bg-color);
	
  --lbl-important-bg-color: rgba(174, 90, 65, 0.8);
  --lbl-important-h-bg-color: rgba(174, 90, 65, 0.7);
  --lbl-warning-bg-color: rgba(195, 203, 113, 0.7);
  --lbl-warning-h-bg-color: rgba(195, 203, 113, 0.6);
  --lbl-success-bg-color: rgba(85, 158, 131, 0.8);
  --lbl-success-h-bg-color: rgba(85, 158, 131, 0.7);
  --lbl-info-bg-color: rgba(70, 130, 180, 0.8);
  --lbl-info-h-bg-color: rgba(70, 130, 180, 0.7);
  --lbl-inverse-bg-color: rgba(144, 147, 148, 0.65);
  --lbl-inverse-h-bg-color: rgba(144, 147, 148, 0.55);
	
  --alert-success-bg-color: rgba(85, 158, 131, 0.2);
  --alert-danger-bg-color: rgba(174, 90, 65, 0.2);
  --alert-error-bg-color: rgba(174, 90, 65, 0.2);
  --alert-info-bg-color: rgba(70, 130, 180, 0.2);
  --alert-warning-bg-color: rgba(195, 203, 113, 0.2);
  --alert-inverse-bg-color: rgba(144, 147, 148, 0.65);
	
  /*** Foregrounds ***/
  --main-fg-color: #BBB;
  --secondary-fg-color: #999;
	
  --a-fg-color: #588EBB;
  --a-dim-fg-color: #4682B4;
	
  --inverse-fg-color: #111;
  --inverse-dis-fg-color: #777;
	
  --btn-fg-color: #CCC;
  --btn-dis-fg-color: #666;
	
  --table-sel-fg-color: #CBA671;
	
  --badge-fg-color: #343A3C;
  --badge-active-fg-color: var(--inverse-h-bg-color);
  --badge-info-fg-color: var(--main-fg-color);
	
  --help-fg-color: #AAA;
  --help-title-fg-color: #BE7A66;
	
  --failure-fg-color: #BE7A66;
	
  --statustime-orange-fg-color: #BB8558;
  --statustime-orange-h-fg-color: #BB8558;
	
  --search-optname-fg-color: var(--a-dim-fg-color);
	
  --log-fg-color: #888;
  --log-success-fg-color: rgb(85, 158, 131);
  --log-error-fg-color: rgb(174, 90, 65);
	
  /*** Borders ***/
  --main-border-color: #41494C;
  --secondary-border-color: #545A62;
  --modal-border-color: #565D60;
  --btn-border-color: #202426;
  --input-border-color: var(--main-border-color);
  --table-border-color: var(--main-border-color);
  --dropdown-border-color: var(--secondary-border-color);
  --drag-border-color: var(--secondary-border-color);
  --caret-border-color: var(--main-fg-color);
	
  --alert-success-border-color: rgb(85, 158, 131);
  --alert-danger-border-color: rgb(174, 90, 65);
  --alert-error-border-color: rgb(174, 90, 65);
  --alert-info-border-color: rgb(70, 130, 180);
  --alert-warning-border-color: rgb(195, 203, 113);
  --alert-inverse-border-color: #202426;
}

body {
    color: var(--main-fg-color);
    background-color: var(--main-bg-color);
}
a,
a:hover {
    color: var(--a-fg-color);
}
input,
textarea,
select,
.uneditable-input {
    color: var(--main-fg-color);
    border-color: var(--input-border-color);
}
input,
.input {
	background-color: var(--secondary-bg-color);
}
select {
    background-color: var(--secondary-bg-color);
    -webkit-appearance: none;
    -moz-appearance: none;
}
.btn {
	color: var(--btn-fg-color);
	text-shadow: none;
	background-color: var(--btn-bg-color);
    background-image: none;
	border-color: var(--btn-border-color);
	box-shadow: none;
}
.btn:hover,
.btn:active,
.btn.active {
	color: var(--btn-fg-color);
	background-color: var(--btn-h-bg-color);
	border-color: var(--btn-border-color);
}
.btn.disabled,
.btn[disabled] {
	color: var(--btn-dis-fg-color);
	background-color: var(--btn-bg-color);
	border-color: var(--btn-border-color);
}
.btn-primary {
	background-color: var(--btn-primary-bg-color);
	border-color: var(--btn-border-color);
	text-shadow: none;
}
.btn-primary:hover,
.btn-primary:active,
.btn-primary.active,
.btn-primary.disabled,
.btn-primary[disabled] {
	background-color: var(--btn-primary-h-bg-color);
	border-color: var(--btn-border-color);
	text-shadow: none;
}
.btn-inverse {
	color: var(--inverse-fg-color);
	text-shadow: none;
	background-color: var(--inverse-bg-color);
    background-image: none;
	border-color: var(--btn-border-color);
	box-shadow: none;
}
.btn-inverse:hover,
.btn-inverse:active,
.btn-inverse.active {
	color: var(--inverse-fg-color);
	background-color: var(--inverse-h-bg-color);
	border-color: var(--btn-border-color);
	text-shadow: none;
}
.btn-inverse.disabled,
.btn-inverse[disabled] {
	color: var(--inverse-dis-fg-color);
	background-color: var(--inverse-bg-color);
	border-color: var(--btn-border-color);
}
.btn-group .dropdown-toggle {
	color: var(--main-fg-color);
	text-shadow: none;
	background-color: var(--btn-bg-color);
    background-image: none;
	border-color: var(--btn-border-color);
	box-shadow: none;
}
.btn dropdown-toggle:hover {
	color: var(--main-fg-color);
	background-color: var(--btn-h-bg-color);
	border-color: var(--btn-border-color);
}
.dropup .caret,
.dropdown .caret {
    border-bottom-color: var(--caret-border-color);
}
.btn-primary .caret,
.btn-warning .caret,
.btn-danger .caret,
.btn-info .caret,
.btn-success .caret,
.btn-inverse .caret {
	border-top-color: var(--caret-border-color);
	border-bottom-color: var(--caret-border-color);
}
.nav-list .divider,
.dropdown-menu .divider {
	background: none;
    border-bottom: 1px solid var(--dropdown-border-color);
}
.caret {
    border-top-color: var(--caret-border-color);
}
label {
	color: var(--main-fg-color);
}
.label {
	padding: 1px 4px 0px; /* avoid label overlap */
	color: var(--inverse-fg-color);
	text-shadow: none;
}
.label:hover {
    color: var(--inverse-fg-color);
}
.label-important {
	background-color: var(--lbl-important-bg-color);
}
.label-important:hover {
	background-color: var(--lbl-important-h-bg-color);
}
.label-warning {
	background-color: var(--lbl-warning-bg-color);
}
.label-warning:hover {
	background-color: var(--lbl-warning-h-bg-color);
}
.label-success {
	background-color: var(--lbl-success-bg-color);
}
.label-success:hover {
	background-color: var(--lbl-success-h-bg-color);
}
.label-info {
	background-color: var(--lbl-info-bg-color);
}
.label-info:hover {
	background-color: var(--lbl-info-h-bg-color);
}
.label-inverse {
	background-color: var(--lbl-inverse-bg-color);
}
.label-inverse:hover {
	background-color: var(--lbl-inverse-h-bg-color);
}
.input-prepend .add-on,
.input-append .add-on,
.input-prepend .btn,
.input-append .btn {
	color: var(--main-fg-color);
	background-color: var(--input-addon-bg-color);
	border-color: var(--input-border-color);
	text-shadow: none;
}

/* Icons */
[class^="icon-"],
[class*=" icon-"],
[class^="img-"],
[class*=" img-"] {
	opacity: 0.65;
}
.icon-empty, .icon-plus, .icon-minus, .icon-ok,
.icon-play, .icon-pause, .icon-down, .icon-up,
.icon-bottom, .icon-top, .icon-back, .icon-forward,
.icon-edit, .icon-trash, .icon-downloads,
.icon-remove, .icon-close, .icon-merge, .icon-save,
.icon-split, .icon-postcard, .icon-link, .icon-alert,
.icon-process, .icon-process-auto, .icon-mask, .icon-mask-white {
	-webkit-filter: invert(100%);
	filter: invert(100%);
}
.icon-ring-red,
.icon-circle-red,
.icon-ring-fill-red {
	-webkit-filter: sepia(50%);
	filter: sepia(50%);
}
.icon-ring-blue,
.icon-ring-fill-blue {
	-webkit-filter: sepia(30%) hue-rotate(-30deg);
	filter: sepia(30%) hue-rotate(-30deg);
}
/* NAVBAR */
.img-download-green,
#PlayAnimation.play {
	-webkit-filter: hue-rotate(45deg);
	filter: hue-rotate(45deg);
	opacity: 0.55;
}
.img-download-orange,
#PlayAnimation.pause {
	-webkit-filter: hue-rotate(10deg);
	filter: hue-rotate(10deg);
	opacity: 0.6;
}
.img-download-btn {
	opacity: 0.75;
}
PlayBlockInner:hover .img-download-btn,
.PlayBlockInner:hover .img-download-btn {
	opacity: 0.9;
}
#PlayCaret {
	border-top-color: var(--caret-border-color);
	border-bottom-color: var(--caret-border-color);
}
.navbar-inner {
    background-color: var(--navbar-bg-color);
    background-image: none;
    box-shadow: none;
}
#InfoBlock div:hover {
	color: var(--main-fg-color);
}
#StatusTime.orange {
	color: var(--statustime-orange-fg-color);
}
#StatusTime.orange:hover {
	color: var(--statustime-orange-h-fg-color);
}
#NavLinks .badge {
	float: right;
	margin-left: 4px;
}
.navbar .nav > li > a {
	padding-bottom: 15px;
    color: var(--secondary-fg-color);
    text-shadow: none;
}
.navbar .nav > li > a:hover {
  color: var(--main-fg-color);
}
.navbar .nav .active > a,
.navbar .nav .active > a:hover {
	color: var(--main-fg-color);
	background: var(--main-bg-color);
	background: -webkit-linear-gradient(var(--tertiary-bg-color), var(--main-bg-color));
	background: linear-gradient(var(--tertiary-bg-color), var(--main-bg-color));
}
.navbar-search .search-query {
    color: var(--main-fg-color);
    background-color: var(--tertiary-bg-color);
}
.navbar-search .search-query:focus,
.navbar-search .search-query.focused {
    color: var(--main-fg-color);
    text-shadow: none;
    background-color: var(--secondary-bg-color);
}
.search-caret-button .caret,
.focused .search-caret-button .caret {
	border-top-color: var(--caret-border-color);
	border-bottom-color: var(--caret-border-color);
}
.navbar .dropdown-menu::after {
	border-bottom: 6px solid var(--dropdown-border-color);
}
#RefreshButton {
    background-color: var(--btn-bg-color);
    border-color: var(--btn-border-color);
}
#RefreshButton:hover {
    border-color: var(--btn-border-color);
}
/* Menus */
.menu-header {
	background-color: var(--menu-h-bg-color);
	color: var(--main-fg-color);
	text-shadow: none;
}
.badge {
	color: var(--badge-fg-color);
	text-shadow: none;
}
.badge-active {
	color: var(--badge-active-fg-color);
	background-color: var(--badge-active-bg-color);
}
.badge-info {
	color: var(--badge-info-fg-color);
	background-color: var(--badge-info-bg-color);
}
.badge-info:hover {
	background-color: var(--badge-info-bg-color);
}
.btn-group .dropdown-toggle {
	box-shadow: none;
}
.dropdown-menu {
	background-color: var(--dropdown-bg-color);
	border-color: var(--dropdown-border-color);
}
.dropdown-menu a {
	background-color: var(--dropdown-bg-color);
	color: var(--main-fg-color);
}
.dropdown-menu a:hover {
	background-color: var(--dropdown-h-bg-color) !important;
}
/* Tables */
.table,
.table a,
.table a:hover {
	color: var(--main-fg-color);
}
.table th,
.table td  {
	border-top-color: var(--table-border-color);
	background-color: var(--table-bg-color);
}
.table tbody + tbody {
	border-top-color: var(--table-border-color);
}
.table-bordered {
    border-color: var(--table-border-color);
}
.table-bordered th,
.table-bordered td {
	border-left-color: var(--table-border-color);
}
.table-striped tbody tr:nth-child(odd),
.table-striped tbody tr:nth-child(odd) th,
.table-striped tbody tr:nth-child(odd) td {
	background-color: var(--table-odd-bg-color);
}
.table tbody tr:hover,
.table tbody tr:hover th,
.table tbody tr:hover td {
	background-color: var(--table-h-bg-color);
}
table > thead > tr > th.table-selector {
	background: repeating-linear-gradient(-45deg, var(--table-sel1-bg-color), var(--table-sel1-bg-color) 6px, var(--table-sel2-bg-color) 6px, var(--table-sel2-bg-color) 12px);
	color: var(--table-sel-fg-color);
	text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.75), 0 1px 0 rgba(0, 0, 0, 0.75), -1px 0 0 rgba(0, 0, 0, 0.75);
}
.pagination a {
    border-color: var(--main-border-color);
}
.pagination .disabled span,
.pagination .disabled a,
.pagination .disabled a:hover {
	background-color: var(--secondary-bg-color);
}
.pagination .active a {
	color: var(--main-fg-color);
}
.pagination a:hover,
.pagination .active a {
	background-color: #36454F;
}
/* Checkmarks in table */
div.check {
	border-color: var(--main-border-color);
}
div.check:hover {
	border-color: #5AA6CB;
}
.img-check {
	-webkit-filter: invert(100%) hue-rotate(180deg) brightness(75%);
	filter: invert(100%) hue-rotate(180deg) brightness(75%);
}
tr.checked,
tr.checked td {
	background-color: var(--table-checked-bg-color);
}
.table-striped tbody tr.checked:nth-child(odd) td {
	background-color: var(--table-checked-bg-color);
}
.table tbody tr.checked:hover,
.table tbody tr.checked:hover td {
	background-color: var(--table-checked-h-bg-color);
}
.check-simple tr.checked,
.check-simple tr.checked td,
.table-striped.check-simple tbody tr.checked:nth-child(odd) td {
	background-color: var(--table-checked-bg-color);
}
.table.check-simple tbody tr.checked:hover,
.table.check-simple tbody tr.checked:hover td {
    background-color: var(--table-checked-h-bg-color);
}

/* Progress bars */
tr.checked:nth-child(odd) .progress,
tr.checked:nth-child(even) .progress {
	background-color: var(--progress-checked-bg-color);
}
.progress {
	background-color: var(--progress-bg-color);
}
.progress .bar {
	color: var(--main-fg-color);
	text-shadow: none;
	box-shadow: none;
}
.progress-success.progress-striped .bar {
	background-color: var(--progress-success-bg-color);
}
.progress-striped .bar {
	opacity: 0.6;
}
/* Modals */
.modal,
.modal-header,
.modal-body,
.modal-footer {
    background-color: var(--main-bg-color);
	color: var(--main-fg-color);
}
.modal {
	border: 1px solid var(--modal-border-color);
	box-shadow: none;
}
.modal-header {
	border-bottom: 1px solid var(--modal-border-color);
}
.modal-footer {
	border-top: 1px solid var(--modal-border-color);
	-webkit-box-shadow: none;
	box-shadow: none;
}
.help-block,
.help-inline,
.confirm-help-block {
	color: var(--help-fg-color);
}
.help-block .help-option-title {
	color: var(--help-title-fg-color);
}
.dragover,
.dragover .table-striped tbody tr:nth-child(odd) td,
.dragover .table-striped tbody tr:nth-child(odd) th {
	background-color: var(--alert-success-bg-color);
}
#AddDialog_URLLabel img {
	-webkit-filter: invert(93%);
	filter: invert(93%);
}
/* PopupNotification alerts */
.alert-success {
	background-color: var(--alert-success-bg-color);
    border-color: var(--alert-success-border-color);
    color: var(--main-fg-color);
	text-shadow: none;
}
.alert-danger {
	background-color: var(--alert-danger-bg-color);
    border-color: var(--alert-danger-border-color);
    color: var(--main-fg-color);
	text-shadow: none;
}
.alert-error {
	background-color: var(--alert-error-bg-color);
    border-color: var(--alert-error-border-color);
    color: var(--main-fg-color);
	text-shadow: none;
}
.alert-info {
	background-color: var(--alert-info-bg-color);
	border-color: var(--alert-info-border-color);
    color: var(--main-fg-color);
	text-shadow: none;
}
.alert-warning {
	background-color: var(--alert-warning-bg-color);
    border-color: var(--alert-warning-border-color);
    color: var(--main-fg-color);
	text-shadow: none;
}
.alert-inverse {
	background-color: var(--alert-inverse-bg-color);
	border-color: var(--alert-inverse-border-color);
	color: var(--inverse-fg-color);
	text-shadow: none;
}
/* Statistics dialog */
#StatDialog_Custom a,
#StatDialog_Custom a:hover,
#AddDialog_Files a,
#AddDialog_Files a:hover {
	color: var(--secondary-fg-color);
}
/* Config page */
#ConfigNav.nav-list a {
	color: var(--main-fg-color);
	background-color: var(--tertiary-bg-color);
	text-shadow: none;
}
#ConfigNav.nav-list > .active > a {
	color: var(--main-fg-color);
	background-color: var(--menu-h-bg-color);
}
#ConfigNav.nav-list a:hover,
#ConfigNav.nav-list > .active > a:hover {
	background-color: var(--highlight-bg-color);
}
#ConfigNav {
	border-color: var(--secondary-border-color);
	background-color: var(--tertiary-bg-color);
}
#ConfigContent .config-header {
	border-bottom: 1px solid var(--secondary-border-color);
}
#ConfigContent p.help-block {
	color: var(--help-fg-color);
}
#ConfigContent .help-option-title {
	color: var(--help-title-fg-color);
}
.failure-message {
	color: var(--failure-fg-color);
}
.config-settitle {
	background-color: var(--highlight-bg-color);
	color: var(--main-fg-color);
	border-bottom-color: var(--secondary-border-color);
}
#DownloadsEdit_ParamTab div.control-group.wants-divider,
#HistoryEdit_ParamTab div.control-group.wants-divider,
#ConfigContent div.control-group,
#ConfigContent.search div.control-group.multiset {
	border-bottom-color: var(--secondary-border-color);
}
.search .option-name:hover {
	color: var(--search-optname-fg-color);
}
hr {
	border-top: 1px solid var(--secondary-border-color);
	border-bottom: 1px solid var(--secondary-border-color);
}
#ConfigReloadInfo img {
	-webkit-filter: invert(93%) hue-rotate(180deg);
