@-moz-document domain("skyscrapercity.com") {
.node-icon,
.node-main,
.node-stats{
    padding-top: 1em;
    padding-bottom: 1em;
    vertical-align: top;
}

.node-title {
    margin: 0.3333em 0;
}

.node-description {
    display: none;
}

.node-subNodeMenu {
    display: block;
}

.node-subNodeMenu a.menuTrigger {
    display: none;
}

.node-subNodeMenu .menu {
    box-shadow: none;
    display: block;
    margin-top: 1em;
    max-width: none;
    opacity: 1;
    padding: 0;
    position: relative;
}

.node-subNodeMenu .menu-content {
    background-color: #f0f3f5;
    padding: 0;
}

.node-subNodeMenu .subNodeMenu {
    margin: 0;
    max-height: none;
    overflow: visible;
}

.node-subNodeMenu .subNodeMenu li > ol {
    display: none;
}

.node-subNodeMenu .subNodeLink {
    display: inline-block;
}

}

@-moz-document regexp("https?://(www.)?skyscrapercity.com/forums/(.+)") {
.node-subNodeMenu .subNodeMenu li > ol {
    display: block;
}

}

