/* ==UserStyle==
@name         Dark elementary.io
@description  Dark elementary.io поддерживает только главную страницу и мне лень делать страницы на github для этого проекта.
@version      1.0.0
@namespace    https://github.com/DopeCatSoft/RealDarkStylus
@author       dopecatsoft (http://dopecatsoft.c1.biz)
@license      CC-BY-NC-4.0
@preprocessor stylus
==/UserStyle== */

@-moz-document url-prefix("https://elementary.io/") {
html, body {
    background-color: #121212;
    color: #FFFFFFD3;
    fill: #FFFFFFD3;
}
	img {
		opacity: 0.65;
		filter: brightness(.8) contrast(1.2);
	}
@media screen and (min-width: 1200px) and (orientation: landscape) {
.section__showcase .bg{
  display: block;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url('data:image/png;base64,') center/cover no-repeat;
  width: 1194.11px;
  height: 645px; 
  padding-left: 1194.11px; 
}
}
@media screen and (max-width: 1200px){
.section__showcase .bg{
    filter: invert(.93);
}
}
.grey {
    background-color: #252525;
}
button:active, .button:active, button.active, .button.active, .target-amount.checked {
    background-color: #121212B3;
    background-image: none;
    border-color: #333;
    box-shadow: none;
}
button, select, .button, input{

    background-image: none;
    border: 1px solid #333;
    box-shadow: none;
    color: #333;
    text-shadow: none; 
    color: #FFFFFFB3;
}
#slingshot .app-display__description svg, #slingshot .app-display__description svg * ,#parental-controls .app-display__description svg, #parental-controls .app-display__description svg *, #logotype svg *{
    color: #FFFFFFB3!important;
    fill: #FFFFFFB3!important;
}
.pre-amount {
    color: #FFFFFFB3;
}
#appcenter {
    background-color: #121212;
    background-image: none;
    color: #ffffffb3;
    text-shadow: none;
}
#appcenter .button:first-child {
    background-color: #151515;
    color: #7239b3;
}
.button.flat:focus, .button.flat:hover {
    background: #151515;
    box-shadow: none;
}
.button.flat {
    background: #151515;
    transition: opacity 0ms ease;
}
#appcenter .button {
    border-color: #333;
    color: #ffffffb3;
}
}
