a.new, a.new:visited {
	color: black;
}

ul {
	list-style: disc;
}

#mw-navigation, #mw-page-base, #mw-head-base, #footer {
	/* nuke everything but the content */
	display: none;
}

/* Content Box */

.markdown-body {
	max-width: 720px;
	margin: 1em auto;
}

#content, div#content {
	border: none;
	color: black;
}

#firstHeading, #siteSub {
	text-align: center;
	display: block;
}

#siteSub {
	margin-top: -0.5em;
	margin-bottom: 4em;
}

/* Article Body */

.markdown-body {
	font-size: inherit;
	text-align: justify;
	-moz-hyphens: auto;
	-webkit-hyphens: auto;
	hyphens: auto;
	line-height: 1.5em;
}

.markdown-body blockquote {
	font-size: inherit;
}

.markdown-body {
	padding: 2em 0;
}

.markdown-body h1, .markdown-body h2, .markdown-body h3, .markdown-body h4, .markdown-body h5, .markdown-body h6, div#content h1, div#content h2, div#content #firstHeading {
	font-family: inherit;
	line-height: 1.5em;
	margin-bottom: 0.5em;

	-moz-hyphens: none;
	-webkit-hyphens: none;
	hyphens: none;
}

.markdown-body h1, div#content h1 {
	font-size: 2em;
}

.markdown-body h2, div#content h2 {
	font-size: 1.5em;
}

.markdown-body h3 {
	font-size: 1.2em;
}

.markdown-body h4 {
	font-size: 1.1em;
}

.markdown-body h5, .markdown-body h6 {
	font-size: 1.0em;
}

.markdown-body p {
	margin: 0;
}

.markdown-body p + p {
	text-indent: 2em;
}

.markdown-body ul {
	margin-top: 0.5em;
	margin-bottom: 0.5em;
}

.mw-editsection {
	/* hide more non-content */
	display: none;
}

table.ambox {
	margin-bottom: 1em;
}

dl dd {
	/* center definitions (most useful for display equations) */
	text-align: center;
}

span.texhtml {
	/* revert inline math to default font */
	font-family: inherit;
	font-size: inherit;
	line-height: inherit;
}

.markdown-body img.centered {
	margin-left: auto;
	margin-right: auto;
	display: block;
}

/* Table of Contents */

#toc, .toc {
	border: none;
	padding: 0;
	background: none;
	font-size: inherit;
	/* span 100% of the width */
	display: block;
}

.markdown-body #toc h2, .markdown-body .toc h2 {
	font-family: inherit;
	font-size: 1.5em;
}

#toc h2, .toc h2 {
	display: block;
}

#toc #toctitle, .toc #toctitle, #toc .toctitle, .toc .toctitle {
	text-align: left;
}

.toctoggle {
	display: none;
}

/* Figures */

div.tright {
	margin: 0.5em 0 0.5em 1.5em;
}

div.tleft {
	margin: 0.5em 1.5em 0.5em 0;
}

/* Code */

.mw-code {
	margin: 1em 0;
}

pre, code {
	font-family: "Latin Modern Mono", monospace !important;
}

sup {
	text-indent: 0;
}

/* Table */

.markdown-body table {
	margin: 1.5em auto;
	width: fit-content;
}

.markdown-body table th {
    border: none;
    border-top: 1px solid #000;
		border-bottom: 1px solid #000;
		padding: 0px 6px;
		text-align: center;
}

.markdown-body table td {
    border: none;
		padding: 0px 6px;
}

.markdown-body table tr {
    border: none;
}

.markdown-body table tr:nth-child(2n) {
    background-color: transparent;
}

.markdown-body table tr:last-child {
    border-bottom: 1px solid #000;
}

/* Disable a::after */
@media print {
		a {
			text-decoration: none;
			color: blue !important;
		}
		a[href]:after {
				content: none;
		}
}

/* Disable comment */
#doc.comment-enabled {
	padding-right: 0;
}

.comment-buttons-container {
    display: none !important;
}
}

@-moz-document domain("hbr.org") {
div[class="article article-first-row"] {
    width: 150% !important;
}

h1[class="article-hed"] {
    width: 200% !important;
    margin-left: -170px !important;
    text-align: center;
}
}
