  .moveselect button:hover, .switchselect button:hover {
    background: transparent;
    border: none;
  }
  
  input[name="megaevo"] {
    position: absolute;
    margin-top: 50px;
  }
  
  input[name="megaevo"]::after {
    width: 50px;
  }
  
  /*****************************/
       /** Battle CSS **/
  /*****************************/

  .ps-room .battle-controls {
    background: transparent !important;
  }
}
