@-moz-document domain("t.bilibili.com") {
/* 背景色 */
#app {
    background: #080f1e;
}
#app .fixed-bg {
    display: none
}
/* 加载过程 */
#app .loading-content .loading-text .loading-img {
    display: none
}
/* 顶栏 */
#app .sticky-bar {
    background: #1a335b;
    box-shadow: none
}
#app .sticky-bar .bar-content .title {
    color: #c3c4ce
}
/* 左边栏-用户面板 */
#app .user-panel {
    background: #092951;
    height: auto;
}
#app .user-panel .content {
    height: 148px;
}
#app .user-pic {
    display: none;
}
#app .user-panel .content .user-name {
    padding-top: 35px;
    margin-left: 125px;
    color: #c3c4ce;
    font-size: 15px;
}
#app .user-panel .content .bottom .number-part .numbers {
    padding-top: 30px;
}
#app .user-panel .content .user-head {
    top: 15px;
    left: 40px;
}
}

@-moz-document domain("t.bilibili.com") {
/* 中间栏-发送栏 */
.home-page .home-container .home-content .center-panel .section-block,
.publish-panel {
    background-color: #092951!important;
}
.input-section .textarea {
    background-color: #121e3a!important;
    color: #8c9ab6
}
.input-section .placeholder-shown::before {
    color: #7896be;
}
.input-section {
    border: 1px solid #092951!important;
    color: #909aa4!important;
}
.input-section:hover,
.input-section:focus,
.input-section.active {
    background-color: #121e3a!important
}
.input-section .editor:focus,
.input-section .editor:hover {
    background-color: #262a38!important;
    color: #8fa8d4!important
}
.static-popup {
    background-color: #3d568e!important;
    border: none!important
}
.static-popup.bp-arrow:before {
    display: none!important
}
/* 表情 */
.static-popup .title {
    color: #fff
}
.static-popup .emoji {
    background-color: #3d568e;
    color: #9cb5e0
}
.static-popup .emoji:hover {
    background-color: #20335e
}
.static-popup .pagination {
    background-color: #253760
}
.static-popup .emoji-cover.selected {
    background-color: #4f6fb7
}
.static-popup .emoji-cover .kaomoji {
    color: #fff
}
/* 话题/@ */
#app .bg-white {
    background-color: #3d568e;
    border: none
}
#app .hash-popup .topic-container .item.is-selected,
#app .at-popup .user-container .item.is-selected {
    background-color: #1f2d4f;
    color: #9cb5e0
}
#app .hash-popup .topic-container .item,
#app .at-popup .user-container .item {
    color: #9cb5e0
}
/* 投票 */
.vote-box .create-vote {
    border: none;
    background-color: #3d568e;
}
.vote-box .title,
.vote-box .bp-radio,
.button-bar .bp-button {
    color: #fff
}
input::-webkit-input-placeholder {
    color: #a2b1c6;
}
.bp-input .input-box .indicator,
.bp-vote-container .bp-selector select {
    background-color: #3d568e;
    color: #a2b1c6;
}
/* 定时 */
.input-section .schedule-panel,
.schedule-panel .date-input {
    background-color: #0c1937
}
.schedule-panel .date-input > input,
.schedule-panel .date-input > input:focus,
.schedule-panel .scroll-input .s-input,
.schedule-panel .scroll-input .s-input:focus,
.schedule-panel .s-panel,
.schedule-panel .date-panel,
.schedule-panel .date-panel:hover {
    background-color: #3d568e;
    border: none
}
.schedule-panel .s-item {
    color: #a2b1c6;
}
.date-panel .ym {
    border-bottom: none;
    color: #a2b1c6
}
.ym .left {
    border-left: 2px solid #a2b1c6
}
.ym .arrow {
    border-top: 2px solid #a2b1c6
}
.ym .right {
    border-right: 2px solid #a2b1c6
}
.md .week {
    color: #0a1737
}
.md .day.disabled {
    color: #333
}
.md .day {
    color: #ccc
}
}

