/* i really want this to be global */
@-moz-document url-prefix(https://web.whatsapp.com) {
  :root {
  --dark: #1f232aad;
  --darker: #1f232aad;
  --light: #d1d1d1;
  --lighter: #e9e9e9;
  --accent: #073d87;
  --icon: #e1e1e1;
  --shadow: rgba(0, 0, 0, 0.1);
  --mred: #a3525b;
  --mgreen: #70a352;
  --mblue: #527aa3;
  --hover-delay: 0.8s;
  --image: url(https://i.postimg.cc/7L8F317b/bg-59.jpg);
  --emojiOpacity: 0.75;
  --ui-font: "font_name", "Segoe UI", "Helvetica Neue", "Helvetica",
    "Lucida Grande", "Arial", "Ubuntu", "Cantarell", "Fira Sans", sans-serif;
  --version: "sherlock 39";
  --message: "39";
  --changes: "39";
}

.ZP8RM {
  background-color: #23282e !important;
}
/* Body reset. */
body {
  font-family: var(--ui-font);
  background: none;
  background-color: var(--dark);
}

/* Scrollbar track. */
*::-webkit-scrollbar-track {
  background-color: var(--shadow);
}
/* Scrollbar thumb. */
*::-webkit-scrollbar-thumb {
  background-color: var(--accent);
}
* {
  scrollbar-width: thin;
  scrollbar-color: var(--accent) var(--shadow);
}

/* Highlight selection. */
::selection {
  color: var(--light) !important;
  background-color: var(--accent) !important;
}
::-moz-selection {
  color: var(--light) !important;
  background-color: var(--accent) !important;
}
/* Input placeholders. */
::placeholder {
  color: var(--lighter) !important;
}
::-moz-placeholder {
  color: var(--lighter) !important;
}
::-webkit-input-placeholder {
  color: var(--lighter) !important;
}

/* Progress. */
progress[value]::-webkit-progress-bar {
  background: var(--darker);
}
/* Value. */
progress[value]::-webkit-progress-value {
  background: var(--accent);
}
progress {
  background: var(--darker);
}
::-moz-progress-bar {
  background: var(--accent) !important;
}

/* App wrapper. */
#app .app {
  background: none;
  background-color: var(--dark);
}

/* Log-in page. */
.landing-wrapper::before {
  opacity: 0.6;
  background-color: var(--accent) !important;
}
.landing-window {
  border-radius: 4px !important;
  border: 1px solid var(--shadow);
  box-shadow: 0 8px 32px 0 var(--shadow) !important;
  background-color: var(--darker) !important;
}
.landing-main + div {
  background-color: var(--darker) !important;
}
.landing-main + div img:not(:hover) {
  opacity: 0.3;
}
/* Text color. */
.landing-main * {
  color: var(--light);
}
/* Reload QR code. */
._1MOym {
  background-color: var(--darker) !important;
}
._3PxOr rect {
  fill: var(--dark) !important;
}
/* Fix QR code. */
.landing-wrapper div[data-ref] {
  border-radius: 0;
  filter: contrast(150%) !important;
  outline: 4px solid white !important;
}
/* Loader. */
#startup svg > circle {
  stroke: var(--lighter) !important;
}
/* Logo colors. */
span._3PxOr svg path:nth-child(1) {
  fill: var(--dark) !important;
}

._2HTIU {
  background: #20252c !important;
}
.selectable-text {
  color: white !important;
}
._6xQdq {
  color: white !important;
}
._F7Vk {
  color: white !important;
}
._11ARv {
  background-color: #020202 !important;
}

.WoUSh {
  color: white !important;
}
html[dir] ._26JG5 {
  background-color: transparent !important;
}
/* Landing page. */
.app .iFKgT {
  border: none;
  background-color: var(--dark) !important;
}
/* Image. */
.app .iFKgT [data-asset-intro-image] {
  max-width: 200px;
  max-height: 200px;
  border-radius: 50%;
  filter: opacity(0.75);
  transition: opacity 1s ease !important;
}
/* Text color. */
.app .iFKgT * {
  color: var(--light);
}
/* Accent bar. */
.app .iFKgT::after {
  border-top-color: var(--accent) !important;
}
/* Hide the text. */
.iFKgT ._3mkas {
  font-size: 0px;
}
/** Theme note. */
.app .iFKgT ._3mkas::before {
  display: block;
  margin-bottom: -20px;
  font-size: 16px;
  content: var(--message) var(--changes) !important;
}
/** Theme info. */
.app .iFKgT ._3mkas::after {
  display: block;
  margin-top: 10px;
  padding-top: 10px;
  font-size: 16px;
  content: var(--version) !important;
  border-top: 1px solid var(--darker);
}

/* Left drawer. */
#app .app #side {
  background-color: var(--darker);
}

/* Chat list. */
#side > header {
  background-color: var(--darker) !important;
}

/* Enable notifications. */
#side ._2GB6m {
  background-color: #23282e !important;
}
/* Low battery. */
#side ._2t3oV {
  background-color: var(--mred) !important;
}
/* Phone not connected. */
#side ._1puWZ {
  background-color: var(--mred) !important;
}
/* Text color. */
#side ._2GB6m *,
#side ._1puWZ * {
  color: var(--lighter);
}

/* Global -> Search. */
