data.css:
@-moz-document domain("podio.com") {
html {
    color: #000;
    font-family: raleway,sans-serif;
    font-weight: 600;
    background-image: url(https://trello-backgrounds.s3.amazonaws.com/5143c001f780afe850001730/1920x1080/508e9ee54551493b36e40bad41754650/Bugatti-Pillars.jpg); 
    background-size: cover;
    background-attachment: fixed;
    min-height: 100%;
    position: relative;
}

body {
    background-image: url(https://trello-backgrounds.s3.amazonaws.com/5143c001f780afe850001730/1920x1080/508e9ee54551493b36e40bad41754650/Bugatti-Pillars.jpg); 
    background-size: cover;
    background-attachment: fixed;
    line-height: 160%;
    position: relative;
}

/* BLACK HEADER */ 
.space-switcher-wrapper.highlight {
     background: #45484d;
    background: -moz-linear-gradient(top, #45484d 0%, #000000 100%);
    background: -webkit-linear-gradient(top, #45484d 0%,#000000 100%);
    background: linear-gradient(to bottom, #45484d 0%,#000000 100%);
}

#header-global {
    height: 41px;
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 101;
     background: #45484d;
    background: -moz-linear-gradient(top, #45484d 0%, #000000 100%);
    background: -webkit-linear-gradient(top, #45484d 0%,#000000 100%);
    background: linear-gradient(to bottom, #45484d 0%,#000000 100%);
    border-bottom: 1px solid #000;
    transition: all .2s ease-in-out;
    transition-property: right,margin-right;
    margin-right: 0;
}

html #header-global .nav-box>.inner .user-functions {
     background: #45484d;
    background: -moz-linear-gradient(top, #45484d 0%, #000000 100%);
    background: -webkit-linear-gradient(top, #45484d 0%,#000000 100%);
    background: linear-gradient(to bottom, #45484d 0%,#000000 100%);
    /* background: linear-gradient(to right,rgba(80,146,189,0),#5092bd 10px); */
    float: none;
    position: absolute;
    top: 0;
    right: 0;
    height: 41px;
    padding-left: 10px;
}

#stream .object {
    padding: 20px 0;
    border-bottom: 1px solid #e9e9e9;
    position: relative;
    background-color: white;
}

#header-global .podio-search-box {
    font-size: 13px;
    font-size: 1.3rem;
    transition: all .15s,.2s ease-in;
    transition-property: width,background-color;
     background: #45484d;
    background: -moz-linear-gradient(top, #45484d 0%, #000000 100%);
    background: -webkit-linear-gradient(top, #45484d 0%,#000000 100%);
    background: linear-gradient(to bottom, #45484d 0%,#000000 100%);
}


/* BLACK LOGO */
html #header-global .nav-box>.inner .logo {
    float: left;
    position: relative;
    transition: all .2s ease-in;
    transition-property: background-color;
    overflow: hidden;
    margin: 0 auto;
    width: 87px;
    height: 41px;
    background: #45484d;
    background: -moz-linear-gradient(top, #45484d 0%, #000000 100%);
    background: -webkit-linear-gradient(top, #45484d 0%,#000000 100%);
    background: linear-gradient(to bottom, #45484d 0%,#000000 100%);
}

/* SUBNAV */
#space-navigation {
    -moz-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    user-select: none;
    box-sizing: border-box;
    transition: all .2s ease-in;
    transition-property: opacity;
    line-height: 16px;
    position: relative;
    padding: 0px 20px 10px 10px;
    background: #ffffff;
    background: -moz-linear-gradient(top, #ffffff 0%, #f6f6f6 47%, #ededed 100%);
    background: -webkit-linear-gradient(top, #ffffff 0%,#f6f6f6 47%,#ededed 100%);
    background: linear-gradient(to bottom, #ffffff 0%,#f6f6f6 47%,#ededed 100%);
    border-bottom: 2px solid #f6f6f6;
    box-shadow: 0 3px 3px 0 rgba(50,50,50,.09);
}

/* TASK TITLE FIX */
.items-section .body .content-row>ul.tasks-list .task-list li .inner a {
    color: inherit;
    margin-left: 5px;
}


/* FOOTER */
.dashboard .gridster-widget .footer a, .dashboard .gridster-widget .footer a:active, .dashboard .gridster-widget .footer a:visited {
    color: crimson;
}


/* LINKS AND TITLES */
a:active, a:link, a:visited {
    color: crimson;
}

.header-sub-title>a:active, .header-sub-title>a:link, .header-sub-title>a:visited, .header-title>a:active, .header-title>a:link, .header-title>a:visited, .sub-navigation .tabs ul>li h5>a:active, .sub-navigation .tabs ul>li h5>a:link, .sub-navigation .tabs ul>li h5>a:visited {
    color: crimson;
}

.app-view-component .app-views-list__name {
    font-size: 14px;
    font-size: 1.4rem;
    font-family: Helvetica,Arial,sans-serif;
    font-style: normal;
    font-weight: 200;
    color: crimson;
}

.header-sub-title, .header-title, .sub-navigation .tabs ul>li h5 {
    font-size: 17px;
    font-size: 1.7rem;
    font-family: citrixsans,Helvetica,Arial,sans-serif;
    color: crimson;
    transition: color .1s ease;
    font-weight: bold;
}

  /* Page background */
.article-body {
    word-wrap: break-word;
    line-height: 180%;
    font-size: 16px;
    background-color: white;
    padding: 20px;
}

.article-view h1 {
    font-family: Arial,Helvetica,sans-serif;
    margin-bottom: 20px;
    background-color: white;
    padding: 20px;
}

.article-view .side-column {
    padding-top: 21px;
    padding-left: 40px;
    padding-bottom: 21px;
    border-left: 1px solid #efefef;
    background-color: white;
}

.article-view .main-column {
    margin-top: 30px;
    float: left;
    width: 618px;
    background-color: white;
}

.sub-nav .breadcrumbs {
    float: left;
    font-size: 12px;
    margin: 0 0 30px;
    background-color: white;
    padding: 5px;
}
  
/* Bugatti BACKGROUND IN APPS VIEW */
.app-view__content .app-wrapper__content {
    -webkit-flex: 1 1 auto;
    -moz-flex: 1 1 auto;
    -ms-flex: 1 1 auto;
    flex: 1 1 auto;
    overflow-y: auto;
    overflow-x: hidden;
    position: relative;
    background-image: url(https://trello-backgrounds.s3.amazonaws.com/5143c001f780afe850001730/1920x1080/508e9ee54551493b36e40bad41754650/Bugatti-Pillars.jpg);
    padding: 10px;
}

/* REMOVING FOOTER */
.footer-global {display:none !important;}
}

/* ==UserStyle==
@name           Eutropia
@namespace      https://gitea.gelse.eu/Riedler/Userstyles-Riedler/src/branch/eutropia
@version        0.1.3
@description    A dark mode for dolphin-emu.org
@author         Riedler
@preprocessor	uso
@var	color	bg0		"Background 0"		#000
@var	color	bg1		"Background 1"		#151515
@var	color	bg2		"Background 2"		#222
@var	color	txt		"Text"				#BBB
@var	color	htxt	"Hovered Text"		#DDD
@var	color	btn		"Buttons"			rgb(55, 100, 153)
@var	color	hbtn	"Hovered buttons"	rgb(82,148,226)
@var	color	brd		"Borders"			#888
@var	color	inf		"Information Boxes"	rgba(55, 100, 153, .25)
@var	color	lnk		"Links"				rgb(55, 100, 153)
@var	color	hlnk	"Hovered links"		rgb(82,148,226)
@var	color	pl0		"Game: Perfect"		rgb(55, 100, 153)
@var	color	pl1		"Game: Playable"	#2A2
@var	color	pl2		"Game: Starts"		#CA0
@var	color	pl3		"Game: Broken"		#B33
@var	color	lgt		"Highlight"			#FFFFFF15
==/UserStyle== */
@-moz-document regexp("https?:\\/\\/(..\\.)?dolphin-emu\\.org($|\\/.*)") {
	::-webkit-scrollbar,::-webkit-scrollbar-track-piece{
		background-color:/*[[bg0]]*/ !important;}
	::-webkit-scrollbar-corner,::-webkit-scrollbar-thumb{
		background-color:/*[[bg2]]*/ !important;
		border-radius:0.4em;}
	:root{/*Because Firefox doesn't have the ::-moz-scrollbar selectors*/
		scrollbar-width:thin;
		scrollbar-color:/*[[bg2]]*/ /*[[bg0]]*/;}
	:root,
	body{
		background-color:/*[[bg0]]*/;}
    .col-row .col,
    .breadcrumb,
    .jumbotron{/*I'm not laughing, you're laughing!*/
		background-color:/*[[bg1]]*/;}
    .nav>li>a:hover,
	.nav>li>a:focus{
		background-color:/*[[bg2]]*/;}
    .compat-list tbody tr:nth-child(2n),
    code{
		background-color:/*[[lgt]]*/;}
    footer,
    .page-header,
	.versions-list tr:first-child,
	.versions-list tr.download{
		border-color:/*[[brd]]*/;}
    :not(.active)>a[href]:not([class^="tag"]):not(.btn),
	:not(.active)>a[href]:not([class^="tag"]):not(.btn) span{
		color:/*[[lnk]]*/}
	:not(.active)>a[href]:not([class^="tag"]):not(.btn):hover,
	:not(.active)>a[href]:not([class^="tag"]):not(.btn):hover span{
		color:/*[[hlnk]]*/}
    .compat-list tbody tr:nth-child(2n+1){
		background-color:transparent;}
    .col-row,
    img{
		border-radius:0.5rem;}
    :root,body,
	p,span,pre,code,
	h1,h2,h3,h4,h5,h6,
	td,dt,figcaption,li,
    .jumbotron h1,
	.jumbotron .h1,
    a[href]:not([class^="tag"]):not(.btn).rss-tag,
    ul.tag-cloud>li>a[rel="tag"]:not(.btn){
		color:/*[[txt]]*/;
        text-shadow:none !important}
    ul.tag-cloud>li>a[rel="tag"]:not(.btn):hover,
    a[href]:not([class^="tag"]):not(.btn).rss-tag:hover{
        color:/*[[htxt]]*/;
        background-color:/*[[hbtn]]*/;}
	body{
        padding-top:7rem;}
	.compat-list>tbody{
		background-color:/*[[bg1]]*/;
        border-radius:0.5rem;}
	#navbar-dolphin{
		background-image:none;
		background-color:/*[[bg1]]*/;
		position:fixed;
		z-index:3;
		top:0;
		width:100vw;
        height:5rem;}
	.btn-info,
	.btn-primary{
		background-image:none;
		background-color:/*[[btn]]*/;
		border:none;
		color:/*[[txt]]*/}
	.btn-info:hover,
	.btn-primary:hover{
		background-color:/*[[hbtn]]*/;
		color:/*[[htxt]]*/}
	pre{
		background-color:/*[[bg1]]*/;
		border-color:/*[[brd]]*/;}
	.alert-info{
		background-image:none;
		background-color:/*[[inf]]*/;
		border:none;
		color:/*[[txt]]*/}
	.alert{
		box-shadow:inset 0 1px 0 /*[[brd]]*/, 0 1px 2px rgba(0,0,0,0.05);}
	.well{
		background-color:/*[[bg1]]*/;
		background-image:none;
		border:none;
		border-radius:0.5rem;
		box-shadow:none;}
	.progress{
		background-color:/*[[bg2]]*/;
		background-image:none;}
	.progress-bar-info{
		background-color:/*[[pl0]]*/;
		background-image:none;}
	.progress-bar-success{
		background-color:/*[[pl1]]*/;
		background-image:none;}
	.progress-bar-warning{
		background-color:/*[[pl2]]*/;
		background-image:none;}
	.progress-bar-danger{
		background-color:/*[[pl3]]*/;
		background-image:none;}
	input[type="text"]{
		background-color:/*[[bg1]]*/;
		border:none;}
	.input-group>span>button{
		background-color:/*[[bg1]]*/;
		color:/*[[txt]]*/;}
	.input-group>span>button:hover{
		background-color:/*[[bg1]]*/;
		color:/*[[htxt]]*/;}
	ul.tag-cloud a,
	.rss-tag{
		background-color:/*[[btn]]*/;
		color:/*[[txt]]*/;}
	.rss-tag:hover,
	.rss-tag:focus{
		background-color:/*[[hbtn]]*/;
		color:/*[[htxt]]*/;}
	#toc{
		background-color:/*[[bg1]]*/;
		border:none;
		border-radius:0.5rem;}
	div.thumbinner{
		background-color:/*[[bg1]]*/;
		border:none;
		border-radius:0.5rem;}
	.category>img[title="Wiiware"],
	.category>img[title="Gamecube"],
	.category>img[title="Wii"],
	.category>img[title="Virtual-console"],
	.category>img[title="Channel"]{
		filter:invert(0.8);}
	img[src="//dolphin-emu.org/m/static/img/stars/5.7d3488bc4093.png"],
    img[src="//dolphin-emu.org/m/static/img/stars/4.5096f691933f.png"],
    img[src="//dolphin-emu.org/m/static/img/stars/3.43fe25f46703.png"],
    img[src="//dolphin-emu.org/m/static/img/stars/2.195f9785addd.png"],
    img[src="//dolphin-emu.org/m/static/img/stars/1.6dfb2570f44e.png"]{
        filter:invert(1) hue-rotate(175deg) brightness(2);}
	.pagination>li>a,
	.pagination>li>span{
		background-color:/*[[bg1]]*/;
		border-color:#0000;
		color:/*[[lnk]]*/;}
	.pagination>li.active>a,
	.pagination>li.active>span,
	.pagination>li.active>a:hover,
	.pagination>li.active>span:hover,
	.pagination>li.active>a:focus,
	.pagination>li.active>span:focus{
		background-color:/*[[btn]]*/;
		border-color:/*[[btn]]*/;
		z-index:1;
		color:/*[[txt]]*/;}
	.pagination>li>a:hover,
	.pagination>li>span:hover,
	.pagination>li>a:focus,
	.pagination>li>span:focus{
		background-color:/*[[bg2]]*/;
		color:/*[[hlnk]]*/;
		border-color:#0000;}
	.dropdown-menu{
		background-color:/*[[bg1]]*/;
		position:absolute;
		overflow-y:scroll;
		max-height:100vh;
		top:0;}
	.dropdown-menu>li>a:hover,
	.dropdown-menu>li>a:focus{
		background-image:none;
		background-color:/*[[bg2]]*/}
	::-webkit-scrollbar{
    	background:transparent;
		background-image:linear-gradient(to right,transparent 0,transparent 95%,/*[[bg1]]*/ 90%);}
	::-webkit-scrollbar-thumb{
		background-image:linear-gradient(to right,transparent 0,transparent 95%,/*[[btn]]*/ 90%);}
	::-webkit-scrollbar-thumb:hover{
		background-image:linear-gradient(to right,/*[[btn]]*/ 0,/*[[btn]]*/ 90%);
		box-shadow:0 0 0 100vh /*[[bg1]]*/;}
	.carousel-caption>p{
		text-shadow:2px 2px 0.1em #000 !important;
		color:/*[[htxt]]*/;
		font-weight:bold;
		display:none;}
	#carousel-sshot:hover .carousel-caption>p{
		display:initial;}
}
@-moz-document regexp("https?:\\/\\/(..\\.)?dolphin-emu\\.org\\/media($|\\/.*)"){
	#gallery>a{
		border:none;}
	img{
		border-radius:0.5rem;}
}
@-moz-document regexp("https?:\\/\\/forums\\.dolphin-emu\\.org($|\\/.*)"){
    #container{
        background-color:/*[[bg0]]*/;
        border:none;
        border-radius:0.5em;}
    table.tborder{
        background-color:/*[[bg1]]*/;
        border:none;}
    table.tborder td.thead,
    table.tborder td.tfoot{
        background-color:/*[[bg2]]*/;}
    table.tborder tr:nth-child(2n+1){
        background-color:/*[[bg2]]*/;}
    .smalltext,
    span,
    td,
    .pagination,
    .navigation .active,
    strong{
        color:/*[[txt]]*/;}
    #copyright,
    blockquote cite,
    strong{
        color:/*[[htxt]]*/;}
    a:link,
    .thead a:link{
        color:/*[[lnk]]*/;}
    a:link:hover,
    .navigation a:hover,
    .navigation a:active{
        color:/*[[hlnk]]*/;
        text-decoration:underline;}
    .forum_status{
        filter:invert(1) hue-rotate(180deg);}
    .pagination .pagination_current{
        background-color:/*[[hbtn]]*/;
        border-radius:0.5em;
        color:/*[[htxt]]*/;
        border:none;
        text-decoration:none;}
    .pagination .pagination_page,
    .pagination .pagination_last,
    .pagination .pagination_next{
        background-color:/*[[btn]]*/;
        border:none;
        color:/*[[txt]]*/;
        border-radius:0.5em;}
    .pagination .pagination_page:hover,
    .pagination .pagination_last:hover,
    .pagination .pagination_next:hover{
        background-color:/*[[hbtn]]*/;
        color:/*[[htxt]]*/;
        text-decoration:none;}
    .pagination .go_page>img{
        display:none;}
    .pagination a.go_page,
    #breadcrumb_multipage{
        border-bottom:solid 0.5em /*[[btn]]*/;
        border-right:solid 0.5em #0000;
        border-left:solid 0.5em #0000;
        border-top:none;
        padding:0;
        border-radius:0;
        line-height:0.5em;
        background-color:#0000;}
    .pagination .drop_go_page{
        background-color:/*[[inf]]*/;}
    input.textbox{
        background-color:/*[[bg1]]*/;
        border:solid 1px /*[[brd]]*/;
        color:/*[[txt]]*/;}
    input.textbox:focus{
        background-color:/*[[bg2]]*/;
        color:/*[[htxt]]*/;}
    input.button{
        background-color:/*[[btn]]*/;
        background-image:none;
        color:/*[[txt]]*/ !important;
        border:none;}
    input.button:hover{
        background-color:/*[[hbtn]]*/;
        background-image:none;
        color:/*[[htxt]]*/ !important;}
    .trow_sep{
        background-color:/*[[bg0]]*/;
        color:/*[[txt]]*/;}
    .star_rating{
        filter:invert(1) hue-rotate(175deg) brightness(2);}
    .star_rating a:not(.current_rating){
        filter:brightness(1.8) hue-rotate(30deg);}
    .thread_legend img,
    .thread_status{
        filter:brightness(0.8);}
    select{
        background-color:/*[[bg1]]*/;
        border:solid 1px /*[[brd]]*/;
        color:/*[[txt]]*/;
        border-radius:0.5em 0 0 0.5em;}
    select:focus{
        color:/*[[htxt]]*/;
        background-color:/*[[bg2]]*/;}
    #copyright>span::after{ /*inserts myself into the copyright notice*/
        content:"| Dark theme by Riedler"}
    .post>.post_date{
        background-image:linear-gradient(/*[[bg1]]*/ 0%,/*[[bg2]]*/ 50%);
        padding-top:2em;}
    .post>.post_author,
    .post>.post_content{
        background-color:/*[[bg2]]*/;
        border:none;}
    .post>.post_controls{
        background-image:linear-gradient(/*[[bg2]]*/ 50%,/*[[bg1]]*/ 100%);
        border:none;
        padding-bottom:2em;}
    .postbit_buttons>a:link{
        background-image:none;
        background-color:/*[[btn]]*/;
        color:/*[[txt]]*/;
        border:none;}
    .postbit_buttons>a:link:hover{
        background-color:/*[[hbtn]]*/;
        color:/*[[htxt]]*/;}
    .codeblock{
        background-color:/*[[bg0]]*/;
        border:none;}
    .buddy_status{
        filter:invert(1) hue-rotate(180deg);}
    .post>.post_author>.author_avatar>a>img{
        background-color:/*[[bg0]]*/;
        border:none;
        border-radius:1em;
        overflow:hidden;
        padding:0;}
    img[src="/images/groupimages/english/team-developer.gif"],
    img[src=" /images/groupimages/english/team-contentcreator.png"]{
        filter:brightness(0.8);}
    #breadcrumb_multipage{
        height:0;}
    #breadcrumb_multipage_popup{
        border:none;
        outline:none;
        background:/*[[inf]]*/;
        border-radius:0.5em;}
    #breadcrumb_multipage_popup>.pagination_current{
        background-color:/*[[btn]]*/;
        color:/*[[txt]]*/;}
    #breadcrumb_multipage_popup>.pagination_current:hover{
        background-color:/*[[hbtn]]*/;
        color:/*[[htxt]]*/;
        text-decoration:none;}
    .pm_alert{
        background-color:/*[[inf]]*/;
        color:/*[[htxt]]*/;
        border:none;}
    .sceditor-container,
    .sceditor-container>.sceditor-toolbar{
        background-color:/*[[bg2]]*/;
        border:none;}
    .sceditor-container>iframe{
        background-color:/*[[bg2]]*/;}
    blockquote{
        background-color:/*[[bg2]]*/;
        color:/*[[txt]]*/;
        border:none;}
    div.sceditor-group{
        background-color:/*[[bg1]]*/;
        border-bottom:none;}
    .sceditor-button:hover,
    .sceditor-button:active,
    .sceditor-button.active{
        background-color:/*[[bg0]]*/;}
    .popup_menu,
    .popup_menu .popup_item{
        background-color:/*[[bg2]]*/;
        color:/*[[txt]]*/ !important;}
    .popup_menu .popup_item:hover{
        color:/*[[htxt]]*/ !important;}
    hr,
    .navMain hr{
        background-color:/*[[brd]]*/;}
    fieldset{
        border-color:/*[[brd]]*/;}
    textarea{
        background-color:/*[[bg2]]*/;
        border:none;
        color:/*[[txt]]*/}
    #select2-drop{
        background-color:/*[[bg1]]*/;}
    #select2-results-1>li{
        background-color:/*[[bg1]]*/;
        color:/*[[txt]]*/;}
    #select2-results-1>li:not(:last-child){
        border-bottom:solid 1px /*[[brd]]*/;}
    .select2-search>input,
    .select2-search input.select2-active{
        background-color:/*[[bg1]]*/;
        background-image:none;
        color:/*[[txt]]*/;
        border:solid 1px /*[[brd]]*/;
        border-radius:0.5em;}
    .select2-container .select2-choice,
    .select2-container>.select2-choice.select2-default{
        background-color:/*[[bg1]]*/;
        background-image:none;
        text-decoration:none !important;}
    .select2-container .select2-choice .select2-arrow{
        background-image:none;
        background-color:/*[[bg1]]*/;
        border-left:none;}
    .select2-dropdown-open .select2-choice{
        box-shadow:none;}
    .pmspace_unused{
        background-color:/*[[bg2]]*/;}
    .pmspace_used.low{
        background-color:/*[[pl1]]*/;
        border:none;
        border-left:solid 1px /*[[pl1]]*/;}
    .pmspace_container{
        border:none;
        line-height:2;
        margin-bottom:-0.3em;
        background-color:#0000;}
}
@-moz-document regexp("https?:\\/\\/wiki\\.dolphin-emu\\.org($|\\/.*)") {
    :root,
    body,
    #mw-page-base{
        background-color:/*[[bg0]]*/;
        background-image:none;}
    div.vectorTabs,
