@-moz-document domain("golang.org") {
:root {
    --regular-text: #e3e3e3;
    --header-text: #ccd6e1;
    --button-background: #222;
    --button-border: #004b60;
}

html, body {
    background-color: black;
    color: var(--regular-text);
    display: block;
    margin-left: auto;
    margin-right: auto;
    width: 85%;
}

/*Some images*/
/*
List:
.Header-logo
.Hero-gopher
img.gopher
.Footer-gopher
*/
img {
    opacity: 0.8;
}

img:Hover {
    opacity: 1;
}

.HeroDownloadButton-image {
    filter: invert(90%);
    opacity: 1;
}

/*Header Menu*/
.HeaderSearch {
	border: 0.12rem solid #444;
	border-radius: 0.1875rem;
}

.HeaderSearch-input {
	background-color: #000;
    color: var(--regular-text);
}

.HeaderSearch-submit {
	background-color: #000;
	transition: background-color 200ms;
}

.HeaderSearch-icon {
	fill: #c6c6c6;
}

a, .exampleHeading .text, .expandAll {
	color: #009ec6;
}

.Header-menuItem, .Header-menuItem a:link, .Header-menuItem a:visited {
	color: var(--header-text) !important;
	text-align: center;
}

.Blog-when, .Footer-supportedBy, .Hero-description {
	color: #a6a6a6;
}

/*Articles*/
h1, h2, h3, h4 {
	color: var(--header-text) !important;
}

h2 {
	background: #24262b;
}

#sidebar {
	background: #171717;
}

#sidebar h2 {
	padding: 5px;
}

#content .title {
	padding: 5px;
}

a.downloadBox
{
    color: #eee;
    border: 1px solid var(--button-border);
    background: #252728;
}

table.codetable td, table.codetable th
{
    background: #252728;
    color: #eee;
}

table.codetable tr.highlight td
{
    background: #373a3c;
}

/*Code*/
pre {
	background: #171717;
}

pre .comment {
	color: #00a200;
}

pre .ln {
	color: #fff;
	background: #171717;
}

/*Playground embedded*/
div.play .input, div.play .output {
	border: #242424;
}

div.play .input, div.play .input textarea, div.play .output, div.play .output pre {
	background: #1e1e1e;
	color: #d0e0f0;
}

.output .system {
	color: #a4a4a4;
}

/*Try Playground*/
.Playground-selectExample {
	background-color: var(--button-background);
	border: 0.0625rem solid var(--button-border);
	font-size: 16px;
	height: 2.375rem;
	margin: 0 0 0.5rem 0;
	width: 100%;
}

.Playground-inputContainer, .Playground-input, .Playground-outputContainer, .Playground-output {
	background: #1e1e1e;
	color: #d0e0f0;
}

#banner {
	position: absolute;
	left: 0;
	right: 0;
	top: 0;
	height: 50px;
	background-color: #3d3f42;
}

/*Buttons*/
.Button--primary, .Button--primary:link, .Button--primary:visited {
	border: 0.0625rem solid var(--button-border);
}

.Button, .Button:link, .Button:visited {
	background-color: var(--button-background);
	box-shadow: 0 2px 5px rgba(255, 255, 255, .2);
	color: #00a0c8;
}
}

@-moz-document domain("go.dev") {
:root{
    --regular-text: #e3e3e3;
    --h3: #d3d9dd;
    --yellow-bg: #1c1b17;
}

html, body, .Site-content {
    background-color: black;
    color: var(--regular-text);
}

a, a:link, a:visited {
	color: #009ec6;
}

.Banner {
	background-color: #33322c;
}

/*Header*/
.Site-header, .bluebg {
	background: #004151;
}

/*Footer*/
.Footer {
	background-color: #0e131a;
}

.Site-footer {
	border: none;
}

/*Main page*/
.WhyGo, .WhoUses {
	background-color: #262626;
    color: var(--regular-text);
}

.WhoUses-caseStudy a:link:hover, .WhoUses-caseStudy a:visited:hover {
	color: var(--h3);
}

.UseCase-logo img {
    filter: invert(100%);
}

.LearnGo-subHeader h3 {
	color: var(--h3);
}

.LearnGo, .UseCases {
    color: var(--regular-text);
	background-color: #17171a;
}

/*Courses*/
.LearnGo-courseImage img, .Learn-tileThumbnail img {
	max-height: 80%;
	max-width: 80%;
}

.LearnGo-courseImage {
	background: none;
	box-shadow: 0 0.125rem 0.5rem 0 rgba(0, 0, 0, .22);
}

.Learn-tileThumbnail {
	background-color: #aaa;
	box-shadow: 0 0.125rem 0.5rem 0 rgba(255, 255, 255, .22);
}

.LearnGo-courseImage:Hover, .Learn-tileThumbnail:Hover {
	background-color: #eee;
}

/*Events*/
.LearnGo-events {
	background-color: #090909;
	border: 0.0625rem solid rgba(117, 117, 117, .19);
	box-shadow: 0 0.125rem 0.8125rem 0 rgba(0, 0, 0, .26);
}

.Learn-eventThumbnail--noimage, .LearnGo-eventThumbnail--noimage {
	background: none;
}

.LearnGo-eventThumbnail img, .Learn-eventThumbnail img {
    opacity: 0.7;
}

.LearnGo-eventThumbnail:Hover img, .Learn-eventThumbnail:Hover img {
    opacity: 1;
}

.LearnGo-eventDate p, .Learn-eventDate p {
	color: #a2a6aa;
}

.LearnGo-eventItem + .LearnGo-eventItem, .Learn-eventItem + .Learn-eventItem {
	border-top: 0.0625rem solid #333;
}

/*learn.go.dev*/
.Learn-gettingStarted {
	background-color: var(--yellow-bg);
}

.Learn-quickstart a, .Learn-quickstart a:visited, .Learn-quickstart a:hover {
	color: #c6cdd5;
}

.Learn-quickstartThumbnail {
	background: none;
    border: none;
}

.Learn-quickstartThumbnail:Hover {
	background: #eee;
	box-shadow: 0 0.125rem 0.8125rem 0 rgba(0, 0, 0, .26);
}

.Learn-viewMoreButton, .Learn-viewMoreButton:visited, .Learn-viewMoreButton:hover, .Learn-viewMoreButton {
	background-color: #141515;
	border: 0.0625rem solid #00add8;
	box-shadow: 0 0.125rem 0.3125rem 0 rgba(0, 0, 0, 0.2);
}

.Learn-inPerson:nth-child(1), .Learn-inPerson:nth-child(2) {
	border-top: 0.0625rem solid #5b5b5b;
}

.Learn-inPerson {
	border-bottom: 0.0625rem solid #5b5b5b;
}

.Learn-events {
	border-bottom: 0.0625rem solid #5b5b5b;
	border-top: 0.0625rem solid #5b5b5b;
}

/*solutions*/
.Solutions-headline {
	background-color: var(--yellow-bg);
}

.Solutions-caseStudy:hover, .Solutions-useCase:hover {
	box-shadow: 0 0.125rem 0.5rem 0 rgba(0, 0, 0, 0.22);
}

.Solutions-caseStudyTitle, .Solutions-useCaseTitle {
    color: var(--regular-text);
}

.Solutions-caseStudy a:link, .Solutions-caseStudy a:visited, .Solutions-useCase a:link, .Solutions-useCase a:visited {
	color: var(--regular-text);
}

.Solutions-caseStudyLogo {
    border-radius: 0.25rem;
	background-color: #666;
	box-shadow: 0 0 3px #eee;
}

.Solutions-caseStudyLogo:Hover {
	background-color: #eee;
}

/*Article*/
.Article {
	color: #eee;
}

.Article p, .Article ul, .Article ol {
	color: var(--regular-text);
}
}

