/*Colorthemes
355-pink
306-purple
242-lilac
221-navy blue
203-twitter blue*****
163-fresh green
102-spring green
41-brown
20-brown
0-red

42
*/

/********************
*******************Colors
***************************/
/*Theme 1 - White*/
body[style*="background-color: rgb(255, 255, 255);"] {
    --bodybg: hsl(var(--p, 203), 37%, 84%);
    --tweetbg: hsla(var(--p, 203), 89%, 98%,var(--a,1.0));
    --menubg: hsl(var(--p, 203), 89%, 97%);

    --border: rgba(0, 0, 0, 0.2);
    --article_border: hsl(var(--p, 203), 17%, 70%);
    --article_border_width: 1px;
    --lightborder: hsl(var(--p, 203), 37%, 84%);
    --box-shadow: 0px 0px 4px 2px rgba(0, 0, 0, 0.04);
    --border-radius: 15px;
    --opacity: 1.0;

    --topbarbg: hsl(var(--p, 203), 89%, 98%);
    --topbar-border: 1px solid hsl(var(--p, 203), 37%, 84%);
    --text: hsl(189, 11%, 19%);
    --lighttext: hsl(189, 8%, 25%);
    --graytext: hsl(var(--p, 203), 14%, 46%);
    --menutext: hsl(6, 30%, 9%);

    --quote: rgba(0, 0, 0, 0.02);
    --quote-border: hsl(var(--p, 203), 25%, 92%);
    --connector-color: hsl(var(--p, 203), 17%, 67%);

    --sidebar-title-text: hsl(189, 11%, 19%);
    --sidebar-title-text-shadow: none;
    --sidebar-trend-border: hsl(var(--p, 203), 89%, 98%);
    --trend-border-style: dashed;
    --sidebar-graytext: hsl(var(--p, 203), 14%, 46%);
    --sidebar-heading: hsl(189, 11%, 19%);

    --input: rgba(0, 0, 0, 0.1);
    --input-border: rgba(0, 0, 0, 0.2);
    --input-border-radius: 0px;

    --Tweet: none;
}

/*Theme 2 - Blue*/
body[style*="background-color: rgb(21, 32, 43);"] {
    --bodybg: hsl(var(--t,0), 51%, 16%);
    --tweetbg: hsl(var(--t,0), 45%, 20%);
    --menubg: hsl(var(--t,0), 45%, 20%);

    --border: rgba(0, 0, 0, 0.2);
    --article_border: /*rgba(255,255,255,0.05)*/rgba(255,255,255,0.08);
    --article_border_width: 1px;
    --border-radius: 15px;
    --lightborder: rgba(255,255,255,0.2);
    --box-shadow: 0px 0px 4px 2px rgba(0, 0, 0, 0.04);
    --opacity: 1.0;

    --topbarbg: hsl(var(--t,0), 45%, 20%);
    --topbar-border: 1px solid hsl(var(--t,0), 45%, 20%);

    --text: hsla(0, 0%, 100%, .850);
    --lighttext: hsl(var(--t,0), 61%, 86%);
    --graytext: hsl(var(--t,0), 35%, 62%);
    --menutext: hsla(0, 0%, 100%, .850);

    --quote: hsl(var(--t,0), 51%, 16%);
    --quote-border: rgba(0,0,0,0.2);
    --connector-color: hsl(var(--t,0), 32%, 38%);

    --sidebar-title-text: hsla(0, 0%, 100%, .850);
    --sidebar-title-text-shadow: none;
    --trend-border: 1px dashed rgba(255,255,255,0.2);
    --sidebar-graytext: hsl(var(--t,0), 35%, 62%);
    --sidebar-heading: white;

    --input: rgba(255,255,255,0.05);
    --input-border: rgba(255,255,255,0.06);
    --input-border-radius: 15px;

    --Tweet:none;
}

/*Theme 3 - Lights Out*/
body[style*="background-color: rgb(0, 0, 0);"] {
