@-moz-document domain("vimm.net") {

:root {
    --colorone: #282828;
    --colortwo: #151515
}

.menuColor,
#menuCorner,
#innerMain,
#innerMenu,
#ads {
    background: var(--colortwo) !important
}

#main {
    background: var(--colortwo)
}

a[href="/vault/GameCube"],
a[href="/?p=vault"],
img[src="/images/vault.png"] {
    display: none !important
}


a:link {
    color: #e1e1e1;
    font-weight: 300;
}

a:visited {
    color: #939393;
    font-family: 'Roboto', sans-serif;
    font-weight: 200;
}

input[name='q'] {
    background-color: var(--colorone);
    border: #000 solid 1px;
    padding: 5px;
    position: absolute;
    width: 90% !important;
    left: 5%;
    text-align: center;
}

a[href="vault?p=search"] {
    display: none
}

#vaultMenu {
    padding-top: 10px;
}

caption[style="font-size:28pt"] {
    border: solid 2px white;
    width: 50px;
    margin: auto;
    margin-bottom: 10px;
    border-radius: 10px
}

tr.even,
tr[class="odd"],
.rounded {
    background-color: var(--colorone)
}

#menu {
    background-color: var(--colorone)
}
}

