@-moz-document domain(bbs.io-tech.fi) {
    /* 2020-03-21 */
    /* Light user theme for bbs.io-tech.fi */
    /* Install Fira Sans -typeface before use */
    /* Made by Joonikko */

    /* Normalize css */
    html {
        line-height: 1.15;
        -webkit-text-size-adjust: 100%;
    }

    body {
        margin: 0;
    }

    main {
        display: block;
    }

    h1 {
        font-size: 2em;
        margin: 0.67em 0;
    }

    hr {
        box-sizing: content-box;
        height: 0;
        overflow: visible;
    }

    pre {
        font-family: monospace;
        font-size: 1em;
    }

    a {
        background-color: transparent;
    }

    abbr[title] {
        border-bottom: none;
        text-decoration: underline;
        text-decoration: underline dotted;
    }

    b,
    strong {
        font-weight: bolder;
    }

    code,
    kbd,
    samp {
        font-family: monospace, monospace;
        font-size: 1em;
    }

    small {
        font-size: 80%;
    }

    sub,
    sup {
        font-size: 75%;
        line-height: 0;
        position: relative;
        vertical-align: baseline;
    }

    sub {
        bottom: -0.25em;
    }

    sup {
        top: -0.5em;
    }

    img {
        border-style: none;
    }

    button,
    input,
    optgroup,
    select,
    textarea {
        font-family: inherit;
        font-size: 100%;
        line-height: 1.15;
        margin: 0;
    }

    button,
    input {
        overflow: visible;
    }

    button,
    select {
        text-transform: none;
    }

    [type="button"],
    [type="reset"],
    [type="submit"],
    button {
        -webkit-appearance: button;
    }

    [type="button"]::-moz-focus-inner,
    [type="reset"]::-moz-focus-inner,
    [type="submit"]::-moz-focus-inner,
    button::-moz-focus-inner {
        border-style: none;
        padding: 0;
    }

    [type="button"]:-moz-focusring,
    [type="reset"]:-moz-focusring,
    [type="submit"]:-moz-focusring,
    button:-moz-focusring {
        outline: 1px dotted ButtonText;
    }

    fieldset {
        padding: 0.35em 0.75em 0.625em;
    }

    legend {
        box-sizing: border-box;
        color: inherit;
        display: table;
        max-width: 100%;
        padding: 0;
        white-space: normal;
    }

    progress {
        vertical-align: baseline;
    }

    textarea {
        overflow: auto;
    }

    [type="checkbox"],
    [type="radio"] {
        box-sizing: border-box;
        padding: 0;
    }

    [type="number"]::-webkit-inner-spin-button,
    [type="number"]::-webkit-outer-spin-button {
        height: auto;
    }

    [type="search"] {
        -webkit-appearance: textfield;
        outline-offset: -2px;
    }

    [type="search"]::-webkit-search-decoration {
        -webkit-appearance: none;
    }

    ::-webkit-file-upload-button {
        -webkit-appearance: button;
        font: inherit;
    }

    details {
        display: block;
    }

    summary {
        display: list-item;
    }

    template {
        display: none;
    }

    [hidden] {
        display: none;
    }

    /* Theme itself */

    :root {
        --color-primary: #222;
        --color-secondary: #f0f0f0;
        --color-light1: #f0f0f0;
        --color-light2: #f7f7f7;
        --color-grey: #7c7c7c;
        --color-dimgrey: #696969;
        --color-link: #282c34;
        --color-green: #2dce89;
        --line-height: 1.2em;
        --font-family: "Fira Sans";
        --font-weight-bold: 600;
        --font-weight-normal: 500;
        --font-weight-light: 400;
        --font-size-large: 16pt;
        --font-size-medium: 11pt;
        --font-size-normal: 10pt;
        --font-size-small: 9pt;
        --structItem-spacing: 1px 0 1px 0;
    }

    body {
        align-items: center;
        background-color: var(--color-light1);
        display: flex;
        font-family: var(--font-family) !important;
        justify-content: center;
    }

    h3 {
        font-weight: var(--font-weight-normal);
    }

    .bbWrapper {
        line-height: var(--line-height) !important;
        font-family: var(--font-family) !important;
    }

    .block-body a {
        font-weight: var(--font-weight-normal);
        color: var(--color-link);
    }

    .button.button--scroll,
    a.button.button--scroll {
        display: none;
    }

    .block-container .block-header {
        font-size: var(--font-size-normal);
        font-weight: var(--font-weight-light);
    }

    .block-header-wrapper h2,
    .block-minorHeader a,
    .listHeap a {
        font-size: var(--font-size-normal);
    }

    .p-body-content .p-body-pageContent .block-container .block-body {
        background-color: var(--color-light2);
        color: var(--color-primary);
    }

    .block-minorHeader {
        height: 30px;
        display: flex;
        align-items: center;
        justify-content: left;
    }

    .button {
        font-size: var(--font-size-small);
        background-color: var(--color-primary);
        color: var(--color-secondary);
        height: 25px;
    }

    .contentRow-main a {
        font-weight: var(--font-weight-bold);
        font-size: var(--font-size-normal);
        color: var(--color-primary);
    }

    .contentRow-minor,
    .contentRow-minor ul,
    .contentRow-minor a,
    .u-dt[title] {
        font-size: var(--font-size-small);
        font-weight: var(--font-weight-light);
        color: var(--color-link);
    }

    .contentRow-title a {
        font-weight: var(--font-weight-light);
    }

    .footerList {
        line-height: 0.6em;
    }

    .label {
        font-weight: var(--font-weight-light);
        border: none !important;
        padding: 5px 10px 5px 10px;
        border-radius: 100px;
        box-shadow: none !important;
    }

    .label--red {
        background: #dc0001 !important;
    }

    .label--green {
        background: #2dce89 !important;
    }

    .label--blue {
        background: #4267b2 !important;
    }

    .label--skyBlue {
        background: #7cc3e0 !important;
    }

    .label--orange {
        background: #f5bb26 !important;
    }

    .label.yellow {
        background: #fff589;
    }

    .message-cell--user .message-userExtras {
        padding-top: 1em;
    }

    .message-cell.message-cell--user,
    .message-cell.message-cell--action {
        background-color: var(--color-light2);
    }

    .message-avatar-wrapper a {
        border-radius: 10px;
        width: 75px !important;
        height: 75px !important;
    }

    .message-header .message-attribution {
        height: 27px;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .message-header * {
        background-color: var(--color-primary);
        color: var(--color-light2) !important;
        font-weight: var(--font-weight-light);
        font-size: var(--font-size-small);
    }

    /* .message-attribution-main .u-dt,
.message-attribution a {
    font-weight: var(--font-weight-light);
    color: var(--color-light2);
    font-size: 8pt;
} */

    .message-footer .message-actionBar a {
        color: var(--color-grey);
    }

    .message-userBanner.userBanner {
        font-size: 8pt;
        font-weight: var(--font-weight-normal);
        width: 90%;
        border: none !important;
        border-radius: 15px;
        padding: 7px;
        display: flex;
        align-items: center;
        justify-content: center;
        box-shadow: none;
        opacity: 0.8;
    }

    .message-userDetails {
        display: flex;
        align-items: center;
        flex-direction: column;
    }

    /* .message-attribution-main .u-dt,
.message-attribution a {
    font-weight: var(--font-weight-light);
    color: var(--color-light2);
    font-size: 8pt;
} */

    .node-body .node-title a {
        font-size: var(--font-size-medium);
    }

    .node-subNodeFlatList a {
        font-weight: var(--font-weight-normal);
        color: var(--color-link);
    }

    .node-description {
        font-style: italic;
        font-weight: var(--font-weight-light);
        font-size: var(--font-size-small);
    }

    .notice-content {
        background-color: var(--color-primary);
        color: var(--color-secondary);
    }

    .notice-content font {
        color: var(--color-green);
    }

    .notices .notice.notice--primary {
        border: 1px solid var(--color-grey);
        margin: 10px 0 22px 0;
    }

    .notices--block {
        border: none;
    }

    .node + .node {
        border: none;
    }

    .node-extra--inner .node-extra-row .listInline a,
    .node-extra--inner .node-extra-row .listInline .node-extra-date {
        color: var(--color-primary);
        font-size: var(--font-size-small);
    }

    .node-subNodesFlat a {
        font-size: var(--font-size-small);
    }

    .p-title h1 {
        font-size: var(--font-size-large);
    }

    .p-body-inner {
        background-color: var(--color-secondary);
    }

    .p-body-header {
        color: var(--color-primary);
    }

    .p-description {
        color: var(--color-link);
    }

    .p-pageWrapper {
        max-width: 1400px;
    }

    .p-footer-copyright {
        display: none;
    }

    .p-breadcrumbs {
        background: transparent;
        border: none;
        padding: 0;
    }

    .p-breadcrumbs--parent {
        background: none;
    }

    .p-breadcrumbs--parent a {
        color: var(--color-primary);
    }

    .p-footer-custom .xb-footer-block:first-child {
        max-width: 250px;
    }

    .p-footer-custom .xb-footer-block .pairs {
        margin: 0;
        padding: 0;
    }

    .p-header-logo a {
        display: flex;
        justify-content: center;
        align-items: center;
        width: 100px;
    }

    .p-pageWrapper .p-nav-inner {
        height: 35px;
    }

    .p-nav-scroller,
    .is-calculated {
        display: flex;
        align-items: center;
        height: 100% !important;
        padding: 0 !important;
        margin: 0 !important;
        overflow: hidden !important;
    }

    .p-sectionLinks-inner {
        display: flex;
        height: 25px;
        align-items: center;
        justify-content: start;
    }

    .p-sectionLinks a {
        font-size: var(--font-size-small);
    }

    .p-nav-smallLogo img {
        display: none !important;
    }

    .p-nav .p-nav-scroller div {
        border: 0 !important;
        padding: 12px 0px 12px 0px;
    }

    .p-nav-scroller a {
        margin: 0 15px 0 15px;
        padding: 0 !important;
        height: 100% !important;
    }

    .pairs.pairs--justified > dt {
        color: var(--color-primary);
        font-size: var(--font-size-small);
        font-weight: var(--font-weight-normal);
    }

    /* .pairs.pairs--justified>dd {
    font-size: var(--font-size-small);
    font-weight: var(--font-weight-light);
    color: var(--color-primary);
} */

    .p-navgroup {
        font-size: var(--font-size-small);
        height: 37px;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .p-navgroup-link {
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .p-navgroup-link span {
        margin: 0 2px 0 2px;
    }

    .p-nav-list a {
        font-size: var(--font-size-small);
    }

    .structItem {
        border: none;
        margin: var(--structItem-spacing);
    }

    .structItem-title a,
    .structItemContainer-group.structItemContainer-group--sticky {
        border: none !important;
        font-weight: var(--font-weight-normal);
        font-size: var(--font-size-medium);
        color: var(--color-dimgrey);
    }

    .structItem-title {
        margin-bottom: 5px;
    }

    .is-unread a {
        color: var(--color-link) !important;
    }

    .structItem-minor a,
    .structItem-startDate .u-dt {
        font-size: var(--font-size-small);
    }

    .structItem-pageJump a {
        color: var(--color-primary);
        opacity: 0.6;
        font-size: var(--font-size-small);
        font-weight: var(--font-weight-normal);
        padding: 1px 3px 1px 3px;
    }

    .structItem-pageJump a:hover {
        color: var(--color-primary);
        opacity: 0.8;
    }

    .subNodeLink.subNodeLink {
        font-weight: var(--font-weight-normal);
        color: var(--color-grey);
    }

    .subNodeLink.subNodeLink--unread {
        font-weight: var(--font-weight-bold);
        color: var(--color-primary);
    }

    .tabs {
        background-color: var(--color-light1);
        color: var(--color-primary);
        border: none;
    }

    .tabs .hScroller {
        margin: 5px;
    }

    .xb-footer--wrapper {
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .xb-footer-block-4 {
        display: none;
    }

    select.input[size],
    .input.input--select[size],
    select.input[multiple],
    .input.input--select[multiple] {
        height: 100px;
    }

    @media only screen and (max-width: 1600px) {
        .p-pageWrapper {
            width: 100%;
        }
    }

    @media only screen and (min-width: 1599px) {
        .p-pageWrapper {
            width: 70%;
            max-width: 1400px;
        }
    }
}

