data.css:
@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
		input[type="checkbox"]:after {
			background: #ff80b0;
		}
		.entry {
			border-top: 0;
		}
		input, button, input:not([type]), #search, #manage\.newUI\.sort {
			background: var(--bg);
			border: 1px solid var(--b);
			color: var(--w);
		}
		button:active {
			background: var(--b-bg);
			border-color: var(-b);
		}
		.main-controls .style-name:hover, .style-name:hover, a:hover, .newUI .entry.enabled .style-name:hover .style-name-link {
			color: var(--w-h)
		}
		#header {
			border-right: 0;
			-webkit-box-shadow: none;
			box-shadow: none;
		}
		.newUI .entry .svg-icon {
			fill: #999;
		}
		#options-title {
			background: var(--bg);
			border-bottom: 1px solid var(--b);
		}
		#message-box > div {
			background: var(--bg);
			color: var(--w);
			box-shadow: none;
		}
		.newUI .can-update:not([data-details$="locally edited"]) .update:after {
			background: var(--b-bg);
			border: 1px solid var(--b);
			background: var(--b-bg);
			border: 1px solid var(--b);
		}
		.newUI .updater-icons > :not(.check-update):after {
			border: 1px solid var(--b);
			background: var(--b-bg);
			border: 1px solid var(--b);
			background: var(--bg);
		}
		.config-body label:not(:first-child) {
			border-top: 1px solid var(--b);
		}
		#message-box-buttons button:not(:disabled):hover {
			background: var(--bg);
			border-color: var(--b);
		}
		#message-box-buttons, .entry .menu, #options {
			background: var(--bg);
		}
		#message-box-title {
			background: var(--bg);
			color: var(--w);
		}
		body, #help-popup, .sort-wrapper, .usercss #name {
			color: var(--w);
			background: var(--bg);
			box-shadow: none;
		}
		button:not(:disabled):hover {
			background: var(--b-bg);
			border: 1px solid var(--b);
			color: var(--w-h);
		}
		select, input, button, input:not([type]), #search, #manage\.newUI\.sort {
			background: var(--bg);
			border: 1px solid var(--b);
			color: var(--w);
		}
		.cm-s-3024-night.CodeMirror {
			background: #000000;
			color: #d6d5d4;
		}
		.menu-items-wrapper {
			background: var(--bg);
			border: solid 2px var(--b);
		}
		.entry:nth-child(-n+10):before, .entry:nth-child(11):before, a {
			color: var(--w);
		}
		#confirm > div {
			background: var(--bg);
			border: 1px solid var(--b);
		}
		#hotkey-info[data-active] {
			border-left: 1px solid var(--b);
			box-shadow: none;
		}
		#hotkey-info div {
			border-top: 1px solid var(--b);
			background: var(--bg);
		}
		#hotkey-info mark {
			background: transparent;
			border: 1px solid var(--b);
			box-shadow: none;
			color: var(--w);
		}
		.svg-icon, .svg-icon.info:hover, .svg-icon.settings:hover, .svg-icon.select-arrow, a:hover .svg-icon {
			fill: var(--w-h);
		}
		.svg-icon:hover, .svg-icon.info, .svg-icon.settings, .add-applies-to:hover .svg-icon, .remove-applies-to:hover .svg-icon, .write-style-link, a svg:hover {
			fill: var(--w);
		}
		.add-applies-to .svg-icon, .remove-applies-to .svg-icon, .write-style-link {
			fill: var(--ic);
			color: var(--ic);
		}
		a svg {
			fill: var(--ic);
		}
		.newUI .entry:hover .svg-icon:hover {
			fill: var(--w);
			fill: var(--ic)
		}
		#header summary h2 {
			border-bottom: 0;
		}
		.newUI .entry.odd {
			background-color: transparent;
		}
		#filters label:hover, #filters .filter-selection:hover {
			background-color: transparent;
		}
		#backup-buttons .dropdown-content a {
			color: var(--w);
		}
		#backup-buttons .dropdown-content a:hover {
			background-color: var(--bg);
		}
		#backup-buttons .dropdown-content {
			background-color: var(--bg);
			box-shadow: none;
		}
		#backup-buttons .dropdown:hover .dropbtn {
			border-color: var(--b);
			background-color: var(--b-bg);
		}
		.section-editor .section:not(:first-child) {
			border-top: 2px solid var(--b);
		}
		.CodeMirror {
			border: solid 1px var(--b)
		}
		.resize-grip:after {
			border-top-style: solid
		}
		.CodeMirror-focused {
			outline: 0
		}
		:focus {
			outline: 0
		}
		.newUI .entry .style-name:hover::before {
			background: transparent
		}
		#stylus-options {
			border: 1px solid var(--b)
		}
		#options .block {
			border-bottom: 1px solid var(--b)
		}
		.onoffswitch input:checked + span, .onoffswitch input:checked + span::before {
			border-color: var(--b)
		}
		.onoffswitch input:checked + span {
			background-color: var(--bg-switch-control)
		}
		.onoffswitch span {
			border: 0;
			border-radius: 10em;
			background-color: var(--bg-switch-control);
			box-shadow: 0
		}
		.onoffswitch span::before {
			background: var(--w);
			border-radius: 10em
		}
		#options-close-icon .svg-icon {
			fill: var(--ic)
		}
		#options-close-icon:hover .svg-icon {
			fill: var(--w)
		}
		input::-webkit-outer-spin-button, input::-webkit-inner-spin-button {
			-webkit-appearance: none;
			margin: 0
		}
		input[type=number] {
			-moz-appearance: textfield
		}
		.entry .style-name::before {
			background: transparent
		}
		#disableAll-label:hover {
			color: var(--w-h)
		}
	:root {
		--w: rgba(255, 255, 255, 0.51);
		--w-h: rgba(255, 255, 255, 0.63);
		--bg: #191919;
		--b: #333;
		--ic: #666;
		--b-bg: #151515;
	}
	#installed {
		border-bottom: 0;
	}
	input[type="checkbox"] {
		width: 14px;
		height: 14px;
		margin: 0;
		display: block;
		padding: 0;
		z-index: 1;
		position: relative;
		font-size: 12px;
		visibility: hidden;
	}
	input[type="checkbox"]:before {
		visibility: visible;
		top: -2px;
		left: -3px;
		content: '';
		position: absolute;
		width: 14px;
		height: 14px;
		-webkit-border-radius: 2px;
		-moz-border-radius: 2px;
		border-radius: 2px;
		background-color: transparent;
		border: solid 1px var(--b);
	}
	input[type="checkbox"]:after {
		top: 1px;
		visibility: visible;
		content: '';
		position: absolute;
		width: 10px;
		height: 10px;
		-webkit-border-radius: 2px;
		-moz-border-radius: 2px;
		border-radius: 2px;
		opacity: 0;
		-webkit-transition: all 200ms linear;
		-moz-transition: all 200ms linear;
		-ms-transition: all 200ms linear;
		-o-transition: all 200ms linear;
		transition: all 200ms linear;
	}
	input[type="checkbox"]:checked:after {
		opacity: 1;
	}
	.onoffswitch input[type="checkbox"] {
		display: none;
	}
	.onoffswitch input[type="checkbox"]:before {
		display: none;
	}
	.onoffswitch input[type="checkbox"]:after {
		display: none;
	}
	.onoffswitch input:checked + span, .onoffswitch input:checked + span::before {
		border-color: var(--b);
	}
	.onoffswitch input:checked + span {
		background-color: var(--bg-switch-control);
	}
	input::-webkit-outer-spin-button, input::-webkit-inner-spin-button {
		-webkit-appearance: none;
		margin: 0;
	}
}

