@-moz-document domain("roblox.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("roblox.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

.bd-sidebar .bd-toc-link,
.bd-sidebar-wrap .toggle-sidebar a,
.home .section .section-title,
.home .section .sm-title,
.learn-roblox-pages .learning-materials-content .tab-content .learning-type
{
	text-transform: capitalize!important;
}

aside.quote .title,
.SquareTabGray,
.c-version .search-options .category-section,
.c-version .search-options .filters-section,
.c-version .search-options .search-options-form,
.drop-down-mode .d-header-icons .active .icon,
.form-control,
.input-group-btn .input-addon-btn,
.learn-roblox-pages .learning-materials-content .tab-content .inner-wrap:hover,
.pager .disabled>a,
.pager .disabled>a:focus,
.pager .disabled>a:hover,
.pager .first a,
.pager .last a,
.pager .pager-next a,
.pager .pager-prev a,
.rbx-select-group.active,
.rbx-select-group:focus,
.rbx-select-group:hover,
.rbx-tabs-horizontal .rbx-tab .rbx-tab-heading.active,
.rbx-tabs-horizontal .rbx-tab .rbx-tab-heading:active,
.rbx-tabs-horizontal .rbx-tab .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.small-action,
.topic-avatar,
.topic-body,
.topic-map .participants .user,
.topic-map,
.topic-status-info,
:focus,
aside.onebox
{
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

.message-summary-content .text-preview {
    white-space: pre-wrap!important;
}

.items-list .item-card .item-card-equipped {
    border-radius: 22px!important;
}

.item-card-equipped {
    border: 2px solid #b70202!important;
}

.item-card-equipped .item-card-equipped-label {
    border-top-right-radius: 22px!important;
    border-top: 21px solid #b70202!important;
    border-left: 21px solid transparent!important;
}

.menu-vertical .menu-option .menu-secondary-option:hover,
.menu-vertical .menu-option.active,
.menu-vertical .menu-option:focus,
.menu-vertical .menu-option:hover
{
    box-shadow: 4px 0 0 0 #870202 inset!important;
}

.table .thead-dark th {
    border-color: #f2f2f2!important;
}

table {
    border-collapse: separate!important;
}

.has-description.no-logo {
	border-spacing: 12px!important;
}


/*___ Fonts ________________________________________________________________________*/

.gotham-font .h1,
.gotham-font .h2,
.gotham-font .h3,
.gotham-font .h4,
.gotham-font .h5,
.gotham-font .h6,
.gotham-font .text,
.gotham-font body,
.gotham-font button,
.gotham-font h1,
.gotham-font h2,
.gotham-font h3,
.gotham-font h4,
.gotham-font h5,
.gotham-font h6,
.gotham-font html,
.gotham-font input,
.gotham-font pre,
.gotham-font select,
.gotham-font textarea,
.gotham-font,
.sub-pages h5,
.sub-pages h6,
.message-summary-content .text-preview,
.messages .messageDivider .message-summary-date,
.messages .messageDivider.unread .message-summary-body .subject,
.rbx-login-partial-legacy h3,
b,
body,
h1,
h2, 
h3,
h4,
strong
{
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

.gotham-font .font-title,
.gotham-font .h1,
.gotham-font .h2,
.gotham-font h1,
.gotham-font h2
{
    font-size: 27px!important;
    font-weight: 400!important;
}

.breadcrumb-container li.breadcrumb-filter,
.sub-pages h5,
.sub-pages h6
{
	font-size: 21px!important;
}

.bd-sidebar .bd-toc-link,
.markdown-field-data li,
.markdown-field-data p
{
	font-size: 19px!important;
}

.table td,
html,
kbd
{
	font-size: 17px!important;
}

selector {
	font-size: 15px!important;
	line-height: 27px!important;
}

.gotham-font .item-card-label {
	font-size: 14px!important;
	line-height: 19px!important;
}

/*___ Remove Items _________________________________________________________________*/

selector {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.avatar-back {
    margin-left: 0!important;
    margin-top: 0!important;
}

.markdown-field-data img {
    margin-left: 6rem!important;
}

.navbar-padding {
    padding-left: 0!important;
}

.markdown-field-data li,
.bd-content p {
    margin-left: 2rem!important;
}

.timeline-container {
    margin-left: 88%!important;
}

/*___ Margins ______________________________________________________________________*/

.bd-content,
.bd-sidebar-wrap,
.bd-toc-wrap,
.col-xl-3,
.game-cards .game-card,
.home .section,
.item-cards-stackable .item-card,
.items-list .item-card,
.loading-container.ember-view,
.no-gutter-ads .rbx-left-col,
.right-panel,
.section-content:not(.tab-horizontal-submenu),
.topic-post
{
	margin: 12px!important;
}

#BodyWrapper {
    margin: 12px 12px 0px 14%!important;
}

.content {
    margin: 12px 0 0px 13%!important;
}

selector {
	margin: 3rem!important;
}

/*___ Paddings _____________________________________________________________________*/

#BodyWrapper,
#DevelopSplashScreen .center,
.bd-toc-wrap,
.content,
.d-editor-textarea-wrapper,
.game-cards .game-card,
.has-description.no-logo,
.home .section,
.item-cards-stackable .item-card,
.items-list .item-card,
.loading-container.ember-view,
.menu-vertical,
.no-gutter-ads .rbx-left-col,
.right-panel,
.search-options,
.submenu-row,
.timeline-container,
.topic-post,
[aria-expanded="true"]
{
	padding: 12px!important;
}

p>code,
li>code,
pre>code,
kbd {
    padding: 2px 6px!important;
}

.rbx-left-col ul {
    padding: 5px 0 0 0!important;
}

.table td {
    padding: 11px 16px 14px 16px!important;
}

/*___ Heights ______________________________________________________________________*/

.gotham-font .game-cards .game-card .game-name-title {
	max-height: none!important;
}

.places-list {
	height: auto!important;
}

.gotham-font .game-cards .game-card {
    height: 19rem!important;
}

#DevelopSplashScreen .footer-container .footer-content {
    height: 189px!important;
}

.item-cards-stackable .item-card-price {
    height: 29px!important;
}

/*___ Widths _______________________________________________________________________*/

.container,
.content,
.header-wrap,
.main-container,
.wrap
{
	max-width: none!important;
}

.light-theme .rbx-left-col,
.topic-body {
	width: auto!important;
}

.rbx-left-col .rbx-scrollbar,
.left-wrapper .section-content,
.mCSB_container {
    width: 113%!important;
}

.wrap,
.main-container {
	width: 98%!important;
}

.content {
    width: 88%!important;
}
.container {
    width: 82%!important;
}

.right-panel {
    width: 74%!important;
}


.list-item.game-card.game-tile.ng-scope,
.right-wrapper,
.right-wrapper-placeholder
{
    width: 65%!important;
    left: 39rem!important;
}

.left-wrapper-placeholder {
    width: 24%!important;
}

.col-sm-6,
.gotham-font .game-cards .game-card
{
    width: 40%!important;
}

.game-cards .game-card,
.list-item.game-card.game-tile.ng-scope
{
    width: 18%!important;
}

.catalog-container .splash .catalog-results .item-cards-stackable .item-card:nth-child(-n+5),
.items-list .item-card {
    width: 12%!important;
}

.item-cards-stackable .item-card {
    width: 10%!important;
}


.list-item.game-card.group-container {
    width: 14.5%!important;
}

.col-xl-3 {
    flex: 0 0 23%!important;
}
}

@-moz-document domain("roblox.com") {
/*__________________________________________________________________________________*/
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.SquareTabGray a:hover,
.SquareTabGray.selected a,
.SquareTabGray.selected:hover a,
.SquareTabGray.ui-tabs-selected a,
.SquareTabGray.ui-tabs-selected:hover a,
.menu-vertical .menu-option .menu-secondary-option:hover span,
.menu-vertical .menu-option.active,
.menu-vertical .menu-option:hover,
.tab-horizontal-submenu li:hover,
.topic-map .map .number,
a[href]:hover span,
a[href]:hover,
fieldset .chosen-container .chosen-results li.highlighted,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.light-theme .game-card-info .info-label,
.light-theme .modal-dialog .modal-content .modal-body,
.light-theme .modal-dialog .modal-content .modal-top-body,
.SquareTabGray a,
.badge-wrapper.bullet span.badge-category,
.btn-control-md,
.category-list td,
.category-list th,
.discourse-no-touch .btn-flat:hover .d-icon,
.dropdown-menu li a,
.game-card-experimental,
.game-card-name-secondary,
.learn-roblox-pages .intro .lead,
.learn-roblox-pages .learning-materials-content .tab-content .desc,
.list-cell,
.login-container .fb-divider-container .divider-text-container .divider-text,
.markdown-field-data .alert,
.markdown-field-data,
.profile-avatar-left .enable-three-dee,
.table-heading,
.text,
.text-nav,
.text-warning,
.topic-list .topic-excerpt,
body,
kbd,
li,
li>code,
nav.post-controls .like-button .like-count,
nav.post-controls .like-button .like-count:hover,
nav.post-controls button.d-hover,
p,
p>code,
pre,
pre>code
{
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

.chat-windows-header .chat-header-label span,
.rbx-header.light-theme .text-header,
.breadcrumb-container li .breadcrumb-filter-name,
.c-version .search-options .panel-heading,
.category-list th,
.learn-roblox-pages .learning-materials-content .tab-content .learning-type,
.rbx-login-partial-legacy h3,
.sub-pages h5,
.sub-pages h6,
.table .thead-dark th,
.table-header th,
.topic-list th,
h1,
h2
{
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.messages .messageDivider.unread .message-summary-body .subject,
.text-overflow.text-label,
b,
h3,
h4,
strong
{
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

.form-label,
.home .section .sm-title,
.item-card-label,
.text-label,
h5,
h6,
label
{
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

.learn-roblox-pages .time,
.learn-roblox-pages .timestamp,
.messages .messageDivider .message-summary-date,
.text-lead,
.timeline-container .topic-timeline .now-date,
.timeline-container .topic-timeline .start-date,
.timeline-container .topic-timeline .timeline-ago,
time
{
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/


selector {
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

#MyTransactions_tab .empty,
#TradeItems_tab .empty,
.login-container .no-account-text,
.section-content-off,
.strike-through
{
	color: #ff0707!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.custom-select-box.select2-container .select2-choice,
.input-group-btn .input-dropdown-btn,
.list-controls .combo-box .combo-box-header,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder { /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #f48f8f;
  opacity: 1; /* Firefox */
}

/*___ Code _________________________________________________________________________*/

.token.punctuation {
    color: #fff!important;
}

.token.selector, .token.attr-name, .token.string, .token.char, .token.builtin, .token.inserted {
    color: #af0!important;
}

.token.atrule, .token.attr-value, .token.keyword {
    color: #00b3ff!important;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted {
    color: #ff6cbe!important;
}
}

@-moz-document domain("roblox.com") {
/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

.rbx-select-group .rbx-select:hover, .rbx-select-group .rbx-select:focus, .rbx-select-group .rbx-select.active {
    box-shadow: 4px 0 0 0 #ff0000 inset!important;
}

.close-quotation-mask,
.open-quotation-mask {
	background-image: none!important;
}

/*___ Background Gray ______________________________________________________________*/

#header,
#wrap.pinned .right-wrapper:before,
.container-footer,
.d-header,
.footer,
.header-wrap,
.learn-roblox-pages .learning-materials-content .nav-tabs-wrap,
.login-container .fb-divider-container .divider-text-container .divider-text,
.navbar-landing,
.rbx-header
{
	background-color: #060505!important;
}

.header {
	background-color: #100d0d!important;
}


/*___ Background 0000 ______________________________________________________________*/

.bd-sidebar,
.topic-map,
a.mention,
a.mention-group
{
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

.light-theme .rbx-header .rbx-navbar-search,
aside.quote .title,
#Body,
#DevelopSplashScreen .body-container .blurbs-container,
#DevelopSplashScreen .testimonials-container,
#DevelopSplashScreen .wiki-container,
.SquareTabGray a:hover,
.SquareTabGray,
.StandardPanelContainer .StandardPanelWhite,
.bg-primary,
.breadcrumb-container li.breadcrumb-filter,
.btn-control-md,
.chat-main .chat-header,
.chat-main-empty .chat-disconnect,
.content,
.d-header-icons .icon:focus,
.d-header-icons .icon:hover,
.drop-down-mode .d-header-icons .active .icon,
.dropdown-menu li.active,
.dropdown-menu li:focus,
.dropdown-menu li:hover,
.form-control,
.game-card-container,
.input-group-btn .input-addon-btn,
.item-card-container,
.learn-roblox-pages .learning-materials-content .nav-tabs,
.main-container,
.menu-panel li a:focus,
.menu-panel li a:hover,
.menu-panel li.heading a:focus,
.menu-panel li.heading a:hover,
.modal-dialog .modal-content,
.nav-pills>li>a:hover,
.notification-stream-banner.banner-new,
.notification-stream-body .container-empty,
.notification-stream-body,
.notification-stream-container .notification-content-view,
.notification-stream-header,
.pager .disabled>a,
.pager .disabled>a:focus,
.pager .disabled>a:hover,
.pager .first a,
.pager .last a,
.pager .pager-next a,
.pager .pager-prev a,
.popover,
.profile-avatar-right .profile-avatar-mask,
.rbx-select-group.active,
.rbx-select-group:focus,
.rbx-select-group:hover,
.rbx-tab .rbx-tab-heading,
.rbx-tabs-horizontal .nav-tabs,
.tab-horizontal-submenu li:hover,
.table .thead-dark th,
.table-header th,
.user-menu .notifications li,
fieldset .chosen-container .chosen-results li.highlighted,
nav.post-controls .like-button:hover,
nav.post-controls button:hover
{
	background-color: rgba(0, 0, 0, 0)!important;
}

/*___ Highlight Background _________________________________________________________*/

selector:hover {
	border-left-color: #434343!important;
	background: linear-gradient(to right, #383838, #0000)!important;
}

/*__ Blue __________________________________________________________________________*/

.btn,
.btn-control-md,
.btn-control-sm,
.btn-secondary-md,
fieldset .chosen-container-active.chosen-with-drop .chosen-single
{
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn:hover,
.btn-control-md:hover,
.btn-control-sm:hover,
.btn-secondary-md:hover,
fieldset .chosen-container-active.chosen-with-drop .chosen-single:hover
{
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

.btn:active,
.btn-control-md:active,
.btn-control-sm:active,
.btn-secondary-md:active,
fieldset .chosen-container-active.chosen-with-drop .chosen-single:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Red __________________________________________________________________________*/

#decline-btn {
	background-image: linear-gradient(to bottom right, #f00, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

#decline-btn:hover {
	background-image: linear-gradient(to bottom right, #f11, #000)!important;
	color: #ff0!important;
}

#decline-btn:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Orange ________________________________________________________________________*/

.btn-secondary-xs,
.btn-w-border {
	background-image: linear-gradient(to bottom right, #da3a04, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn-secondary-xs:hover,
.btn-w-border:hover {
	background-image: linear-gradient(to bottom right, #eb4b15, #000)!important;
	color: #ff0!important;
}

.btn-secondary-xs:active,
.btn-w-border:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Green ________________________________________________________________________*/

.btn-buy-md,
.btn-primary-lg,
.btn-primary-md,
.btn-primary-sm
{
	background-image: linear-gradient(to bottom right, #02b757, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn-buy-md:hover,
.btn-primary-lg:hover,
.btn-primary-md:hover,
.btn-primary-sm:hover
{
	background-image: linear-gradient(to bottom right, #13c868)!important;
	color: #ff0!important;
}

.btn-buy-md:active,
.btn-primary-lg:active,
.btn-primary-md:active,
.btn-primary-sm:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Facebook__________________________________________________________________*/

.login-container .fb-button {
	background-image: linear-gradient(to bottom right, #3b5998, #000)!important;
