/* BOX PART */

.list-item-checkbox[data-checked="false"]:before {
	box-shadow: inset 0 0 0 1px #666666, 0 0 0 0px #666666, 0 0 0 0px rgba(100, 100, 100, 0);
	background: rgba(0, 0, 0, 0);
	z-index: 1000 !important;
	animation: checkbox-uncheck .9s ease-in-out;
}

.list-item-checkbox[data-checked="true"]:before {
	box-shadow: inset 0 0 0 1px #666666, 0 0 0 0px #666666, 0 0 0 5px rgba(100, 100, 100, 0);
	background: #666666;
	z-index: 1000 !important;
	animation: checkbox-check .6s ease-in-out;
}

@-webkit-keyframes checkbox-check {
	0% {
		box-shadow: inset 0 0 0 1px #666666, 0 0 0 0px #666666, 0 0 0 0px rgba(100, 100, 100, 0);
		background: rgba(0, 0, 0, 0);
	}

	20% {
		box-shadow: inset 0 0 0 1px #666666, 0 0 0 2px #666666, 0 0 0 4px rgba(100, 100, 100, 0);
		background: #666666;
		border-radius: 4px;
	}

	35% {
		box-shadow: inset 0 0 0 1px #666666, 0 0 0 1px #666666, 0 0 0 4px rgba(100, 100, 100, .3);
		background: #666666
	}

	45% {
		box-shadow: inset 0 0 0 1px #666666, 0 0 0 0px #666666, 0 0 0 4px rgba(100, 100, 100, .25);
		background: #666666
	}

	55% {
		box-shadow: inset 0 0 0 1px #666666, 0 0 0 0px #666666, 0 0 0 4px rgba(100, 100, 100, .2);
		background: #666666
	}

	75% {
		box-shadow: inset 0 0 0 1px #666666, 0 0 0 0px #666666, 0 0 0 4px rgba(100, 100, 100, .1);
		background: #666666
	}

	100% {
		box-shadow: inset 0 0 0 1px #666666, 0 0 0 0px #666666, 0 0 0 5px rgba(100, 100, 100, 0);
		background: #666666;
	}
}

@-webkit-keyframes checkbox-uncheck {
	0% {
		box-shadow: inset 0 0 0 1px #666666, 0 0 0 0px #666666;
		background: #666666;
	}

	10% {
		box-shadow: inset 0 0 0 1px #888888, 0 0 0 2px #888888;
		background: #888888;
	}

	50% {
		box-shadow: inset 0 0 0 1px #666666, 0 0 0 0px rgba(100, 100, 100, 0);
		background: rgba(100, 100, 100, 0);
	}

	100% {
		box-shadow: inset 0 0 0 1px #666666;
		background: rgba(100, 100, 100, 0);
		;
	}
}

/*CHECK PART*/
.list-item-checkbox[data-checked="false"]:after {
	opacity: 0;
	transition: .1s !important;
	transition-delay: .05s !important;
	z-index: 1000 !important;
}

.list-item-checkbox[data-checked="true"]:after {
	padding: 0px;
