.docs-icon-img-container .docs-icon-img .goog-inline-block .docs-icon-arrow-dropdown {
    content: none;
}

/* scrollbars */
.grid-shim-end-ltr, .grid-shim-bottom {
    background: var(--color-scrollbars);
    border: 1px solid var(--color-scrollbars);
}

