    src: local('Roboto Bold'), local('Roboto-Bold'), url(https://fonts.gstatic.com/s/roboto/v20/KFOlCnqEu92Fr1MmWUlfChc4EsA.woff2) format('woff2');
    unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 700;
    font-display: swap;
    src: local('Roboto Bold'), local('Roboto-Bold'), url(https://fonts.gstatic.com/s/roboto/v20/KFOlCnqEu92Fr1MmWUlfBBc4.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}






:root {
    --aft-base-ff: 'Roboto', Arial, Helvetica, sans-serif;
    --aft-blockquote__font-size: 14px;
    --aft-base__border-radius: 4px;
    --aft-menu__active__color: #da5b5b;
    --aft-menu__active__background-color: #eee;
    --aft-a__colorB: #959fc1;
    --aft-a__colorVis: #9c6e72;
    --aft-comment__intended__border-color: #ccc;
    --aft-comment__intended--highlighted__border-color: #e88;
    --aft-comment__intended--highlighted__bg-color: rgba(213, 197, 197, 0.53);
    --aft-comment--highlighted__background-color: rgba(111, 147, 143, 0.31);
}

/* header */

.aft-header {
    height: 60px;
    background: url('https://aftershock.news/sites/default/files/styles/avatar-mini/public/pictures/picture-5-1487328749.png?itok=HcIaeN3H') 20px center no-repeat;
    background-size: 40px;
}

.aft-header_left,
.aft-header_right {
    display: none;
}

.desktop .aft-nav {
    right: 40px;
    width: auto;
}

/*layout* /
.aft-content-layout,
.aft-layout-cell
{
    display: block;
}/**/
.top_rating_selectors {
    margin: 0;
}
.block:not(.block-commentsblock) {
    position: relative;
    z-index: 1;
}

@media (max-width: 1024px) {
    .aft-content-layout-row {
        /*display: grid;*/
        grid-template-columns: 220px minmax(auto, 1180px);
        grid-gap: 20px;
    }
}

.aft-content-layout .aft-sidebar1 {
    margin: unset;
    overflow: visible;
    padding-left: 10px;
}

/*.aft-content-layout .aft-sidebar {
    width: unset;
    padding-right: 10px;
}*/


/* typography */

.aft-postcontent p {
    line-height: 1.23;
    text-rendering: optimizelegibility;
    white-space: normal;
}

/*.aft-postcontent p:only-child {
    margin: 8px 0;
}

.aft-postcontent p:first-child {
    margin-top: 8px;
}

.aft-article h1 {
    font-size: 24px !important;
}*/

.aft-article h2.aft-postheader a {
    font-size: 20px !important;
}

@media (min-width: 1200px) {
    /*.aft-postcontent p  {
        font-size: 16px;
    }*/

    .aft-article h2 {
        font-size: 22px !important;
    }
}


/* buttons / tabs */

ul.arttabs_primary,
ul.arttabs_secondary,
.links,
img.service-link {
    margin: 10px 0 2px;
}

ul.arttabs_primary,
ul.arttabs_secondary {
    display: flex;
    flex-wrap: wrap;
}

ul.arttabs_primary li,
ul.arttabs_secondary li {
    margin-bottom: 8px;
}

.aft-button,
a.aft-button,
a.aft-button:link,
a:link.aft-button:link,
body a.aft-button:link,
a.aft-button:visited,
body a.aft-button:visited,
input.aft-button,
button.aft-button,
.aft-button.hover,
.aft-button:hover {
    display: inline-block;
    border-radius: var(--aft-base__border-radius);
    padding: 2px 8px;
}


/* pager */

.aft-pager>*,
.aft-pager .active,
.aft-pager .more,
.aft-pager a:hover {
    border: 0;
    min-width: 14px;
    text-align: center;
    padding: 5px;
    margin-right: 6px;
    background: #ccc;
}

.aft-pager .active,
.aft-pager a:hover {
    background: #9e9e9e;
}


/* post preview */

.field-name-field-teaser-image {
    text-align: center;
}

.node .teaser-caption {
    text-align: left;
    padding: 2px;
}

.aft-post p img,
.field-item.odd img,
.field-item.even img,
.field-name-field-teaser-image img {
    background-color: var(--aft-input__background-color);
}
.field-item.even blockquote:first-child {
    margin-top: 0;
}
.node-teaser .field-item img {
    max-height: 100px;
    object-fit: cover;
}

.publication-tags-item a:link,
.publication-tags-item a:visited {
    font-weight: normal;
}


/* post */

.aft-postcontent {
    padding-right: 14px;
}

/*blockquote {
    border: none;
    border-radius: var(--aft-base__border-radius);
    padding-right: 30px;
    max-width: 80%;
    margin-bottom: 4px;
    margin-top: 8px;
}*/

.aft-postcontent blockquote,
.aft-postcontent blockquote p,
.aft-postcontent blockquote h1,
.aft-postcontent blockquote h2,
.aft-postcontent blockquote h3,
.aft-postcontent blockquote h4,
.aft-postcontent blockquote h5,
.aft-postcontent blockquote h6,
.aft-postcontent blockquote pre,
.aft-postcontent blockquote li {
    font-size: var(--aft-blockquote__font-size);
}
body.cke_editable blockquote p,
body.cke_editable blockquote ul,
body.cke_editable blockquote ol,
body.cke_editable blockquote pre{
    margin: 0;
    padding: 0;
    line-height: normal;
    font-family: 'Roboto', Arial, Helvetica, sans-serif;
    font-style: normal;
}
.cke_editable blockquote,
.aft-postcontent blockquote {
    overflow: inherit;
    margin: -5px 0 -5px 13px;
    padding: 0 0 0 2px;
    border: 1px dotted #ccdbe3;
    border-left: 2px solid #b6c5ce;
    border-radius: 0;
    font-style: normal;
    background: #f2f5f7;
}
body.cke_editable ol,
body.cke_editable ul {
    margin: 0;
    padding: 0;
}
body.cke_editable ul {
    list-style-type: none;
}
body.cke_editable ol {
    list-style-position: inside;
}

.opengraph-filter {
    border: 0;
}

.aft-article .video-filter {
    text-align: center;
    border-radius: var(--aft-base__border-radius);
}


/* ava */

.node .user-picture a > img,
.aft-comment-avatar a > img {
    padding: 0;
    border-width: 1px;
    border-style: solid;
    border-color: #999;
    background-color: var(--aft-input__background-color);
}

.aft-comment-avatar img:hover,
.node .user-picture img:hover {
    border-color: var(--aft-base__background-color);
}

ul.menu li a {
    display: block;
    padding: 3px 8px;
    border-radius: var(--aft-base__border-radius);
}

ul.menu li a.active {
    color: var(--aft-menu__active__color);
    background-color: var(--aft-menu__active__background-color);
}


/* about category */

.field-name-field-printing-description {
    border-radius: var(--aft-base__border-radius);
    border: none;
    padding: 10px;
    margin: 4px 5px;
    background: #d6eed6;
}

.aft-blockcontent table,
.aft-blockcontent li,
.aft-blockcontent a,
.aft-blockcontent a:link,
.aft-blockcontent a:hover {
    font-size: var(--aft-blockquote__font-size);
    color: #4789a8; /*394b8a*/
}
.aft-blockcontent a:visited {
    font-size: var(--aft-blockquote__font-size);
}

.region-sidebar-right .aft-blockcontent a:visited {
    color: var(--aft-a__colorVis);
}

.region-sidebar-right .aft-postfootericons.aft-metadata-icons,
.region-sidebar-right .aft-blockcontent .aft-postfootericons.aft-metadata-icons a {
    font-size: 11px;
}

.region-sidebar-right .aft-postfootericons {
    margin-left: 42px;
}
.aft-postheadericons a.username:visited,
.aft-postfootericons .aft-postcategoryicon a:visited{color: var(--aft-a__colorVis)}

/* comments */

/*.indented,
.indented0,
.indented1,
.indented2,
.indented3 {
    border-color: var(--aft-comment__intended__border-color);
}*/
.content #comments.aft-commentsform {
    position: relative;
    margin: .6em -322px 0 -16.3em;
    background: #eee;
    padding: 0 322px 1.75em 16.3em;
}
.aft-comment-header {
    border-radius: 2px;
}

.aft-comment:hover +[class^=indented],
.aft-comment:hover +a#new +[class^=indented] {
    border-color: var(--aft-comment__intended--highlighted__border-color);
}

.aft-comment:hover +[class^=indented] > div.aft-comment,
.aft-comment:hover +a#new +[class^=indented] > div.aft-comment {
    background-color: var(--aft-comment__intended--highlighted__bg-color);
}

.aft-comment:hover +[class^=indented]>.aft-comment .aft-comment-header,
.aft-comment:hover +a#new +[class^=indented]>.aft-comment .aft-comment-header {
    background-color: #e9e9e9;
}


/* Переход по якорю к постоянной ссылке  */

a:target+.aft-comment {
    background-color: var(--aft-comment--highlighted__background-color);
}


/* status message */

div.status,
div.error {
    border: none;
    border-radius: 4px;
}


/* cke-editor-white */


.form-textarea-wrapper .cke_chrome.cke_reset,
.form-textarea-wrapper .cke_inner.cke_reset {
    box-shadow: none;
    background: none;
}
.cke_toolbar {float: none}
.cke_top .cke_toolbar .cke_toolgroup {margin: 0 2px 2px 0}
.form-textarea-wrapper .cke_top {
    border: none;
    box-shadow: none;
    background: none;
}
.form-textarea-wrapper .cke_toolbar_separator {
    margin: 3px 2px 2px;
    background-color: rgba(128, 128, 128, 0.2);
}
.form-textarea-wrapper a.cke_combo_button {
    border: none;
    box-shadow: 0 1px 0 rgba(255, 255, 255, 0.2), 0 0 2px rgba(255, 255, 255, 0.15) inset, 0 1px 0 rgba(255, 255, 255, 0.15) inset;
    background: rgba(255, 255, 255, .1);
    background-image: none;
    background-image: linear-gradient(to bottom, rgba(251, 251, 251, 0.51), rgba(138, 138, 138, 0.16));
}
.form-textarea-wrapper .cke_toolgroup {
    border: none;
    background: rgba(255, 255, 255, 0.1);
    background-image: none;
    background-image: linear-gradient(to bottom, rgba(251, 251, 251, 0.51), rgba(138, 138, 138, 0.21));
}
.cke_combo::after,
.cke_toolgroup a.cke_button:last-child::after, .cke_toolgroup a.cke_button.cke_button_disabled:hover:last-child::after {
    border-right: 1px solid transparent;
}

.form-textarea-wrapper .cke_reset {
    border: none;
    box-shadow: rgba(0, 0, 0, 0.15) 0 0 2px;
}
.form-textarea-wrapper .cke_reset.cke_contents {
    border: none;
    box-shadow: rgba(0, 0, 0, 0.15) 0 0 2px;
}
.form-textarea-wrapper .cke_bottom {
    height: 15px;
    border: none;
    box-shadow: 0 1px 2px rgba(223, 223, 223, 0.52) inset;
    background: transparent;
    background-image: linear-gradient(to bottom, rgb(249, 249, 249), rgb(234, 233, 233));
}
textarea.text-full,
textarea.cke_source {
    color: #3d3d3d !important;
    background: #fff;
}
#edit-submit, #edit-preview {
    width: 45%;
    padding: 12px 0;
}


/* cke-editor-combo-dropdown */

.cke_combopanel {
    width: 120px;
}
.cke_panel {
    width: 120px;
    height: auto;
    background-color: transparent;
    border: none;
}
.cke_panel_grouptitle {
    font-size: 11px;
    padding: 0;
    text-shadow: none;
    border-bottom: 1px solid #d7d7d7;
    border-radius: 2px 2px 0 0;
    box-shadow: 0 1px 0 #fbfbfb inset;
    background: #dedfde;
}
body.cke_ltr {
    font-family: Roboto, Arial, Helvetica, sans-serif;
}
.cke_panel_list {
    margin: 1px 3px;
}
.cke_panel_listItem {
    padding-bottom: 0;
    max-height: 13px;
    line-height: 1.3em;
}
.cke_panel_listItem a,
.cke_panel_listItem.cke_selected a {
    padding: 0;
    border: none;
}
.cke_panel_listItem a[title="Heading 1"] {
    margin-top: -2px;
    color: #b5b3b3 !important;
}
.cke_panel_listItem a[title="Heading 2"] {
    color: #9f9d9d !important;
}
.cke_panel_listItem p,
.cke_panel_listItem h1,
.cke_panel_listItem h2,
.cke_panel_listItem h3,
.cke_panel_listItem h4,
.cke_panel_listItem h5,
.cke_panel_listItem h6,
.cke_panel_listItem pre {
    max-height: 21px;
    height: 1.2em;
    min-height: 13px;
    line-height: inherit;
}
.cke_combo_on a.cke_combo_button,
.cke_combo_off a.cke_combo_button:active,
.form-textarea-wrapper .cke_combo_off a.cke_combo_button:hover, .form-textarea-wrapper .cke_combo_off a.cke_combo_button:focus {
    border: 1px solid transparent;
}
a.cke_button_off:hover, a.cke_button_off:focus, a.cke_button_off:active {
    background: #d5d3d3;
    border-radius: 3px;
}
a.cke_button_on {
    margin: -1px;
    background: #cac9c9;
    border: 1px #bfbfbf solid;
    padding: 3px 5px;
    border-radius: 3px;
}
a.cke_button_on:hover {
    background: #d2d2d2;
}




/* ========================= DARK-(updated for ash-clean) THEME ============================ */
div#aft-main,
div.aft-comment-content,
div.aft-comment-content blockquote,
div.aft-postcontent,
div.aft-postcontent h1,
div.aft-postcontent h2,
div.aft-postcontent h3,
div.aft-postcontent h4,
div.aft-postcontent h5,
div.aft-postcontent h6,
div.aft-postcontent p,
div.aft-postcontent blockquote,
div.aft-postcontent li,
div.aft-postcontent table,
div.aft-postcontent a,
div.aft-postcontent a:link,
div.aft-postcontent a:visited,
div.aft-postcontent a.visited,
div.aft-postcontent a:hover,
div.aft-postcontent a.hovered,
div.aft-article h1,
div.aft-postheader,
div.aft-postheader h1 .aft-postheader a,
div.aft-postheader a:link,
div.aft-postheader a:visited,
div.aft-postheader a.visited,
div.aft-postheader a:hover,
div.aft-postheader a.hovered,
div.aft-blockcontent a:visited,
div.aft-blockcontent a.visited {
    font-family: var(--aft-base-ff);
}
.aft-postheadericons a.username,
.aft-postauthoricon a.username{
    font-family: var(--aft-base-ff);
    font-weight: bold;
    color: var(--aft-base__h2__color);
}
.aft-postheader a, .aft-postheader a:link {
    color: rgba(75, 85, 91, 0.85);        /*TODO*/
}
.aft-comment-header a.username:hover,
.aft-comment-header a.username:visited,
.aft-comment-header a.username:active {
    color: var(--aft-a__colorVis) /*!important*/;
}
.aft-postheader .aft-postheadericon a.visited,
.aft-postheader .aft-postheadericon a:visited {
    color: rgba(110, 88, 95, 0.85);        /*TODO*/
}@media (prefers-color-scheme: no-preference) {
/*@media (prefers-color-scheme: dark) { /* ----- or no-preference: uncomment for depend theme of OS theme ----- */
:root {
    --aft-base__color: #ddd;
