/* ========================= DARK-(original) THEME ============================ */
#aft-main {
    background: var(--aft-base__background-color);
}

.aft-page h1,
.aft-article h1,
.aft-postcontent h2,
.aft-postcontent h3 {
    color: var(--aft-base__color);
}

.aft-button,
a.aft-button,
a.aft-button:link,
a:link.aft-button:link,
body a.aft-button:link,
a.aft-button:visited,
body a.aft-button:visited,
input.aft-button,
button.aft-button,
.aft-button.hover,
.aft-button:hover {
    background: var(--aft-input__background-color) !important;
    color: var(--aft-button__color);
}

.aft-button.hover,
.aft-button:hover,
.aft-button.active,
.aft-button:active,
.aft-button.active:hover {
    color: var(--aft-a--hover__color) !important;
    background-color: #40312d !important;
}


/* input */

input[type="text"],
input[type="password"],
input[type="email"],
input[type="url"] {
    color: var(--aft-base__color) !important;
    background: var(--aft-input__background-color);
}


/* pager */

.aft-pager>*,
.aft-pager .more {
    background: var(--aft-input__background-color);
}

.aft-pager a:link,
.aft-pager a:visited {
    color: var(--aft-button__color);
}

.aft-pager .active,
.aft-pager a:hover {
    background: var(--aft-input__background-color);
    color: var(--aft-a--hover__color);
}

.aft-postheader,
.aft-postheader h1 {
    color: var(--aft-base__h1__color);
    margin-bottom: 8px;
}

.aft-postheader a,
.aft-postheader a:link {
    color: var(--aft-base__h1__color);
}

.aft-postheader a:visited,
.aft-postheader a.visited {
    color: var(--aft-base__color);
}

.aft-postheader a:hover,
.aft-postheader a.hovered {
    color: var(--aft-a--hover__color) !important;
}


/* post */

.field-type-text-with-summary a:visited,
.field-type-text-long a:visited {
    color: var(--aft-a__color) !important;
}

.field-type-text-with-summary a:link,
.field-type-text-long a:link {
    color: var(--aft-a__color) !important;
}

blockquote {
    background-color: var(--aft-input__background-color);
    color: var(--aft-button__color);
}

.field-label {
    color: var(--aft-base__color);
}

.opengraph-filter {
    padding: 0 1px 1px 2px;
    border: 1px solid #484848;
    background-color: var(--aft-input__background-color);        /*TODO*/
    color: var(--aft-button__color);
}


/* menus */

.aft-postcontent a:visited,
.aft-postcontent a.visited,
blockquote a:visited,
.aft-postcontent blockquote a:visited,
.aft-blockcontent blockquote a:visited,
.aft-footer blockquote a:visited {
    color: var(--aft-a__color);
    text-decoration-color: var(--aft-a__color);
}
div.aft-footer-inner{padding:0 8px}

.aft-postcontent a,
.aft-postcontent a:link {
    color: var(--aft-a__color);
}

.aft-postcontent a:hover {
    color: var(--aft-a--hover__color) !important;
}


/* post preview top */

.aft-postcounter {
    color: var(--aft-button__color);
}

.aft-postheadericons a:hover,
.aft-postheadericons a.hover,
.aft-postheadericons a:visited,
.aft-postheadericons a.visited {
    color: var(--aft-a--hover__color);
    font-style: normal;
}

.node-teaser {
    position: relative;
    border-color: var(--aft-input__background-color);
}


/* post preview bottom */

.publication-tags-item a:link,
.publication-tags-item a:visited {
    color: var(--aft-a__color) !important;
}

.publication-tags-item a:hover,
.publication-tags-item a:focus,
.publication-tags-category-item a:hover,
.publication-tags-category-item a:focus {
    color: var(--aft-a--hover__color) !important;
}

.publication-tags-category-item a:link,
.publication-tags-category-item a:visited {
    color: var(--aft-a__color);
}


/* comments */

.aft-comments h2 {
    color: var(--aft-base__h2__color);
}

.aft-comment-content {
    color: inherit;
}

.aft-comment-header,
.aft-comment-header a {
    color: var(--aft-comment-header__color) !important;
}

div.aft-comment-header a:hover,
div.aft-comment-header a:visited,
div.aft-comment-header a:active {
    color: var(--aft-a--hover__color) !important;
}

.aft-postcontent li,
.aft-post li,
.aft-textblock li {
    color: #ccc;        /*TODO*/
}

.comment_forbidden span a,
.comment_forbidden span a:visited {
    color: var(--aft-a__color) !important
}

.comment_forbidden span a:hover {
    color: var(--aft-a--hover__color) !important;
}

label.aft-checkbox,
label.aft-radiobutton,
.aft-commentsform input:not([type="submit"]),
.aft-commentsform {
    color: var(--aft-comment-header__color) !important;
}


/* ava */

.node .user-picture img,
.aft-comment-avatar img {
    border-color: transparent;
}

.aft-comment-avatar img:hover,
.node .user-picture img:hover {
    border-color: var(--aft-button__color);
}


/* menu */

.aft-blockheader .t,
.aft-blockheader .t a,
.aft-blockheader .t a:link,
.aft-blockheader .t a:visited,
.aft-blockheader .t a:hover {
    color: var(--aft-base__color);
}

.aft-blockcontent {
    color: inherit;
}

div.aft-blockcontent table,
div.aft-blockcontent li,
div.aft-blockcontent a,
div.aft-blockcontent a:link,
div.aft-blockcontent a:hover,
.aft-comment-header a.username
    {color: var(--aft-a__colorB)}

.aft-blockcontent a:hover,
.aft-blockcontent a.hover {
    color: var(--aft-a--hover__color) !important;
}


/* about category */

.field-name-field-printing-description {
    background: var(--aft-author-comment__background);
    color: var(--aft-recommend__color);
}


/* header*/

.aft-shapes {
    background: none;
}

.aft-header {
    background-color: var(--aft-input__background-color);
}

.aft-header:after {
    color: var(--aft-base__h1__color);
}


/* author, editor comment */

.editor-comments,
.editor-comments .ctools-collapsible-container,
.field-name-field-author-comment {
    background-color: var(--aft-author-comment__background);
    color: var(--aft-recommend__color);
    border-color: var(--aft-input__background-color);
}

.field-name-field-author-comment .field-label, .editor-title, .editor-title a {
    color:  var(--aft-recommend__color);
}

.recommended-comment {
    background: var(--aft-recommend__background);
}

/* sticky post */

.node-sticky {
    background: var(--aft-recommend__background);
}


/* spoiler */
div.spoiler div.spoiler-title {
    color: inherit;
    background: var(--aft-input__background-color);
    border: none;
    box-shadow: none;
}

div.spoiler div.spoiler-content {
    background: var(--aft-input__background-color);
}

.aft-comment:hover+[class^=indented]>.aft-comment .aft-comment-header,
.aft-comment:hover+a#new+[class^=indented]>.aft-comment .aft-comment-header
{
    background-color: var(--aft-comment--highlighted__background-color);
}


/* status message */

div.status {
    background: var(--aft-recommend__background);        /*TODO*/
    color: #be7;
}


/* post preview */

.node-unpublished,
.preview .node {
    background-color: transparent;
}


/* личный блог */

.aft-postauthoricon a:visited,
.aft-postauthoricon a.visited,
.aft-postfootericons a:visited,
.aft-postfootericons a.visited {
    font-family: var(--aft-base-ff);
    color: var(--aft-a__colorVis); /*color:#37607b*/
}

.ctools-collapsible-container {
    /* Скрытый комментарий бота */
    background: var(--aft-recommend__background);
}

.ctools-collapsible-container .ctools-collapsible-handle {
    color: var(--aft-comment-header__color);
}


/* google search */

.gsc-control-cse {
    border-color: transparent;
    background-color: var(--aft-recommend__background);
}

.gsc-control-cse form tbody {
    border: none;
}
} /* ----- end @media (prefers-color-scheme: dark) - uncooment if higher string was uncommented ----- */
}

