data.css:
@-moz-document domain("whitemad.pl") {
html,body{
    background: #000;
    color: #fff;
}
:root{
    scrollbar-color: #fff #000;
}
a,h1,h2,h3,h4{
    color: #fff !important;
}
p,span{
    color: #fff !important;
}
a:hover{
    text-decoration: underline;
}
*{
    box-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],textarea,select{
    background: #000 !important;
    color: #fff !important;
    border: 1px solid #fff !important;
}
input[type="number"]{
    color: #fff !important;
}
input[type="submit"]:hover{
    background: #fff !important;
    color: #000 !important;
}
/*HEADER*/
.cb-tm-light #cb-top-menu .cb-top-menu-wrap,.cb-header,.cb-menu-light #cb-nav-bar .cb-nav-bar-wrap,.cb-stuck.cb-menu-light #cb-nav-bar .cb-nav-bar-wrap,.cb-modal .cb-modal-inner{
    background-color: #000;
}
.cb-menu-light #cb-nav-bar .cb-nav-bar-wrap, .cb-stuck.cb-menu-light #cb-nav-bar .cb-nav-bar-wrap{
    border: 1px solid #fff;
}
#logo img,#cb-nav-bar .cb-main-nav #cb-nav-logo img{
    filter: invert();
}
/*SEARCH*/
.cb-s-modal-on #cb-overlay{
    opacity: .6;
}
.cb-modal .cb-modal-inner{
    color: #fff;
}
.cb-modal .cb-submit-form{
    border-color: #fff;
}
.cb-modal .cb-submit-form:hover{
    background: #fff;
    color: #000 !important;
}
/*SEARCH*/
/*HEADER*/
/*STRONA-GŁÓWNA*/
#cb-outer-container,.cb-body-light #cb-content, .cb-body-light .cb-hp-section{
    background: #000;
}
.cb-body-light .cb-module-header .cb-module-title::after{
    background: #fff;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.cb-body-light .cb-sidebar{
    background: #000;
    border: 1px solid #fff;
}
.cb-body-light .cb-article-footer .cb-title-header{
    border-color: #fff;
}
/*KOMENTARZE*/
.cb-body-light #commentform label{
    color: #fff;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*PODSTRONY*/
#cb-section-a .cb-contents img{
    filter: invert();
}
.cb-body-light .cb-pagination .current{
    background: #fff;
    color: #000 !important;
}
.cb-body-light .cb-pagination a{
    border: 1px solid #fff;
}
.cb-body-light .cb-pagination a:hover{
    background: #fff;
    color: #000 !important;
}
/*SKLEP*/
.big-search .search-wrap{
    opacity: .6 ;
}
.sticky-header .site-header,.fullwidth-slider .site-content{
    background: #000 !important;
}
.custom-logo,.quantity #up,.quantity #down{
    filter: invert();
}
.popup-filter,.woocommerce table.shop_table th,.woocommerce form .form-row label,.cart_item .product-name{
    color: #fff;
}
.popup-filter:hover{
    color: #fff;
    text-decoration: underline;
}
.popup-widgets .sidebar,.woocommerce a.remove,.cart-widget{
    background: #000;
    border: 1px solid #fff;
}
.quantity,.entry-content thead tr{
    border-color: #fff;
}
button,.woocommerce a.button{
    border: 1px solid #fff !important;
}
button:hover,.woocommerce a.button:hover,.woocommerce a.remove:hover{
    background: #fff !important;
    color: #000 !important;
}
button:hover span{
    color: #000 !important;
}
.single-product .related::before{
    background: #fff;
}
.woocommerce form .form-row .required{
    color: red;
}
.woocommerce-error{
    color: #fff;
    border-color: #fff;
}
/*SKLEP*/
/*POLITYKA-PRYWATNOŚCI*/
.entry-content li{
    color: #fff;
}
/*POLITYKA-PRYWATNOŚCI*/
/*LOGIN*/
label.checkbox::before{
    border-color: #fff !important;
}
/*LOGIN*/
/*PODSTRONY*/
/*FOOTER*/
.site-footer{
    background: #000;
}
.site-info{
    color: #fff;
}
.cb-copyright:after,.site-info:after{
    content: " CIEMNY MOTYW BY MACIEJOZAMAN";
    cursor: none;
}
/*FOOTER*/
}

@-moz-document domain("tjournal.ru"), domain("dtf.ru"), domain("vc.ru") {
:root {
    --gray-900: #24292e;
    --gray-800: #2f363d;
    --gray-700: #444d56;
    --gray-600: #586069;
    --gray-500: #6a737d;
    --gray-400: #959da5;
    --gray-300: #d1d5da;
    --gray-200: #e1e4e8;
    --gray-100: #f6f8fa;
    --gray-000: #fafbfc;

    --blue-900: #05264c;
    --blue-900-5: rgba(5, 38, 76, .5);
    --blue-800: #032f62;
    --blue-300: #79b8ff;

    --purple-900-5: rgba(41, 19, 78, .5);

    --red-600: #cb2431;

    --green-900-5: rgba(20, 70, 32, .5);

    --yellow-900: #735c0f;

    --pink-900: #6d224f;
    --pink-600: #d03592;
    
    --green-000:       #f0fff4;
    --green-100:       #dcffe4;
    --green-200:       #bef5cb;
    --green-300:       #85e89d;
    --green-400:       #34d058;
    --green-500:       #28a745;
    --green-600:       #22863a;
    --green-700:       #176f2c;
    --green-800:       #165c26;
    --green-900:       #144620;
    
    --red-000:         #ffeef0;
    --red-100:         #ffdce0;
    --red-200:         #fdaeb7;
    --red-300:         #f97583;
    --red-400:         #ea4a5a;
    --red-500:         #d73a49;
    --red-600:         #cb2431;
    --red-700:         #b31d28;
    --red-800:         #9e1c23;
    --red-900:         #86181d;
}

/*
Более темные цвета.
Чтобы использовать, надо раскоментировать
:root {
    --gray-900: #191919;
    --gray-800: #252525;
    --gray-700: #2d2d2d;
    --gray-600: #4a4a4a;
    --gray-500: #4c4c4c;
    --gray-400: #828282;
    --gray-300: #dfdfdf;
    --gray-200: #e6e6e6;
    --gray-100: #f6f8fa;
    --gray-000: #fafbfc;
}
*/
/* Header */
.main_menu,
.live_head,
.search_bar__field,
.search__field {
    background: var(--gray-900);
    color: var(--gray-100);
}

.main_menu__item, .main_menu__auth {
    color: var(--gray-100);
}

.search__input {
    background: var(--gray-700);
    color: var(--gray-900);
}

.search__input::placeholder {
  color: var(--gray-300);
  opacity: 1;
}

.search__input:-ms-input-placeholder {
  color: var(--gray-300);
}

.search__input::-ms-input-placeholder {
  color: var(--gray-300);
}

.search__input:active,
.search__input:focus {
    color: var(--gray-900);
}

.search__field .icon--search {
    color: var(--gray-400)!important;
}

.search-dropdown {
    color: black;
}

.possession_triangle:after {
    border-top-color: var(--gray-100);
}

/* Main stuff */
body,
.sidebar,
.live {
    background: var(--gray-800);
    color: var(--gray-100);
}

.layout--bg {
    background: var(--gray-700);
}

/* tabs */
.ui-tab--active,
.ui-tab--active .ui-tab__label {
    color: var(--gray-100);
}

.ui-tab__counter {
    color: var(--gray-400);
}

.ui-tab__counter--notify {
    background: var(--gray-900);
    color: var(--gray-000);
}

.ui-tab--active .ui-tab__label:after {
    background-color: var(--gray-100);
}

/* borders */
.ui-border--bottom,
.ui-border--top,
.ui-border--left,
.ui-border--right {
    border-color: var(--gray-500);
}

/* inputs */
.cdx-input,
.codex-editor .form-text-input,
.codex-editor .input,
.editor__title,
.ui_form--2 input[type=email],
.ui_form--2 input[type=name],
.ui_form--2 input[type=password],
.ui_form--2 input[type=text],
.ui_form--2 select,
.ui_form--2 textarea,
.input-suggestion input {
    border: 0!important;
    box-shadow: initial!important;
    background: var(--gray-600)!important;
    border-bottom: 1px solid var(--gray-900)!important;
    border-radius: 3px!important;
    color: var(--gray-100)!important;
}

/* submenus */
.ui_sub_menu,
.bubble,
.dropdown_list__item--selected,
.dropdown_list__item:hover {
    background: var(--gray-900)!important;
    border: 0!important;
    box-shadow: 0 3px 5px 0 rgba(37, 43, 49, .32)!important;
}

.ui_sub_menu--center-arrow:before,
.ui_sub_menu--left-arrow:before,
.ui_sub_menu--right-arrow:before,
.etc_control__list,
.etc_control__list:before,
.etc_control__item,
.bubble[arrow-alignment^="top"]:before,
.dropdown_list__item,
.dropdown_list__item svg {
    color: var(--gray-100)!important;
    background: var(--gray-900)!important;
    fill: var(--gray-400)!important;
    border: 0!important;
}

.ui_sub_menu__item:hover,
.etc_control__item:hover {
    background: var(--gray-800)!important;
}


/* links */
#notify .notify__item p a,
.andropov_telegram__text a,
.andropov_tweet__text a,
.best_comments__item__text a,
.comments__item__text a,
.content figcaption a,
.content p a,
.form2__field--advanced_list .advanced_list__list .adv_list_item a,
.island__container--with_links a,
.island__info a,
.island__list__link,
.page--error .error__message a,
.profile_comment_favorite__text a,
.profile_comment_self__text a,
.sitemap a,
.subsite_card_simple__description a,
.t-link-classic,
.ui-page-header a,
.block-warning a,
.best_comments__item__text a,
.block-quote a,
.block-warning a,
.comments__item__text a,
.content figcaption a,
.content h2 a,
.content h3 a,
.content h4 a,
.content ol a,
.content p a,
.content ul a,
.page--error .error__message a,
.profile_comment_favorite__text a,
.profile_comment_self__text a,
.sitemap a,
.subsite_card_simple__description a,
.subsite_head__description a,
.t-link-classic {
    color: var(--blue-300);
    border-bottom: 1px solid rgba(33, 136, 255, 0.4);
}

.live__item--comment .live__item__date:hover,
.live__item--comment .live__item__date:hover time,
.profile_comment_favorite__title:hover,
.profile_comment_favorite__title:hover time,
.profile_comment_favorite__user:hover,
.profile_comment_favorite__user:hover time,
.profile_comment_self__title:hover,
.profile_comment_self__title:hover time,
.t-link:hover,
.t-link:hover time,
.vote--comment .vote__users__item:hover,
.vote--comment .vote__users__item:hover time,
a.content-header-author:hover,
a.content-header-author:hover time,
a.content-header-number:hover,
a.content-header-number:hover time,
.comments_counter:hover .comments_counter__count__unit,
.comments_counter:hover .comments_counter__count__value,
.comments_counter:hover .comments_counter__count__value_new,
.live__item--comment .live__item__title span {
    color: var(--blue-300);
}

/* feed */
.news_item .time,
.comments_counter__count__unit,
.comments_counter__count__value,
.comments_counter__count__ico svg,
.news_widget__toggle,
.news_widget__toggle .icon,
.navigationDropdown__current__button,
.navigationDropdown__current__link,
.favorite_marker .icon,
.vote .vote__button,
.etc_control,
.content-header-number {
    color: var(--gray-400);
    fill: var(--gray-400);
}


.entry_flash {
    background: var(--gray-900);
    color: var(--gray-100);
}

/* left sidebar */
.sidebar__tree-list__title,
.sidebar__tree-list__button,
.sidebar__footer {
    color: var(--gray-400)!important;
}

.sidebar__tree-list__item--active {
    background-color: var(--gray-500)!important;
}

.sidebar__tree-list__item:not(.sidebar__tree-list__item--active):hover {
    background-color: var(--gray-600)!important;
}

.sidebar .ui-button {
    border: 0;
    background: var(--gray-300);
    color: var(--gray-900);
}

/* live */
.content__thanks a span,
.live__item--comment .live__item__content .live__item__text,
.live__item--comment .live__item__user__name {
    color: var(--gray-300);
}
.live__item--comment .live__item__date {
    color: var(--gray-400);
}

.live__item--notification-comment_replied,
.live__item--notification-entry_commented {
    color: inherit;
    background: var(--blue-900-5);
}

.live__item--notification-comment_voted-positive {
    color: inherit;
    background: var(--green-900-5);
}

.live__item--notification-comment_voted-positive .icon,
.live__item--notification-comment_voted-negative .icon {
    fill: var(--gray-100);
    opacity: 0.7
}

.live__item--notification:after,
.live__item--notification:before {
    background-color: var(--gray-700);
}

/* post */
.favorite_marker__count,
.favorite_marker__title {
    color: var(--gray-400);
}

.block-quote--centered,
.block-quote--default,
.block-incut,
.quiz,
.block-number,
.andropov_link,
.andropov_telegram,
.andropov_tweet,
.block-warning,
.content-image--with_background {
    background: var(--gray-900);
    color: inherit!important;
    border: 0!important;
}

.andropov_link__host,
.content .content-image-caption__author {
    color: var(--gray-400);
}

.content__thanks {
    background: var(--gray-500);
}

.content-header-label {
    background: var(--gray-500);
}

.content mark {
    background: var(--yellow-900);
    color: inherit;
}

.subsite_card_short_with_content,
.subsite_card_short {
    border: 0;
    background: var(--gray-800);
}

.subsite_card_short_with_content__content {
    border-color: var(--gray-600);
}

.subsite_subscribe_button__inner {
    border: 0;
    background: var(--gray-700);
    color: inherit;
}

.subsite_subscribe_button__inner--inactive .icon {
    fill: var(--gray-500);
}

.advert_footer {
    background: var(--gray-900);
}

.content__updating {
    color: var(--gray-400);
}

/* comments */
.best_comments__body:after,
.best_comments__header {
    border-color: var(--gray-500);
}

.best_comments__body:after {
    background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0), var(--gray-700));
}

.comments_form,
.comments_form__textarea {
    background: var(--gray-700);
    color: inherit;
}

.comments__item__self--author .comments__item__user__name .user_name {
    background: var(--gray-500);
}

.comments__item__space--pinned,
.comments__item__space--new,
.comments__item__space--highlighted {
    background: var(--gray-900);
}

.comments__item__date,
.comments__item__replied_to,
.comments__item__reply {
    color: var(--gray-400);
}

.comments__item__children,
.comments_form__textarea-holder {
    border-color: var(--gray-500);
}

.comments_form__upload_images,
.comments_form__video,
.comments_pseudo_form__button {
    background: var(--gray-600);
}

.comments_pseudo_form {
    background: var(--gray-800);
    border: 0;
}

.andropov_preview {
    background: var(--gray-800);
}

.andropov_preview--link .andropov_preview__hostname {
    color: var(--gray-400);
}

.vote--comment .vote__value {
    background: var(--gray-600);
    color: var(--gray-400);
    border-radius: 3px;
}

.vote--voted-positive .vote__button--plus,
.vote--sum-positive .vote__value {
    color: var(--green-600);
}

.vote--comment.vote--sum-positive .vote__value {
    background: var(--green-100);
}

.vote--voted-negative .vote__button--minus,
.vote--sum-negative .vote__value {
    color: var(--red-600);
}

.vote--comment.vote--sum-negative .vote__value {
    background: var(--red-100);
}

.comments_updates {
    background: var(--gray-900);
    color: var(--gray-200);
}

.comments_updates:hover {
    background: var(--gray-500);
}

#updates_arrows path {
    stroke: var(--gray-200)!important;
}


/* widgets */
.widget_wrapper--type-favorited,
.widget_wrapper--type-top_commented_entries {
    background: var(--purple-900-5);
}

.vacancies_widget {
    background: var(--blue-900-5);
}

.vacancies_widget__item__city,
.vacancies_widget__item__company,
.vacancies_widget__item__salary,
.vacancies_widget__item__city:hover,
.vacancies_widget__item__company:hover,
.vacancies_widget__item__title:hover,
.vacancies_widget__title:hover {
    color: var(--blue-300);
}

.vacancies_widget__new {
    border: 0;
    background: transparent;
    color: inherit;
}

.daily-promo-preview__item,
.daily-promo-unit,
.widget_wrapper--type-best_comments,
.widget_wrapper--type-popular_3_entries,
.widget_wrapper--type-yellow,
.island--blogs_entries {
    background: var(--gray-900)!important;
}

.daily-promo-unit__button {
    background: var(--gray-800)!important;
    border: 0!important;
}

.daily-promo-unit__label {
    color: var(--gray-400)!important;
}

.widget_wrapper--type-favorited .widget_wrapper__title,
.widget_wrapper--type-top_commented_entries .widget_wrapper__title {
    color: var(--pink-600);
}

.widget_wrapper--type-favorited .widget_wrapper__item,
.widget_wrapper--type-top_commented_entries .widget_wrapper__item {
    border-color: var(--pink-900);
}

/* editor */
.editor__header {
    background: var(--gray-700);
}

.ce-toolbar__tools,
.ce-toolbar__tools-search-input {
    background: var(--gray-800);
    box-shadow: initial;
}

.ce-toolbar__tools-item--selected,
.ce-toolbar__tools-item:hover {
    background: var(--gray-700);
}

.ce-toolbar__tools-item--selected::before,
.ce-toolbar__tools-item:hover::before {
    background: var(--gray-600);
}

.editor__title {
    color: var(--gray-100);
}

.editor__date,
.editor__select {
    color: inherit;
}

.adviser__container,
.adviser__toggler,
.adviser__container::after {
    background-color: var(--gray-900);
    border-color: var(--gray-700);
}

/* notifes on the top */
#notify .notify__item--shown {
    background: var(--gray-700);
}

/* subreddit/user page */
.subscribers_widget {
    color: var(--gray-400);
}

.hashtags_list .ui-tab__label,
.popup__content--pay_invoice .form2__field label {
    color: var(--gray-400);
}

.subsite_head__registration_date {
    color: var(--gray-400);
}

.profile_comment_favorite {
    border-color: var(--gray-500);
}

.profile_comment_favorite__footer__reply {
    color: var(--gray-400);
}

.subsite_head .subsite_subscribe_button__inner {
    background: var(--gray-800);
}

.island__social_links__item {
    color: var(--gray-200)!important;
    border-color: var(--gray-400)!important;
}

/* settings */
.subsite_settings__section {
    border-color: var(--gray-500);
}

.subsite_settings__section__title,
.ui_form label,
.subsite_settings__section__label {
    color: var(--gray-100);
}

/* small editor */
.small-editor__placeholder {
    background: var(--gray-700);
    box-shadow: initial;
    color: var(--gray-400);
}

.attaches__button {
    background: var(--gray-800);
}

.attaches__button--with-label {
    color: var(--gray-500);
}

.small-editor__form,
.small-editor__title {
    background: var(--gray-800);
    color: inherit;
    border-radius: 0;
}

/* notifications in header */
.head-notifies__panel {
    background: var(--gray-800);
    box-shadow: 0 3px 5px 0 #252b31;
}

.head-notifies__header,
.head-notifies__footer {
    border-color: var(--gray-700)!important;
}

.u-notification {
    background: var(--gray-900);
}

.u-notification:hover {
    background-color: var(--gray-800);
}

.notification--unread {
    background-color: var(--blue-900)!important;
}

.notification--unread:hover {
    background-color: var(--blue-800)!important;
}

.notification__footer,
.notification__subject-toggler {
    color: var(--gray-400);
}

.u-notification:not(:last-of-type) {
    border-bottom: 1px solid var(--gray-800);
}

.u-notification--border:first-of-type {
    border: 0!important;
}

.notify-new-items-label {
    background: var(--gray-700);
    color: var(--gray-300);
}

/* popups */
.captcha_wall__box,
.ui_modal_window,
.popup__container__window--styled .popup__container__window__tpl {
    background: var(--gray-800);
}

.ui_modal_window__footer,
.ui_modal_window__header--border {
    border-color: var(--gray-500);
}

.ui_modal_window--get_api_token .ui_modal_window__content input {
    color: inherit;
}

.popup__content--misprint .selected_text span {
    background: var(--gray-600);
    padding: .09em 0;
}

/* /subs */
.subsites_catalog__search,
.subsites_catalog__label--bordered {
    border-color: var(--gray-500);
}

.subsites_catalog_item:before {
    background: var(--gray-500);
}

.subsites_catalog__label {
    color: var(--gray-400);
}

.subsites_catalog__search__bar {
    background: inherit;
    color: inherit;
}

/* booster */
.b-form__unit_preview {
    background: var(--gray-800);
}
.daily-promo-form .editable-field__input,
.daily-promo-form .button-labeled__inner,
.daily-promo-form .button-apps__inner,
.daily-promo-form .b-form__unit_preview {
    color: var(--gray-900)!important;
}

/* plus page */
.page--plus .plus-features {
    background: var(--gray-900);
}
}

/*
Windows 10 + Chrome
1. 網址列輸入：「chrome://flags/」
2. 找到「Match @font-face { src: local(<name>) } names by PostScript and full font name.」
