@-moz-document url-prefix(https://krunker.io/social.html) {
	
html {background-image: none; background-color:black;} /*background-color*/


* {font-family: 'Comic Sans MS',sans serif ;font-size:24px;} /*font*/

#aHolder {visibility: hidden;} /* hides ad */

.scrollView {height:100%; overflow-y:scroll;padding-bottom:500px;} /* hides bottom bar */

#navBar {background-color:#111;} /* navbar color */

#navTitle {left:25%;} /* Krunker link moved to the left */

.leaderNav {margin-top:-86px;margin-bottom:25px;padding-top:4px;}     /* navbar into top bar */
.leaderBtn {background-color: #0000!important;}
#profileLogin {z-index:1;}

.purchBtn {background-color:transparent; color: transparent; margin-top:-240px;height:240px;width:200px;border-radius:7px;}  /* info button is now as big as the whole card and invisible */
.purchBtn:hover {opacity:.2;background-color:#999;}  /* info button hover */

.itemOwn {color:transparent;}   /* hides the item class, e.g. sniper, back item */
.marketCard {margin-bottom:-20px;background-color: transparent;}

.purchInfoBtn {background-color: #111;color: #555; z-index:11;position: relative;}  /* hides bottom bar */
.purchInfoBtn:hover {background-color:#ffc147; }

.marketPrice {color:#f4f4f4;} /* price color */

#marketSearchB , #filterTog , #filterRBtn {background-color:#ffc147;}  /* buttons color change */
#marketSearchB:hover , #filterRBtn:hover , #filterTog:hover{background-color:#ffce70;}
input:checked+.sliderSml  {background-color:#ffc147;}

::-webkit-scrollbar-track {background-color: #111;border-radius:0;} /*scrollbar color change*/
::-webkit-scrollbar-thumb {background-color: #222;}

#sortFilt {overflow:clip!important;}

#profileKR {color:#ffc147;} /* KR color */

#marketFiltTxt {color:white;background-color:#222;}         /* search filter color */
#sortFilt , #seaFilt  {background-color:#111; color: white;font-size:12px;}
#sortFilt *, #seaFilt * {background-color:#111; color: white;}

.lds-ring {                     /* new loading animation */
  display: inline-block;
  position: relative;
  width: 80px;
  height: 80px;
}
.lds-ring:after {
  content: " ";
  display: block;
  border-radius: 50%;
  width: 0;
  height: 0;
  margin: 8px;
  box-sizing: border-box;
  border: 32px solid #fff;
  border-color: #ffc147 transparent #ffc147 transparent;
  animation: lds-ring 1.2s infinite;
}
@keyframes lds-ring {
  0% {
    transform: rotate(0);
    animation-timing-function: cubic-bezier(0.55, 0.055, 0.675, 0.19);
  }
  50% {
    transform: rotate(900deg);
    animation-timing-function: cubic-bezier(0.215, 0.61, 0.355, 1);
  }
  100% {
    transform: rotate(1800deg);
  }
}
.lds-ring div {visibility: hidden;}


.raritySel {margin-bottom:-5px;}

/* social pages */ 

.scrollItem {background-color:#222; box-shadow:none;}
.scrollItem div , .scrollItem a , .scrollItem span {color:white;}
a.lName {color:#ffc147!important;}

.pSt strong {color:orange;}
.membSinc span {color:lightgrey!important;}

.leaderText a {color:white!important;}
.profileName a {color:#ffc147!important;}	
	}
