@-moz-document domain("postimg.cc"), domain("postimages.org") {
body {
	background: url(/*[[bg]]*/) center left no-repeat fixed !important;
	background-size: cover !important;
	color: #fff;
}
a,
.btn {
	color: /*[[theme]]*/;
}
.gallery .count,
.thumb .imagetitle,
.logins a.active,
.logins span.active,
.mainmenu a.active,
.mainmenu span.active {
	color: #fff;
}
a:focus,
a:hover {
	color: #fff;
	text-decoration: none;
}
.logins a:focus,
.logins a:hover,
.mainmenu a:focus,
.mainmenu a:hover {
	background: none;
	color: #fff;
	border-radius: 20px 20px 0px 0px;
}
#sb-main {
	background: none !important;
}
.panel,
.panel input:not([type="submit"]):not([type="reset"]):not([type="checkbox"]):not([type="radio"]):not([type="file"]):not([type="image"]),
.panel textarea,
.frontpage #content,
textarea,
select,
input,
.gallery,
.gallery h3:focus,
.gallery h3[contenteditable="true"]:hover,
.modal-content,
.modal-header,
header,
.thumb,
.btn,
.thumb .imagetitle:focus,
.tooltip-inner,
.uploaddrop {
	background: rgba(0, 0, 0, /*[[opacity]]*/) !important;
	border: none !important;
}
.gallery-list,
.image-controls,
.tooltip-arrow,
.queue-item {
	background: transparent !important;
}

.tooltip.top .tooltip-arrow,
.tooltip.top-left .tooltip-arrow,
.tooltip.top-right .tooltip-arrow,
.tooltip.right .tooltip-arrow,
.tooltip.left .tooltip-arrow,
.tooltip.bottom .tooltip-arrow,
.tooltip.bottom-left .tooltip-arrow,
.tooltip.bottom-right .tooltip-arrow,
.queue-item,
.queue-item .progress,
#add_gallery_name,
input:not([type="submit"]):not([type="reset"]):not([type="checkbox"]):not([type="radio"]):not([type="file"]):not([type="image"]),
textarea {
	border: none !important;
}

.thumb {
	border-radius: 7px;
}

footer,
.mainmenu > ul:nth-child(1) > li:nth-child(3),
.mainmenu > ul:nth-child(1) > li:nth-child(4),
.hero > div:nth-child(2),
.hero > div:nth-child(4) > div:nth-child(1),
.additional {
	display: none;
}
.logins > ul:nth-child(1) > li:nth-child(1) {
	padding-right: 654px;
}
.frontpage h1 {
	font-family: Segoe UI;
}
.frontpage h1:after {
	content: "!";
}
.logo img {
	opacity: 0;
}
.logo {
	background: url("https://s19.postimg.cc/k1zaurr7n/logo3.png") center no-repeat;
	background-size: contain;
}
}

@-moz-document domain(americanyawp.com) {
div#toc_container, body header, body footer, body nav, body,body.custom-background, body div.site, body div, #body, h1, a, p, #body span, span, li, ul{
  background:black;  
