    padding-right: 0.5em;
    color: #A3A3A3;
}
.mw-body {
    margin-left: 10em;
    border: 1px solid #9BAED2;
    border-right-width: 0;
    margin-top: -1px;
}
.vectorTabs {
    float: left;
    height: 2.5em;
    background-position: bottom left;
    background-repeat: no-repeat;
    padding-left: 1px;
    border-left: 1px solid #9BAED2;
    border-top: 1px solid #9BAED2;
    border-right: 1px solid #9BAED2
}
.mw-search-profile-tabs {
    background-color: #101010;
    margin-top: 1em;
    border: 1px solid #9BAED2;
    border-radius: 2px;
}
.smwb-theme-light .smwb-propval {
    background-color: #101010;
}
.smwb-theme-light .smwb-propval {
    background-color: #101010;
}
.smwb-propval {
    background-color: #101010;
}
.smwb-theme-light .smwb-title, .smwb-theme-light .smwb-center, .smwb-theme-light .smwb-actions, .smwb-theme-light .smwb-prophead {
    background-color: #101010;
}
.smwb-theme-light .smwb-factbox {
    border-left: 0.5em solid #9BAED2;
    background-color: #101010;
}
.smwb-factbox .smwb-cell {
    border: 0px solid #9BAED2;
    border-top: 2px solid #9BAED2;
}
.smwb-theme-light .smwb-ifactbox {
    border-right: 0.5em solid #9BAED2;
}
.smwb-ifactbox .smwb-cell {
    border: 0px solid #9BAED2;
    border-top: 2px solid #9BAED2;
}
.smwb-bottom {
    border-bottom: 2px solid #9BAED2;
}
}

body {
    background-color: black;
    color: lightgreen;
    padding-left: 25%;
    padding-right: 25%;
    font-size: 22;
}

pre {
    font-size: 30;
}
