@-moz-document url-prefix("https://chromium.googlesource.com/chromium/src/"), url-prefix("https://chromium.googlesource.com/v8/"), regexp("https://[-\\w]+\\.googlesource\\.com/[-.\\w]+/([^\\/]+/)?\\+.*") {
.Site {
  background: #222;
  color: #999;
  font: 100%/1.3 'Consolas', monospace;
  overflow-wrap: break-word;
  white-space: pre-wrap;
}
.Site-header,
.Site-footer,
.Footer {
  background: #333;
  color: #999;
}
.Container {
  max-width: 1200px;
}
a:link,
.doc a:link {
  color: lightskyblue;
  text-decoration: none;
}
a:visited,
.doc a:visited {
  color: #a67bec;
}
.Breadcrumbs-crumb:last-child {
  color: yellowgreen;
}
.CommitLog-item--chromiumFuller {
  margin-bottom: 3em;
}
.Metadata {
  color: #999;
  font-size: .9rem;
  line-height: 1.0;
  margin-bottom: 0
}
.Metadata a:link {
  color: #81a2b7;
}
.Metadata th {
  font-weight: normal;
}
.Metadata tr:nth-child(2) td:nth-child(2) {
  color: #fff;
}
.Metadata tr:nth-child(2) td:nth-child(2) a {
  color: #93e3f9;
}
.Metadata tr > *:first-child {
  width: 6rem
}
.Metadata th,
.Metadata td {
  padding-right: 1rem;
}
.MetadataMessage {
  background: linear-gradient(to bottom, #333 2.75rem, transparent 2.75rem);
  color: #BBB;
  padding-left: 6.2rem;
  text-indent: -5rem;
  font-size: 1rem;
  line-height: inherit;
  border: none;
  padding-top: .5ex;
  padding-bottom: 0;
}
.MetadataMessage::first-line {
  color: #ffc72c;
  font: bold 120%/2.5rem sans-serif;
}
.MetadataMessage a:link {
  color: cadetblue;
  font-family: sans-serif;
}
.DiffTree {
  padding-left: 6rem;
  line-height: 1.1;
  opacity: 0.4;
  transition: 0.5s opacity;
  -webkit-filter: grayscale(50%);
  filter: grayscale(50%);
}
.DiffTree-action--add,
.Diff-insert {
  color: yellowgreen;
}
.DiffTree-action--delete,
.Diff-delete {
  color: #b7743a;
}
.DiffTree-action--rename,
.DiffTree-action--copy,
.Diff-hunk {
  color: cyan;
}
pre.Diff {
  height: 2.5rem;
  overflow: hidden;
  margin-top: 2rem;
}
.Diff-fileIndex + a {
  font-weight: bold;
  background: #004e99;
  padding: 2px .5em;
  border-radius: .5em;
}
.Diff-fileIndex + a + a {
  font-family: sans-serif;
}
.Diff-unified {
  border: none;
  margin-left: 5.5rem;
  padding: .25rem .5rem .5rem;
  background: #282828;
}
.CommitLog-item:hover .DiffTree {
  opacity: 1.0;
  -webkit-filter: none;
  filter: none;
}
.u-sha1 {
  background-color: #444;
  color: #eee;
  font-weight: bold;
  font-size: 1rem;
}
.CommitLog-author:not([title*="autoroll"]):not([title^="chrome-"]) {
  color: #caba88;
}
.CommitLog-time {
  color: inherit;
}
.FileList-item--regularFile::before {
  filter: invert(.9);
}
.FileList-item:hover {
  background: #333;
}
.doc pre,
.doc code {
  background: #181818;
  border: 1px solid #333;
}
.CloneRepo {
  background: #333;
}
input[type="text"] {
  color: inherit;
  background: #111;
  border-color: #444;
}

/* commit diff view */
.u-lineNum {
  border: none;
}
.u-lineNum::before {
  color: #666;
}
.com {
  color: #7e6a4b;
}
.kwd {
  color: #CC7833;
  font-weight: bold;
}
.u-pre {
  font-size: 1rem;
}
.pln {
  color: #BBB;
}
.typ {
  color: palevioletred;
}
.lit {
  color: turquoise;
}
.pun {
  color: #888;
}
.str {
  color: yellowgreen;
}
.tag {
  color: #E8BF6A;
  font-weight: bold;
}
.atn {
  color: violet;
}
.atv {
  color: orange;
}
.dec {
  color: #3aac97;
}
::-webkit-scrollbar {
  width: 14px;
  height: 14px;
  background: #1a1a1a;
}
::-webkit-scrollbar-button:single-button {
  background: radial-gradient(circle at center, #333 40%, #1a1a1a 40%)
}
::-webkit-scrollbar-button:single-button:hover {
  background: radial-gradient(circle at center, #444 40%, #1a1a1a 40%);
}
::-webkit-scrollbar-button:single-button:active {
  background: radial-gradient(circle at center, #555 40%, #1a1a1a 40%);
}
::-webkit-scrollbar-track-piece {
  background: #333;
  border: 4px solid #1a1a1a;
  border-radius: 8px
}
::-webkit-scrollbar-track-piece:hover {
  background: #444;
}
::-webkit-scrollbar-track-piece:active {
  background: #555;
}
::-webkit-scrollbar-thumb {
  border: 3px solid #1a1a1a;
  border-radius: 8px;
  background: #555
}
::-webkit-scrollbar-thumb:hover {
  background: #666;
}
::-webkit-scrollbar-thumb:active {
  background: #777;
}
:-webkit-autofill {
  box-shadow: 0 0 0 1000px #111 inset;
  -webkit-text-fill-color: #fff;
}
}

