#metadata-line.ytd-grid-video-renderer {line-height: 1.3em; color: #767676; max-width: 100%; max-height: 2.6rem; font-size: 1.1rem; font-weight: 500;
-o-display: inline-block; -o-letter-spacing: 0px; margin-top: 0px; }
/*вместимость шрифта по горизонтали - выравнивание по правому Краю*/
#meta.ytd-grid-video-renderer {padding-right: 0px /*0px - так нужно*/;}

/* МЕНЮ КАНАЛА: Главная, Видео, Плейлисты, Каналы, Обсуждение, О Канале */
