data.css:
@-moz-document domain("nyaa.si") {
body {
    background: #20252a;
    color: #FFF;
}

/* 

	HEADER 

*/
.navbar {
    background: #343b41;
    border: none;
    border-top: 4px solid #577289;
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.2);
}
.navbar-brand,
.navbar-nav > li > a {
    color: #DDD !important;
}
.navbar-brand:hover,
.navbar-nav > li > a:hover {
    color: #FFF !important;
}
.navbar-inverse .navbar-nav > .active > a,
.navbar-inverse .navbar-nav > .active > a:hover,
.navbar-inverse .navbar-nav > .active > a:focus {
    background: #577289;
}

/* HEADER: Dropdown menus */
.open > a {
    background: rgba(0, 0, 0, 0.3) !important;
}
.navbar-nav > li > .dropdown-menu,
.navbar-right .dropdown-menu {
    background: #343b41;
    margin-top: 22px;
    border-radius: 6px;
    border: none;
    overflow: visible !important;
    padding: 3px;
    left: 0;
    right: auto;
}
.navbar-nav > li > .dropdown-menu {
    margin-top: 12px;
}
.dropdown.open .dropdown-toggle::after {
	content: '';
	width: 0;
	height: 0;
	border: 8px solid transparent;
	border-bottom-color: #343b41;
	position: absolute;
	top: 46px;
	margin-left: -10px;
}
.btn-group.open::after {
	content: '';
	width: 0;
	height: 0;
	border: 8px solid transparent;
	border-bottom-color: #343b41;
	position: absolute;
	margin-left: -25px;
	top: 40px;
}
.navbar-nav > li > .dropdown-menu a,
.dropdown-menu li a {
    padding: 6px 10px;
    border-radius: 6px;
    margin: 3px;
    color: #DDD;
}
.dropdown-menu li a[href="/logout"] {
    background: #ee5050;
    color: #FFF;
}
.dropdown-menu li a[href="/logout"]:hover {
    background: #eb6b6b;
    color: #FFF;
}
.dropdown-menu li a:hover {
    background: rgba(215, 235, 255, 0.1);
    color: #FFF;
}
.dropdown-menu li a:focus {
    background: rgba(215, 235, 255, 0.1);
    color: #FFF;
    outline: 0;
}
.dropdown-menu::-webkit-scrollbar {
    width: 0px;
}
.dropdown-menu > .active > a,
.dropdown-menu > .active > a:hover,
.dropdown-menu > .active > a:focus {
    background: rgba(215, 235, 255, 0.1);
    color: #FFF !important;
}

/* HEADER: Search and filters */
.input-group-btn:first-child > .btn,
.input-group-btn:first-child > .btn-group,
.btn-group {
    margin-right: 4px;
}
.btn-group.open .dropdown-toggle {
    box-shadow: none;
}
.bootstrap-select {
    border: none!important;
}
.bootstrap-select button {
    border: 1px solid rgba(0, 0, 0, 0.1) !important;
}
.navbar-form .dropdown-toggle {
    background: rgba(0, 0, 0, 0.25);
    border: none;
    border-radius: 4px !important;
    color: #FFF;
}
.dropdown-toggle.btn-default:hover,
.dropdown-toggle.btn-default:focus,
.dropdown-toggle.btn-default:active,
.dropdown-toggle.btn-default:active:hover {
    background: rgba(0, 0, 0, 0.35) !important;
    color: #FFF;
}
.bootstrap-select .dropdown-toggle:focus {
    outline: 0 !important;
}
.open > .dropdown-toggle.btn-default,
.open > .dropdown-toggle.btn-default:focus,
.btn-default:focus,
.btn-default.focus {
    background: rgba(0, 0, 0, 0.35) !important;
    color: #FFF !important;
}
.open > .dropdown-toggle.btn-default:hover {
    background: rgba(0, 0, 0, 0.35);
    color: #FFF;
}
.form-control.search-bar {
    background: rgba(0, 0, 0, 0.25);
    border: 1px solid rgba(0, 0, 0, 0.1);
    border-radius: 4px !important;
    color: #FFF;
    padding: 6px 12px;
    height: auto;
    margin-right: 3px;
}
.form-control.search-bar:focus {
    background: rgba(0, 0, 0, 0.35);
    box-shadow: none;
}
.input-group-btn:last-child > .btn {
    margin-left: 5px;
    border-radius: 4px;
}
.btn-primary,
.btn-success,
.btn-default {
    border: 1px solid rgba(0, 0, 0, 0.5) !important;
    color: #FFF;
    background: rgba(215, 235, 255, 0.2);
    outline: 0 !important;
}
.btn-primary:focus,
.btn-success:focus,
.btn-default:focus {
    background: rgba(215, 235, 255, 0.2);
}
.btn-primary:hover,
.btn-success:hover,
.btn-default:hover {
    color: #FFF;
    background: rgba(215, 235, 255, 0.30);
}
.btn-primary:active,
.btn-success:active,
.btn-default:active {
    color: #FFF;
    background: rgba(215, 235, 255, 0.30)!important;
    box-shadow: none;
}
.btn-primary:active:hover,
.btn-success:active:hover,
.btn-default:active:hover {
    color: #FFF;
    box-shadow: none !important;
}
.dropdown-menu .divider {
    margin: 0px 11px;
    background: rgba(0, 0, 0, 0.3);
}
.glyphicon {
    top: 10%;
}

/* 

	BODY 

*/
.alert-info {
    background: rgba(117, 185, 255, .1) !important;
    border: 1px solid rgba(117, 185, 255, .1) !important;
    color: #FFF !important;
}
.table,
.table-bordered > thead > tr > th,
.table-bordered > tbody > tr > th,
.table-bordered > tfoot > tr > th,
.table-bordered > thead > tr > td,
.table-bordered > tbody > tr > td,
.table-bordered > tfoot > tr > td {
    border: none;
}
.table {
    margin-bottom: 0px;
    overflow: hidden;
    background: rgba(0, 0, 0, 0.2);
}
.table-responsive {
    border-radius: 5px;
    border: 1px solid rgba(0, 0, 0, 0.4);
}
.table thead {
    background: none;
    color: #FFF;
    border-bottom: 2px solid rgba(0, 0, 0, 0.4);
}

/* BODY: Torrents */
.table tbody tr {
    border-top: 1px solid rgba(0,0,0,0.4);
    padding: 5px;
}
.table tbody tr * {
    background: none !important;
    color: #FFF;
    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.7);
}
body.dark .torrent-list > tbody > tr {
	color: #FFF;
}
.table-hover > tbody > tr:hover {
    background: none;
}
.table-striped > tbody > tr.default {
    background: rgba(215, 235, 255, 0.1) !important;
}
.table-striped > tbody > tr:nth-of-type(odd) {
    background: rgba(215, 235, 255, 0.05) !important;
}
.table-striped > tbody > tr.default:hover {
    background: rgba(215, 235, 255, 0.1) !important;
}
.table-striped > tbody > tr:nth-of-type(odd):hover {
    background: rgba(215, 235, 255, 0.05) !important;
}
.table-striped > tbody > tr.success,
.panel-success .panel-heading {
    background: #6ad56e61 !important;
}
.table-striped > tbody > tr.success:hover,
.panel-success .panel-heading:hover {
    background: #6ad56e61 !important;
}
.table-striped > tbody > tr.danger,
.panel-danger .panel-heading {
    background: #eed5626e !important;
}
.table-striped > tbody > tr.danger:hover,
.panel-danger .panel-heading:hover {
    background: #eed5626e !important;
}
.modal-content {
    background: #22252a;
}
.modal-header {
    padding: 10px;
    border-bottom: 1px solid #44474b;
}
.modal-header .close {
    color: #FFF;
    text-shadow: none;
    line-height: normal;
    margin-top: 0px;
    margin-right: 5px;
}
/* BODY: Inset stuff */
table.torrent-list tbody tr td a:visited,
.pagination-page-info {
    color: #FFF;
}
.table tbody tr a.comments {
    background: rgba(0, 0, 0, 0.5) !important;
    color: #FFF;
    border: none;
    margin-top: 1px;
}
body.dark table.torrent-list tbody .comments {
	color: #FFF;
}
.hdr-link {
    width: 56px !important;
}
.text-center {
    font-weight: 600;
}
tbody .text-center {
    text-align: right;
}
td:nth-child(6) {
    color: #3F6 !important;
}
td:nth-child(7) {
    color: #F35 !important;
}
.fa-download:hover {
    color: #3F6;
}
.fa-magnet:hover {
    color: #F35;
}

/* BODY: Torrent Pages */
.panel {
    background: rgba(0, 0, 0, 0.2);
    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.5);
    border: none;
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.25);
}
.panel-heading {
    border: none;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2);
}
.panel-default .panel-heading {
    border: none;
    background: rgba(215, 235, 255, 0.15);
    color: #FFF;
}
.panel-success .panel-heading {
    color: #CEC;
}
.panel-danger .panel-heading {
    color: #FDB;
}
.panel-body {
    color: #FFF;
}
.panel-footer {
    background: rgba(215, 235, 255, 0.10);
    color: #FFF;
    border: none;
}
.panel-footer a i:hover {
    color: inherit;
}
#torrent-description p:last-child {
    margin-bottom: 0px;
}
#comments {
    padding-bottom: 10px;
}
.comment-box {
    margin-top: 20px;
}
#comment,
body > .container input:not(.btn),
textarea,
select.form-control {
    background: rgba(215, 235, 255, 0.1) !important;
    border: 1px solid rgba(215, 235, 255, 0.1) !important;
    color: #FFF !important;
}
#comment:focus,
body > .container input:not(.btn):focus,
textarea:focus,
select.form-control:focus {
    outline: 0 !important;
    color: #FFF;
    border: 1px solid rgba(215, 235, 255, 0.1) !important;
    box-shadow: none !important;
    background: rgba(215, 235, 255, 0.15) !important;
}
#comments .btn-sm {
    float: right;
    padding: 10px 20px;
}
.alert-warning {
    background: #f3e1833b;
    border-color: #f3e1833b;
    color: #f3e183;
    margin: 5px;
}

/*

	PAGINATION 

*/
.pagination li a {
    margin: 5px;
    margin-top: 0px;
    border-radius: 3px;
    background: rgba(215, 235, 255, 0.2);
    color: #EEE;
    border: none;
}
.pagination li a:focus {
    background: rgba(215, 235, 255, 0.2);
    color: #EEE;
}
.pagination li a:hover {
    background: rgba(215, 235, 255, 0.5);
    color: #FFF;
}
.pagination li.active a {
    background: rgba(215, 235, 255, 0.5);
    color: #FFF;
}
.pagination li.active a:hover {
    background: rgba(215, 235, 255, 0.5);
    color: #FFF;
}
.pagination .active a:focus {
    background: rgba(215, 235, 255, 0.5);
}
.pagination li.disabled a {
    background: rgba(0, 0, 0, 0.4);
}
.pagination li.disabled a:hover {
    background: rgba(0, 0, 0, 0.4);
}
.pagination li.disabled a:focus {
    background: rgba(0, 0, 0, 0.4);
}
.pagination li:first-child a,
.pagination li:last-child a {
    padding: 2px 8px;
    margin-top: 4px;
}

/* RESPONSIVE FIXES */

@media (max-width: 1228px) {
    .navbar-nav > li > .dropdown-menu,
    .navbar-right .dropdown-menu {
        left: auto;
        right: 0;
    }
}
@media (max-width: 991px) {
	.dropdown.open .dropdown-toggle::after {
		display: none;
	}
}

/* GENERAL ELEMENTS */

a {
    color: #a4c6f6;
}
a:hover {
    color: #c9e0fc;
}
kbd,
code,
pre {
    border: none;
    color: #d1e6f8;
    background: rgba(215, 235, 255, 0.1);
    box-shadow: none;
}
option {
    background: #343b41;
}
textarea {
    max-width: 100%;
}
::selection {
    background: rgba(215, 235, 255, 0.2);
}
footer {
	background: rgba(0, 10, 20, 0.2);
	padding: 10px;
}
footer p {
	margin: 0px;
}
}

@-moz-document regexp(".*nyaa.si/profile") {
ul.nav-tabs#profileTabs {
    margin: 0;
    border: none;
}
.nav-tabs > li {
    margin: 0;
}
#profileTabs li a {
    color: #FFF;
    border: none;
}
.nav > li > a:focus {
    outline: 0;
    background: none;
}
#profileTabs li:not(.active) a:hover {
    background: rgba(215, 235, 255, 0.05);
}
#profileTabs .active a {
    background: rgba(215, 235, 255, 0.1);
}
.tab-content {
    background: rgba(215, 235, 255, 0.1);
    padding: 15px;
    margin-bottom: 20px;
    border-radius: 6px;
    border-top-left-radius: 0px;
}
hr {
    display: none;
}
}

@-moz-document regexp(".*nyaa.si/help"), regexp(".*nyaa.si/rules"), regexp(".*nyaa.si/profile"), regexp(".*nyaa.si/upload") {
body > .container {
    background: rgba(0, 0, 0, 0.2);
    border-radius: 10px;
    margin-bottom: 20px;
}
}

@-moz-document regexp(".*nyaa.si/upload") {
ul.nav-tabs {
    margin: 0;
    border: none;
}
.nav-tabs > li {
    margin: 0;
}
.nav-tabs li a {
    color: #FFF !important;
    border: none;
}
.nav > li > a:focus {
    outline: 0;
    background: none;
}
.nav-tabs li:not(.active) a:hover {
    background: rgba(215, 235, 255, 0.05);
}
.nav-tabs > li.active > a {
    background: rgba(215, 235, 255, 0.1) !important;
    border: none !important;
}
.tab-content {
    background: rgba(215, 235, 255, 0.1);
    padding: 15px;
    margin-bottom: 20px;
    border-radius: 6px;
    border-top-left-radius: 0px;
}
.well {
    margin: 0;
}
form .row:last-child {
    margin-top: -20px;
}
#description-markdown-target {
    background: #1a1e22;
    border: none;
}
}

