/* logo */
.oJZym div.Igw0E.rBNOH.eGOV_.ybXk5._4EzTm { background-image: url('https://hupaa.com/images/insta.png') !important; background-repeat: no-repeat; background-size: 300px; height: 52px; }
.oJZym div.Igw0E.rBNOH.eGOV_.ybXk5._4EzTm img { display: none; }

/* search bar */
