@-moz-document domain("fimtale.com") {
body {
    background-color: #1a1a1a !important;
}
nav {
    background-color: #3a3a3a;
}
.row .col.l9 {
    width: 75%;
    margin-left: auto;
    left: auto;
    right: auto;
    background: #252525;
    color: #999;
    /* text-shadow: 0 0 10px #782596; */
    box-shadow: 1px 4px 7px 8px #0000002e;
}
/* 文章卡片 */
.card {
    background: #88888873;
    box-shadow: 0 0 7px 0px #00000085;
    color: #eeedea !important;
    transition: 0.3s;
    border: 1px solid #88888873;
}
.card:hover {
    box-shadow: 0 0 17px 3px #060606e3;
    border: 1px solid white;
}

.card .card-action {
    background-color: #000000cf;
    /* border-top: 1px solid rgba(241, 46, 46, 0); */
    position: relative;
    padding: 16px 24px;
    /* text-shadow: 0px 1px 0px white; */
}
/* 更多信息 */
.card .card-reveal {

    background-color: #2f120b;
}

.black-text {
    color: #d1d1d1 !important;
    text-shadow: 0 0 22px #000000ab;
    transition: 0.3s;
}

.black-text:hover {
    color: #fdffff !important;
}
.by-theme-text {
    color: rgba(244, 244, 244, .87);
    text-shadow: 6px 0px 13px #fbfbfb57, -6px 0px 13px #fbfbfb52;
}

footer {
    padding-top: 20px;
    /* color: #503f3f85 !important; */
    background-color: #730005;
    box-shadow: 0 0 black;
}

#background-pony-word-panel {
    background-color: #535353;
    color: wheat;
}

#sider-divider {
    visibility: hidden;
}

/* 搜索栏 */
.search-bar .select-wrapper {
    background-color: #6b6666;
}

.search-bar .col {
    background-color: #6b6666;
}
/* 过滤弹出菜单 */
.dropdown-content {
    background-color: #6b6666;
}

.dropdown-content li {
    min-height: 0;
}
.dropdown-content li > a,
.dropdown-content li > span {
    font-size: 12px;
    color: #ffffffd6;
    display: block;
    line-height: 22px;
    padding: 1px 10px;
}
.dropdown-content li:hover,
.dropdown-content li.active {
    background-color: rgba(26, 26, 26, 0.56);
}
/* 右边刷新推荐 */
.collection .collection-item {
    /* margin: 0; */
    border-bottom: 0px;
}
.collection {
    margin: 0em;
    border: 0px;
    border-radius: 2px;
    overflow: hidden;
    position: relative;
}

/* 阅读界面  字体选择*/
.select-wrapper input.select-dropdown {
    color: #c99de0;
    /*     box-shadow: inset 0 0 8px 3px #0000005c; */
    padding: 0px 10px;
    border-bottom: 0;
    height: 2.5rem;
    border-radius: 22px;
}
.main-content {
    font-size: 11px;
    color: #f9f4d5;
}

.story-info p {
    padding: 4px;
    color: #dedede;
    text-shadow: 8px 0px 20px black;
}

.grey {
    background-color: #9e9e9e !important;
    height: 4px!important;
}
.page-footer {
    padding-top: 20px;
    color: #fff;
    background-color: #2b2626;
}
#mainreadingbox {
    padding: 50px;
}
/* 按钮 */
.btn {
    margin: 5px;
    color: cyan;
    background-color: black;
    box-shadow: 9px 9px #0000004d;
    transition: 0.3s;
}
/* 评论区  */
.comment-container {
    margin: 41px 0;
    padding: 23px;
    /* border: 1px solid; */
    box-shadow: 5px 3px 11px 0px #171211;
}
.comment-avatar a img {
    border-radius: 50%;
    box-shadow: 0 0 6px black;
    padding: 0;
}
.comment-info,
.comment-info-extra,
.comment-date,
.comment-actions {
    margin: 0;
}
/* 小标签 */
.chip{
    color:#1c1a1a !important;
        border-radius: 3px;
    background-color: #484848;
    box-shadow: inset 0px 1px 7px #000000ba;
}
/* 某些右侧的推荐圆形图片 */
.circle {
    border-radius: 8%;
    box-shadow: 3px 5px 16px 3px #000000;
}
.circle:hover {
    box-shadow: 3px 5px 16px 3px #ff5f00;
}
.hoverable:hover {
    box-shadow: inset 0 0 10px black;
}
}

