@-moz-document domain("nitrotype.com") {
/* Everything in the :root selector is a customizable color :) */

:root {
    
    /* Start Custom Site Colors */
    
    --custom-color: #d62f3a; /* Site Logo, Button Color */
    --custom-color-lighter: #d62f3a; /* Main Color Highlight ex. Hovering over a button */
    --custom-secondary: #1C99F4; /* Header Of Most Site Tabs, ex. Dealership Header + Profile BG */
    --custom-secondary-translucent: #1C99F4ab; /* Used For The Profile Background */
    --custom-secondary-dark: #0e4d7a; /* Darker Version Of The Secondary Color */

    --custom-text-color: #fff; /* Main text color */
    --custom-text-color-dark: #d7d7d7; /* Darker Text Color ex. Unselected Sections In Dealership */
    
    /* End Custom Site Colors */
    
    /* Start Custom Input Typing Box Colors */
    
    /* Default Colors */
    --main-text-color: #2E3141;
    --main-text-bg-color: #fff;
    --main-bg-color: var(--main-text-bg-color); /* Can be changed to a hex value, but not really recommended */
    
    /* Incorrectly Typed Letter */
    --error-color: #fff;
    --error-bg-color: #D62F3A;
    
    /* Already Correctly Typed Letter */
    --correct-color: #2E3141;
    --correct-bg-color: transparent;
    --correct-opacity: 0.5;
    
    /* Current Letter */
    --current-color: #fff;
    --current-bg-color: #1C99F4;
    
    /* End Custom Input Typing Box Colors */
}

/* Start Typing Box Edits */

.dash-copy, .dash-copyContainer {
    background: var(--main-bg-color) !important;
}

.dash-letter {
    background-color: var(--main-text-bg-color);
    color: var(--main-text-color);
}

.dash-letter.is-waiting {
    color: var(--current-color);
    background-color: var(--current-bg-color);
}

.dash-letter.is-correct {
    color: var(--correct-color);
    background-color: var(--correct-bg-color);
    opacity: var(--correct-opacity);
}

.dash-letter.is-incorrect {
    color: var(--error-color);
    background-color: var(--error-bg-color);
}

/* End Typing Box Edits */

/* Start Site Color Edits */

path[fill="#D62F3A"] {
    fill: var(--custom-color);
}



.profile-bgHead {
    background: url("https://www.nitrotype.com/dist/site/images/themes/profiles/default/bg-mid.png") no-repeat 75% -50%/45%, top left repeat, linear-gradient(90deg, var(--custom-secondary-translucent) 60%, var(--custom-secondary-dark) 100%) !important;
}

.btn--primary, .dais-tags, .btn--negative {
    background-color: var(--custom-color) !important;
    color: var(--custom-text-color);
}

.btn--primary:hover, .btn--primary:focus, btn--negative:hover {
    background-color: var(--custom-color-lighter) !important;
}

.btn {
    box-shadow: inset 0px -3px 0px 0px rgba(2, 2, 2, 0.5);
}

.prog--c .prog-barFill, .btn.is-active, .raceResults-title, .raceResults-title--main {
    background-color: var(--custom-secondary) !important;
    color: var(--custom-text-color);
}

.raceResults-title--main::after {
    background-color: var(--custom-secondary);
}

.raceResults-title--place, .raceResults-title--place:after {
    background-color: var(--custom-secondary-dark) !important;
}

.season--default .season-date {
    border-left-color: var(--custom-secondary);
}

.tc-dodger {
    color: var(--custom-secondary);
}

h1, h2, h3, a {
    color: var(--custom-text-color);
}

.has-btn.has-btn--vertical a {
    color: var(--custom-text-color-dark) !important;
}

.has-btn.has-btn--vertical .is-active {
    color: var(--custom-text-color) !important;
}

.link .link--bare .mrxxs .twb {
    color: var(--teamColor) !important;
}

.bg--gradient {
    background: url(https://www.nitrotype.com/dist/site/images/backgrounds/bg-noise.pung) top left repeat, var(--custom-secondary);
}

.bg--gradient h1 {
    color: var(--custom-text-secondary) !important;
}

.raceResults-close {
    background-color: var(--custom-color);
}

.raceResults-close:hover {
    background-color: var(--custom-color-lighter);
}

/* End Site Color Edits */
}
