/* ==UserStyle==
@name           灵梦御所暗色主题
@namespace      github.com/hyrious
@version        0.1.1
@description    fuck my eyes
@author         hyrious
==/UserStyle== */
@-moz-document domain("blog.reimu.net") {
/* Background Color */
body,
body:before,
.site-header {
    background-color: #202124;
}
/* Box Background Color */
.post-navigation,
.pagination,
.site-footer,
.hentry,
.page-header,
.page-content,
.comments-area,
.widecolumn {
    background-color: #292a2c;
    box-shadow: 0 1px 5px rgba(0, 0, 0, .125);
    border-radius: 4px;
}
/* Button Text Color */
button,
input[type="button"],
input[type="reset"],
input[type="submit"],
.pagination .prev,
.pagination .next,
.widget_calendar tbody a,
.widget_calendar tbody a:hover,
.widget_calendar tbody a:focus,
.page-links a,
.page-links a:hover,
.page-links a:focus,
.sticky-post {
    color: #e6e6e6;
}
/* Button Background Color */
button,
input[type="button"],
input[type="reset"],
input[type="submit"],
.pagination .prev,
.pagination .next,
.widget_calendar tbody a,
.page-links a,
.sticky-post {
    background-color: #292a2d;
    outline: 1px solid #6c6c6c
}
/* Text Color */
body,
blockquote cite,
blockquote small,
a,
.dropdown-toggle:after,
.image-navigation a:hover,
.image-navigation a:focus,
.comment-navigation a:hover,
.comment-navigation a:focus,
.widget-title,
.entry-footer a:hover,
.entry-footer a:focus,
.comment-metadata a:hover,
.comment-metadata a:focus,
.pingback .edit-link a:hover,
.pingback .edit-link a:focus,
.comment-list .reply a:hover,
.comment-list .reply a:focus,
.site-info a:hover,
.site-info a:focus {
    color: #f1f3f5;
}
/* Link Color */
.entry-content a,
.entry-summary a,
.page-content a,
.comment-content a,
.pingback .comment-body > a,
.author-description a,
.taxonomy-description a,
.textwidget a,
.entry-footer a:hover,
.comment-metadata a:hover,
.pingback .edit-link a:hover,
.comment-list .reply a:hover,
.site-info a:hover {
    color: #74c0fc;
    border-color: #74c0fc;
}
blockquote,
.logged-in-as a:hover,
.comment-author a:hover {
    border-color: #adb5bd;
}
.widget input,
.widget select,
.widget button,
.widget input[type="button"],
.widget input[type="reset"],
.widget input[type="submit"],
.widget_calendar tbody a {
    color: #6c6c6c;
    border-color: #3c3c3c;
    padding: 4px 4px;
    background-color: #1c1d1f;
}
input::placeholder {
    color: #adb5bd;
}
.entry-footer,
button,
input,
select,
textarea {
    background-color: #1c1d1f;
}
button:hover,
input:hover,
select:hover,
textarea:hover,
button:focus,
input:focus,
select:focus,
textarea:focus {
    background-color: #3c4043;
    caret-color: #e9ecef;
}
blockquote,
a:hover,
a:focus,
.main-navigation .menu-item-description,
.post-navigation .meta-nav,
.post-navigation a:hover .post-title,
.post-navigation a:focus .post-title,
.image-navigation,
.image-navigation a,
.comment-navigation,
.comment-navigation a,
.widget,
.author-heading,
.entry-footer,
.entry-footer a,
.taxonomy-description,
.page-links > .page-links-title,
.entry-caption,
.comment-author,
.comment-metadata,
.comment-metadata a,
.pingback .edit-link,
.pingback .edit-link a,
.post-password-form label,
.comment-form label,
.comment-notes,
.comment-awaiting-moderation,
.logged-in-as,
.form-allowed-tags,
.no-comments,
.site-info,
.site-info a,
.wp-caption-text,
.gallery-caption,
.comment-list .reply a,
.widecolumn label,
.widecolumn .mu_register label {
    color: #e9ecef
}
a:hover {
    border-bottom: 1px solid !important;
}
.navigation {
    margin-top: 40px;
}
.wp-pagenavi {
    border-color: rgba(255, 255, 255, .25) !important;
}
.wp-pagenavi a,
.wp-pagenavi > span {
    background-color: #1c1d1f !important;
    color: #e9ecef !important;
    border-color: rgba(255, 255, 255, .25) !important;
}
