@-moz-document domain("www.nitrotype.com") {
canvas[width="1018"] {
	position: absolute; 
  overflow: hidden; 
  clip: rect(0 0 0 0); 
  height: 1px; width: 1px; 
  margin: -1px; padding: 0; border: 0; 
}
.raceChat {
    display: none;
}
.racev3-track {
    padding-top:100px;
    position: absolute;
    top: 275px;
	width:99%;
	left:5px;
    z-index: 0;
	background-color:rgba(1, 25, 3, 0);
}
.raceLight-status {
	content: "Ready..."
}
.race-results {
    height: 450px;
}
.raceLight-tree {
	display: none;
}
.raceResults-badge {
    animation: none;
    -webkit-animation: none;
    -moz-animation: none;
}
.hypeBar-effect {
    display: none;
}
.raceLight-status {
    display: block;
    position: absolute;
    top: -550px;
    left: -520px;
    padding: 50px;
    padding-left: 500px;
    padding-right: 600px;
    z-index: 0;
    height: 350px;
}
.is-state-start {
    padding-bottom: 100px;
}
.raceChat-pickerToggle {
    display: none;
}
}

