data.css:
@-moz-document url-prefix("http://baidu.com/s"), url-prefix("http://www.baidu.com/s"), url-prefix("https://www.baidu.com/s"), url-prefix("https://baidu.com/s") {
/* 统一动画类 */
.s_ipt_wr,
#content_left .result-op,
#content_left .result,
#content_right,
.bdsug li,
.s_btn,
.bdpfmenu a,
.usermenu a {
    transition: all .3s;
}

/* 内容 */
a {
    text-decoration: none;
}

a:link {
    color: #4879BD;
}
a:visited {
    color: #bbb;
}
a:hover {
    color: #4879BD;
}
a:active {
    color: #4879BD;
}

html::-webkit-scrollbar {
    /*滚动条整体样式*/
    width: 10px;
    /*高宽分别对应横竖滚动条的尺寸*/
    height: 1px;
}
html::-webkit-scrollbar-thumb {
    /*滚动条里面小方块*/
    border-radius: 10px;
    background: #C5C5C5;
}
html::-webkit-scrollbar-track {
    /*滚动条里面轨道*/
    border-radius: 10px;
    background: #ededed;
}

body {
    font-family: Microsoft YaHei;
    color: #444;
}

#wrapper_wrapper {
    background-color: #f8f8f8;
}

#content_left {
    max-width: 1083px;
    padding-left: 130px;
}

#content_left .result-op,
#content_left .result {
    /*     width: 670px !important; */
    cursor: default;
    line-height: 1.7;
    vertical-align: top;
    margin: 0 10px 20px 10px;
    display: inline-block;
    background-color: #fff;
    padding: 15px;
    border-radius: 10px;
    box-shadow: 0 0 12px #ececec;
}

#content_left > div:hover {
    box-shadow: 0 0 12px #ccc;
}

#content_left .result-op > h3 > a,
#content_left .result > h3 > a,
#content_left .result-op > h3 > a > em,
#content_left .result > h3 > a > em {
    text-decoration: unset;
    position: relative;
}

#content_left .result-op > h3 > a:visited,
#content_left .result > h3 > a:visited,
#content_left .result-op > h3 > a:visited > em,
#content_left .result > h3 > a:visited > em {
    color: #bbb;
}

#content_left .result-op > h3 > a:after,
#content_left .result > h3 > a:after {
    content: "";
    position: absolute;
    right: 0;
    bottom: -3px;
    width: 0;
    height: 2px;
    background-color: #3676d6;
    transition: all .5s;
}

#content_left .result-op > h3 > a:hover:after,
#content_left .result > h3 > a:hover:after {
    left: 0;
    width: 100%;
}

.hint_common_restop {
    margin: 5px 0 10px 150px;
}

.c-abstract {
    font-size: 12px;
}

#content_right {
    padding: 20px 0 0 30px;
    background-color: #fff;
    border-width: 0px;
    border-radius: 10px;
    box-shadow: 0 0 12px #ececec;
    margin-right: 65px;
}

#content_right:hover {
    box-shadow: 0 0 12px #ccc;
}

#content_left img,
#content_right img {
    border-radius: 10px;
    overflow: hidden;
}

.cr-title {
    font-weight: normal;
}

.nums,
.search_tool_conter {
    margin: 0 0 0 150px;
}

.s_form {
    padding: 0 0 0 130px;
}

#result_logo,
.s_lg_img_gold_show {
    display: none;
}

#rs {
    background-color: transparent;
    margin-left: 150px;
}

#page {
    padding-left: 150px;
}

#s_tab {
    padding: 60px 0 0 145px;
}

.c-icon-tieba,
.c-icon-zhidao,
.c-icon-bear-p,
.c-icon-bear-pn {
    opacity: 0;
}

/* 头部 */
.s_ipt_wr.bg,
.s_btn_wr.bg,
#su.bg {
    border-radius: 30px;
    margin-left: 5px;
}

.s_ipt_wr {
    border-right-width: 1px;
    border-color: #cecece;
}

.s_ipt_wr:hover {
    border-color: #277bf7;
}

.s_ipt {
    margin-left: 10px;
}

#head .bdsug {
    border-radius: 20px;
    overflow: hidden;
    top: 40px;
    left: 5px;
    padding: 10px 0;
    border-width: 0px;
    box-shadow: 0 0 12px #bbb;
    animation: searchTip .3s forwards;
}

@keyframes searchTip {
    from {
        transform: translateY(20px);
        opacity: 0;
    }
    to {
        transform: translateY(0px);
        opacity: 1;
    }
}

.bdsug-feedback-wrap {
    display: none;
}

.bdsug li {
    padding: 8px 10px;
    cursor: pointer;
}

.bdsug li > b {
    font-weight: normal;
    color: #1a67da;
}

.c-tip-con,
.bdpfmenu,
.usermenu,
.s-mod-msg {
    animation: searchTip .3s forwards;
}

.soutu-layer {
    top: 44px;
    left: 5px;
    border-radius: 20px;
    overflow: hidden;
}

.bdpfmenu,
.usermenu {
    border-radius: 10px;
    overflow: hidden;
}

.toindex {
    display: none;
}

#s_msg_content {
    border-radius: 10px;
    overflow: hidden;
}

/* 底部 */
.foot-inner {
    text-align: center;
}

#help {
    float: none !important;
}

#page a,
#page strong {
    background-color: transparent;
}

#page a.n {
    background-color: #fff;

    transition: all .3s;
}

#page a .pc {
    background-color: #fff;
    transition: all .3s;
}

#page a:hover .pc,
#page a.n:hover {
    color: #fff;
    background-color: #38f;
}

/* 广告 */
.CwvaZl,
.RCFKra {
    display: none;
}

@media screen and (max-width: 1226px) {
    #content_right {
        margin-right: -25px;
        padding-left: 12px;
    }
}
}

