div#trello-root.body-board-view .badge {
    background-color: transparent;
    color: #333;
}
div#trello-root.body-board-view .badge.is-voted {
    background-color: var(--custom-green);
    border-radius: 2px;
    padding: 2px 7px;
}
div#trello-root.body-board-view .badge.is-voted span.badge-icon {
    display: none;
}
div#trello-root.body-board-view .badge.is-voted span {
    padding: 0;
    margin: 0;
    width: auto;
    font-weight: 500;
    color: var(--custom-dark);
}
div#trello-root.body-board-view .badge.is-voted span:before {
    content: '+';
}



div#trello-root.body-board-view .badge-icon {
    color: var(--custom-dark)!important;
}
div#trello-root.body-board-view .badge-icon.icon-subscribe {
    color: var(--custom-purple)!important;
    background: var(--custom-yellow);
    border-radius: 3px;
}
div#trello-root.body-board-view .js-due-date-badge {
    border-radius: 0;
    padding: 0!important;
    background: transparent; 
    display: block;
}
div#trello-root.body-board-view .js-due-date-badge:after {
    font-weight: 900;
    font-size: 80%;
    letter-spacing: .025rem;
    padding-left: 4px;
    position: relative;
    top: -1px;
}
div#trello-root.body-board-view .js-due-date-badge.is-due-soon {
    color: var(--theme-sky);
}
div#trello-root.body-board-view .js-due-date-badge.is-due-now {
    color: var(--theme-purple);  
}
div#trello-root.body-board-view .js-due-date-badge.is-due-past {
    color: var(--theme-red);
}
div#trello-root.body-board-view .js-due-date-badge.is-due-soon:after {
    content: 'GO TO GATE';
}
div#trello-root.body-board-view .js-due-date-badge.is-due-now:after {
    content: 'BOARDING';
}
div#trello-root.body-board-view .js-due-date-badge.is-due-past:after {
    content: 'PLANE TOOK OFF';
}
div#trello-root.body-board-view .badge-text {
    padding: 0;
}
div#trello-root.body-board-view .js-due-date-badge .badge-text {
    display: none;
}
div#trello-root.body-board-view .js-due-date-badge .badge-icon {
    color: inherit!important;
}
div#trello-root.body-board-view .card-detail-due-date-badge .card-detail-due-status-lozenge {
