    letter-spacing: .04rem;
    font-weight: 500;
}
div#trello-root.body-board-view .pop-over form.dpicker-widget button:not(.negate) {
    background-color: transparent;
    color: #333;
}
div#trello-root.body-board-view .pop-over button:not(.negate) {
    background-color: var(--button-default);
    color: #333;  
}
div#trello-root.body-board-view button.primary, 
div#trello-root.body-board-view button.primary, 
div#trello-root.body-board-view input[type=button].primary, 
div#trello-root.body-board-view input[type=submit].primary {
