/*General*/
body,
html {
    font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
    font-size: 16px;
    line-height: 1.4;
    color: #495057;
    text-align: center;
}
a {
    color: #0C3357;
}
a:hover {
    color: #1971c2
}
a:visited {
    color: #145591
}
font {
    font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
}
input,
select,
textarea {
    font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
    font-size: 1rem;
    border-radius: .25em;
    border: 1px solid #ced4da;
    padding: .5em;
    background-color: #fff;
    outline: 0;
    vertical-align: middle;
}
FORM#search_form INPUT[type=text] {
    padding: .5em;
    width: 80%;
    max-width: 30em;
    margin-bottom: 1em;
}
FORM#search_form INPUT[type=submit] {
    max-width: 20%;
    margin-bottom: 1em;
}
.inline-fields li:nth-child(2) {
    padding-top: 1em;
}
input[type="text"],
[type="edit"],
[name="req"] {
    box-shadow: inset 0 1px 2px #dee2e6;
}
input[type="text"]:focus,
[type="edit"]:focus,
[name="req"]:focus {
    border-color: #1971c2;
}
input[type="radio"],
input[type="checkbox"] {
    margin-bottom: .75em;
}
td[colspan='4'] input[name="s"] {

    max-width: 48em;
}
select {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    margin: 10px;
    box-shadow: inset 0 -1px 2px #dee2e6;
    padding: .5em 1em .5em .5em;
    position: relative;
    background: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='%23ced4da'><polygon points='0,0 100,0 50,50'/></svg>") no-repeat;
    background-size: 12px;
    background-position: calc(100% - 10px) 55%;
    background-repeat: no-repeat;
    padding-right: 30px;
}
select:before {
    content: 'x';
    position: absolute;

    top: 0;
    right: 0;
}

input[type="submit"],
button,
td#info h2 a,
td[bgcolor="#A9F5BC"] a {
    border: 0;
    background-color: #1971c2;
    color: #fff;
    font-size: .825rem;
