    box-shadow: 0 1px 2px 2px #dee2e6;
}
label {
    margin-right: .5em;
}
form[name="libgen"] {
    line-height: 1.8;
}
form + font[color="grey"] {
    display: inline-block;
    text-align: center;
    margin: 2rem 0 1rem;
}
font[color="#A00000"] {
    font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
