td#info {
    max-width: 40em;
    width: 100%;
    display: block;
    margin: 0 auto;
}

td#ad {
    display: none;
}
img[width="240"] {
    display: block;

    margin: 0 auto;
}
td#info {
    max-width: 40em !important;
    font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
}
td#info h1 {
    text-align: center;
}
td#info h2 a {
    display: inline-block;
    border-radius: .25em;
    text-decoration: none;
}
td[bgcolor="#F5F6CE"] {
    display: none;
}
td[bgcolor="#A9F5BC"] {
    background-color: #fff;
    margin-bottom: 1em;
    padding: .5em;
    border-bottom: 2px solid #e9ecef;
}
td[bgcolor="#A9F5BC"] a {
    color: #fff;
    display: inline-block;
    padding: .25em 2em;
    line-height: 1;
    text-decoration: none;
}
td[bgcolor="#F5F6CE"] + td {

    text-align: center;
    padding: .5em;
}
td[bgcolor="brown"] {
    background-color: #e9ecef;
}
input[size="9"] {
    width: 20em;
    margin: 0 1em;
}

/*table view*/
td[rowspan="22"] + td + td {
    font-size: 120%;
}
table[rules="cols"] {
    /*outline:10px solid red;*/
    border: 0;
    border-spacing: 0;
}
table[rules="cols"] tr,
table[rules="cols"] tbody {

    border-image-width: 0 !important;
    border: 1px solid white !important;
}
table[rules="cols"] tbody > tr > td:first-child,
td[rowspan="22"] + td {
    text-align: right;
}
td[rowspan="22"] {
    text-align: center;
    border-right: 1px solid #868e96;
}
.hashes_header {
    text-align: center;
}
#textarea-example {
    width: 10em;
}
table[rules="cols"] td[colspan="4"] {
    text-align: left !important;
    width: 40em;

    max-width: 100%;
}
a[href="https://forum.mhut.org/viewtopic.php?t=6423"] {
    text-decoration: none;
    padding: 2px 5px;
    border: 1px dashed gray;
    margin-left: 10px;
}
#main {
    max-width: 45em;
}
#main tr:nth-of-type(4) td:nth-of-type(1) {
    display: none;
}

#main tr:nth-of-type(4) td:nth-of-type(2) {
    padding: 1em;
}


#main td[colspan="2"] {

    width: 40em;
}
.record .field {
    text-align: right;
    padding-right: .5em;
    color: #adb5bd;
}
.record .field[colspan="2"] {
    text-align: left;
}
.record td {
    padding-bottom: .25em;
}
ul.record_mirrors li a,
table[rules="cols"] table[rules="cols"] a {
    display: inline-block;
    padding: .5em .75em;
    background-color: #dee2e6;
    border-radius: 3px;
    margin-bottom: .5em;
    box-shadow: 0 1px 2px #f1f3f5;
}
ul.record_mirrors li a:hover,
table[rules="cols"] table[rules="cols"] a:hover {

    background-color: #e9ecef;
}
ul.record_mirrors li:before {
    content: none;
}
ul.record_mirrors li:after {
    content: none;
}
/*Scientific articles*/
table[width="1024"] tbody td[width="200"] {
    text-transform: uppercase;
    font-size: .8em;
    color: #495057;
}
table[width="1024"] tbody td[width="300"] {

    font-size: 1.1em;
}

/*----Menu------*/
ul#menu {

    background-color: #e9ecef;
    border: 0;
    text-align: center;
    border-bottom: 1px solid #e9ecef;
    width: 100%;
    padding: 0;
    height: auto;
    border-radius: 3px;
    margin-bottom: 10px;
}
#menu li,
#menu li:hover {
    padding: 0;
    margin: 0;
    display: inline-block;
    float: none;
    border: 0;
}
#menu li:hover {
    background-color: #fff;
    color: #145591;
}
#menu li .drop,
#menu li:hover .drop {
    padding: 8px 30px;
    font-size: .85rem;
    background: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='%23adb5bd'><polygon points='0,0 100,0 50,50'/></svg>") no-repeat;
    background-size: 12px;
    background-position: calc(100% - 10px) 55%;
    background-repeat: no-repeat;
}
#menu li a {
    display: block;
    font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
    padding: .6em 2em;
    border-radius: 3px 3px 0 0;
    text-transform: capitalize;
}
#menu > li a:hover {

    color: #339af0;
    padding: .6em 2em;
    border: 0;
    box-shadow: 0 0 3px #adb5bd;
}
#menu li .drop:hover {
    padding-right: 30px;

    color: #145591;
    background: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='%23145591'><polygon points='0,0 100,0 50,50'/></svg>") no-repeat;
    background-size: 12px;
    background-position: calc(100% - 10px) 55%;
    background-repeat: no-repeat;
    box-shadow: 0 0 3px #adb5bd;
    background-color: #fff;
}

.dropdown_3lettercolumn,
.dropdown_1column,
.dropdown_2columns,
.dropdown_3columns,
.dropdown_4columns,
.dropdown_5columns,
.dropdown_6columns {
    z-index: 100;
    margin: 0px auto;
    float: left;
    position: absolute;
    top: -5px;
    left: -999em;
    text-align: left;
    padding: 5px;
    border: 0;
    /* border-top: none; */
    background: #Fff;
    box-shadow: 0 3px 3px #adb5bd;
    border-radius: 0px 5px 5px 5px;
}

#menu li ul li {

    line-height: 1.3;
    padding: 0;
    width: 98%;
    text-shadow: none;
}
#menu li ul a {
    font-family: Lato, sans-serif;
    font-size: 14px;
    padding: .25em !important;
}
#menu li:hover div a {
    color: #495057;
}
#menu li ul a:hover #menu li div a:hover #menu li li:hover a,
#menu li div div a:hover {
    box-shadow: none;
    background-color: #e7f5ff;
    color: #343a40 !important;
    text-shadow: none;
    border-radius: 0;
}
#menu li .dropdown_3lettercolumn .greybox li,
#menu li .dropdown_3lettercolumn .greybox li:hover {
    border: 0;
    padding: 0;
    text-align: center;
}
#menu li .dropdown_3lettercolumn .greybox li a {
    background-color: #e9ecef;
    border-radius: 5px;
    font-weight: bold;
}
#menu li .dropdown_3lettercolumn .greybox li a:hover {
    background-color: #339af0;
}
#menu p,
#menu h2,
#menu h3,
#menu ul li {
    font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
    line-height: 1.4;

    text-shadow: none;
}
#menu h3 {
    font-size: 15px;
    padding: .25em !important;
    margin: 0;
    border-bottom: 1px solid #adb5bd;
}

#menu li .greybox li {
    background: none;
    border: none;
    margin: 0 2px 2px 0;
    padding: 0;



    border-radius: 0;
}
#menu li .greybox li a.drop {
    background-color: #f1f3f5;
    padding: .75em;
    border-radius: 3px;
}
#menu li .greybox li:hover {
    background: none;
    border: none !important;
    color: #fff;
}
#menu li .greybox li:hover a {
    padding: 0;
    color: #fff;
    text-shadow: none;
}
#menu li .greybox li a.drop:hover {
    background-color: #339af0;
    color: #fff;
    padding: .75em;
}

table[width="600"] {
    font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
    font-size: .9rem;
    border: 0 !important;
    line-height: 1.2;
    background-color: white !important;
    border-color: white !important;
    padding: 0;
    box-shadow: 0 0 3px #adb5bd;
    border-collapse: collapse;
}
