data.css:
@-moz-document domain("op.gg") {
iframe[name="google_osd_static_frame"], .life-owner, .vm-skin, .vm-footer {
    display: none;
}


.ProfileIcon img {border-radius: 4px;}

.SummonerLayout>.Header>.PastRank>.PastRankList {
    margin: 0px;
}

.GameItem.Win>.Content {
    background-color: #fefefe;
    border-color: #fefefe;
    border: 0;
}

.GameItem.Lose>.Content {
    background-color: #fefefe;
    border-color: #fefefe;
}

.GameItem.Win>.Content>.StatsButton {
    background-color: #5383e8;
    border-color: #5383e8;
}

.GameItem.Lose>.Content>.StatsButton {
    background: #ee5a52;
    border-color: #ee5a52;
}

.GameItemList>.GameItemWrap {
    border-radius: 8px;
    overflow: hidden;
    margin-bottom: 6px;
    box-shadow: 0px 2px 3px rgba(0,0,0,0.05);
    border: 0;
}

html {
    background: #f9f9f9;
}
.SpectatorButtons .Button.SemiRound {
    background: #fff;
    border: 0px;
    border-radius: 32px;
    box-shadow: 0px 3px 7px rgba(0,0,0,0.05);
}
.SummonerRatingMedium, .sub-tier {
    background-color: #fff;
    border: 0px;
}

.sub-tier {
    margin-bottom: 20px;
    box-shadow: 0px 2px 5px rgba(0,0,0,0.05);
    border-radius: 8px;
}

.Box {
    background: #fff;
    border: 0px;
    box-shadow: 0px 2px 5px rgba(0,0,0,0.05);
    margin-bottom: 20px;
    border-radius: 8px;
    overflow: hidden;
}

.SideContent {
    padding-right: 8px;
    box-sizing: border-box;
}

.SummonerLayout>.Header>.PastRank>.PastRankList>.Item {
    background: #fff;
    border: 0px;
    box-shadow: 0px 2px 4px rgba(0,0,0,0.05);
}

.GameListContainer>.Header>.Navigation>.Actions>.Action>.ChampionMatchSearchWrap>.Header {
    background: #fff;
}

.GameListContainer>.Header>.Navigation>.Actions>.Action>.ChampionMatchSearchWrap>.Header>.Input {
    background-color: transparent;
}

.GameAverageStats {
    background: transparent;
}

.GameAverageStats tr>.MostChampion, .GameAverageStats tr>.Title, .GameAverageStats tr>.PositionStats, .GameListContainer>.Header>.Navigation>.Actions>.Action>.ChampionMatchSearchWrap {
    border-left-color: #f3f3f3;
}



.SummonerLayout>.Menu {
    border: 0px;
}

.SummonerLayout>.Menu>.MenuList>.Item.active>a {
    background: #5383e8;
    border: 0px;
    color: #fff;
}
.SummonerLayout>.Menu>.MenuList>.Item.active>a:hover {
    background: #fff;
    color: #333;
}

.SummonerLayout>.Menu>.MenuList>.Item {
    margin-right: 8px;
    box-shadow: 0px 2px 4px rgba(0,0,0,0.05);
    border-radius: 50px;
    overflow: hidden;
}


.SummonerLayout>.Menu>.MenuList>.Item>a {
    background: #fff;
    border: 0;
}

.SummonerLayout>.Header>.Profile>.Information>.FavoriteButton {
    border: 0;
}



.opscore-banner {
    display: none;
}

.Box>.Tab>.TabList>.Item.active {
    background: #fff;
    border: 0px;
}

.Box>.Tab>.TabList>.Item {
    border: 0px;
    background: #fafafa;
}

.Box>.Title+.Content, .Box>.Navigation+.Content, .Box>.Tab+.Content {
    background: #fff;
}

.ChampionBox {
    border: 0;
    margin-bottom: 2px;
}

.Box>.Title {
    border: 0;
}

.SummonersMostGameTable>.Header>.Row>.HeaderCell {
    font-size: 10px;
    padding-left: 0px;
    padding-right: 10px;
    background-color: #fff;
}

.SummonersMostGameTable>.Body>.Row.Odd>.Cell {
    background: #fff;
}

.GameItem.extended.Win>.GameDetail .MatchDetailHeader .tabHeaders {
    background: #5383e8;
}

.GameItem.extended.Win>.GameDetail .MatchDetailHeader li a {
    background: #fafafa;
    border: 0px;
}

.MatchDetailHeader ul li {
    border: 0px;
}

.GameItem.extended.Win>.Content>div.StatsButton {
    border-color: #5383e8;
}

.GameDetailTable.Result-WIN>.Content {
    border: 0px;
    background: #dfe5f3;
}

.GameDetailTable.Result-WIN>.Content>.Row.isRequester {
    background: #b4c8f1;
}

.GameDetailTableWrap>.Summary {
    background: #fff;
}

.summary-graph>.total--container>.graph--container>.win--team {
    background: #5383e8;
}

.summary-graph>.total--container>.graph--container>.lose--team {
    background: #ee5a52;
}

.GameItem.extended.Lose>.GameDetail .MatchDetailHeader .tabHeaders {
    border-color: #ee5a52;
    border: 0;
    background: #ee5a52;
}

.GameItem.extended.Lose>.Content>div.StatsButton {
    border-color: #ee5a52;
}

.GameItem.extended.Lose>.GameDetail .MatchDetailHeader li a {
    border: 0px;
    background: #fafafa;
}

.ChampionWinRatioBox, .PositionWinRatioBox {
   background: #fff;   
}
.__spSite-157 {
  filter: hue-rotate(-56deg) brightness(1.4);
}

.Button.Blue {
    border: 0px;
    background: #5383e8!important;
    color: #fff;
    border-radius: 64px;
    box-shadow: 0px 3px 8px rgba(83, 131, 232, 0.5)!important;
}
.Button.Red {
    border: 0px;
    background: #f54757!important;
    border-radius: 32px;
    box-shadow: 0px 3px 8px rgba(245, 71, 87, 0.4)!important;
}


.Button.Green, .SummonerLayout>.Menu>.MenuList>.Item.inGame.active>.SpectateTabButtonActive, .SummonerLayout>.Menu>.MenuList>.Item.inGame>.SpectateTabButtonActive {
    background: rgb(84, 217, 168);
    border: 0px!important;
    box-shadow: 0px 3px 8px rgba(98, 202, 148, .5)!important;
    border-radius: 32px;
}

.Button.Green:hover, .Button.Red:hover, .Button.Blue:hover, .SummonerLayout>.Menu>.MenuList>.Item.inGame.active>.SpectateTabButtonActive:hover, .SummonerLayout>.Menu>.MenuList>.Item.inGame>.SpectateTabButtonActive:hover {
    background: #fff!important;
    color: #333!important;
    box-shadow: 0px 3px 8px rgba(0, 0, 0, 0.05)!important;
}
.SummonerLayout>.Menu>.MenuList>.Item.inGame>.SpectateTabButtonActive:hover .icon {
    filter: invert(0.9);
}

.gnb-list-item__search button {
    line-height: 24px;
}

.gnb-list-item__search button {
    height: 24px;
}

.gnb-list-item__search input {
    padding: 4px 48px 3px 8px;
}

.gnb-list-item__search .gnb-list-item__select {
    height: 22px;
}

.gnb-list-item__search {
    height: 22px;
}

.l-menu {
    height: 38px;
}

.menu__item>a {
    padding: 0px 0px;
}

.menu {
    height: 38px;
}
}

