data.css:
@-moz-document domain("skyward-ocprod.iscorp.com") {
/* ------ Global ------ */
body {
    color: #eeeeee;
    overflow-y: auto;
    background: url("data:image/png;base64,") !important;
    background-attachment: fixed;
}

#sf_Background {
    background: none;
}


/* ------ Text ------ */
label {
    color: #eeeeee;
}

h1.sf_pageTitle {
    color: #eeeeee;
}

h2.sf_heading {
    color: #eeeeee;
}

#Calendar li {
    color: #eeeeee;
}

#Calendar li.calendarTitle div.title {
    text-shadow: none;
}

div[grid-theme="theme2"] table[grid-table="theme2"] > tbody > tr > td,
div[grid-theme="theme2"] table[grid-table="theme2"] > thead > tr > th {
    color: #eeeeee;
}

/* ------ Loading page ------ */
#dLoading > img {

    border: 0;
    vertical-align: middle;
    margin-bottom: 8px;
    margin-right: 8px;

}


/* ------ Header ------ */
#sf_HeaderWrap {
    background-color: #000000;
    border-bottom: 1px solid #112E4C;
}

#sf_BrandingArea div.sf_skywardLogo {
    background-blend-mode: exclusion;
    background-color: white;
}

#sf_EntityArea div.sf_titleArea {
    width: 108px;
    height: 16px;
    background-blend-mode: exclusion;
    background-color: white;
}

#sf_StudentListArea {
    left: 0;
}

#sf_StudentLabel {
    background-color: #000000;
}


/* ------ Buttons ------ */
a.button, #sf_ContentCollapse {
    background: #181818;
    background: -moz-linear-gradient(top, #101010 0, #202020 100%) !important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #101010), color-stop(100%, #202020)) !important;
    background: -webkit-linear-gradient(top, #101010 0, #202020 100%) !important;
    background: -o-linear-gradient(top, #101010 0, #202020 100%) !important;
    background: -ms-linear-gradient(top, #101010 0, #202020 100%) !important;
    background: linear-gradient(top, #101010 0, #202020 100%) !important;
    border-color: #404040;
    -moz-box-shadow: 0 1px 0 #202020;
    -webkit-box-shadow: 0 1px 0 #202020;
    box-shadow: 0 1px 0 #202020;
    color: #1a5da0;
}

a.button[disabled] {
    background: #505050;
    background: -moz-linear-gradient(top, #303030 0, #404040 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #303030), color-stop(100%, #404040));
    background: -webkit-linear-gradient(top, #303030 0, #404040 100%);
    background: -o-linear-gradient(top, #303030 0, #404040 100%);
    background: -ms-linear-gradient(top, #303030 0, #404040 100%);
    background: linear-gradient(top, #303030 0, #404040 100%);
    border-color: #303030 #404040 #606060;
    color: #9a9a9a;
}

#lContentCollapse {
	border-bottom: initial;
	border-left: initial;
	border-right: initial;
}


/* ------ Navigation Menu ------ */
#sf_navMenu,
#sf_navIcon {
    border: 2px solid #112E4C;
}

#sf_navMenu li,
#sf_navIcon li {
    background: #112E4C;
    background: -moz-linear-gradient(top, #101010 0, #181818 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #101010), color-stop(100%, #181818));
    background: -webkit-linear-gradient(top, #101010 0, #181818 100%);
    background: -o-linear-gradient(top, #101010 0, #181818 100%);
    background: -ms-linear-gradient(top, #101010 0, #181818 100%);
    background: linear-gradient(top, #101010 0, #181818 100%);
    border-left: none;
    border-right: none;
    border-top: 1px solid #112E4C;
    text-shadow: none;
}

#sf_navMenu li a.sf_navMenuItem, #sf_navIcon li a.sf_navMenuItem {
    padding: 8px 8px 8px 8px;
    border: none;
}

#sf_navMenu li.selected a.sf_navMenuItem, #sf_navIcon li.selected a.sf_navMenuItem {
    border: none;
}

#sf_navMenu li a.sf_navMenuItem:hover,
#sf_navIcon li a.sf_navMenuItem:hover,
#sf_navMenu li a.sf_navMenuItem:focus,
#sf_navIcon li a.sf_navMenuItem:focus {
    border: none;
    background: #505050;
    background: -moz-linear-gradient(top, #404040 0, #606060 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #404040), color-stop(100%, #606060));
    background: -webkit-linear-gradient(top, #404040 0, #606060 100%);
    background: -o-linear-gradient(top, #404040 0, #606060 100%);
    background: -ms-linear-gradient(top, #404040 0, #606060 100%);
    background: linear-gradient(top, #404040 0, #606060 100%);
    color: #fff;
    text-shadow: 0 1px 1px #373737;
    text-decoration: none;
}

#sf_navMenu li a.sf_navMenuItem,
#sf_navIcon li a.sf_navMenuItem {
    color: #1b63aa;
}

#sf_navMenu li.first,
#sf_navIcon li {
    border-top: none;
}

#sf_navMenu li.last,
#sf_navIcon li {
    border-bottom: none;
}


/* ------ Main Column ------ */
#sf_ContentWrap.sf_showColumns #sf_ContentBodyWrap {
    background: rgba(0, 0, 0, 0.5);
    border: none;
}

#sf_ContentWrap.sf_showColumns #sf_ContentRightWrap {
    border-left: 1px solid #112E4C;
}

#sf_UtilityArea div.sf_utilityAreaBG {
    border: 1px solid #112E4C;
    -moz-box-shadow: 0 1px 2px #112E4C;
    -webkit-box-shadow: 0 1px 2px #112E4C;
    box-shadow: 0 1px 2px #112E4C;
    background-blend-mode: exclusion;
    background-color: white;
}

#sf_UtilityTable > li.sf_utilLink {
    background: none;
}

#sf_UtilityTable > li.sf_utilUser {
    color: #eeeeee;
    font-weight: bold;
}


/* ------ Menu ------ */
#home_menuTable {
    border: 1px solid #112E4C;
    -moz-box-shadow: 0 1px 2px #112E4C;
    -webkit-box-shadow: 0 1px 2px #112E4C;
    box-shadow: 0 1px 2px #112E4C;
    background-blend-mode: exclusion;
    background-color: white;
}

.sf_menuWrap {
    background-color: #181818;
    border: 1px solid #112E4C;
    -moz-box-shadow: 0 3px 3px #112E4C;
    -webkit-box-shadow: 0 3px 3px #112E4C;
    box-shadow: 0 3px 3px #112E4C;
}


/* ------ Content Boxes ------ */
#sf_OuterWrap fieldset {
    background-color: #101010;
    border: 1px solid #202020;
}

div[grid-theme="theme2"] {
    background-color: #101010;
    border: 1px solid #202020;
}

div[grid-theme="theme2"] table[grid-table="theme2"] {
    border: 1px solid #101010;
}

div[grid-theme="summary"] {
    background: #202020;
    background: -moz-linear-gradient(top, #202020 0, #101010 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #202020), color-stop(100%, #101010));
    background: -webkit-linear-gradient(top, #202020 0, #101010 100%);
    background: -o-linear-gradient(top, #202020 0, #101010 100%);
    background: -ms-linear-gradient(top, #202020 0, #101010 100%);
    background: linear-gradient(top, #202020 0, #101010 100%);
    border: 1px solid #505050;
    color: #eeeeee;
}

div.sfTag {
    background-color: #101010;
    border: 1px solid #101010;
    color: #eeeeee;
}

.jspDrag {
    background: #1B63AA;
}


/* ------ Tables ------ */
div[grid-theme="default"] table[grid-table="default"] > tbody > tr > td,
div[grid-theme="default"] table[grid-table="default"] > thead > tr > th {
    border-right: 1px solid #626262;
    color: #888888;
    background: #101010;
}

div[grid-theme="default"] table[grid-table="default"][zebra="true"] > tbody > tr.even > td {
    background-color: #101010;
    border-bottom: 1px solid #101010;
}

div[grid-theme="default"] table[grid-table="default"][zebra="true"] > tbody > tr.odd > td {
    background-color: #161616;
}


#sf_OuterWrap td.cCl {
    background-color: #101010 !important;
    border-bottom: 1px solid #626262 !important;
    padding: 3px 4px 3px 2px !important;
}

.aC {
    background-color: #161616 !important;
}

.eWs {
    background-color: #101010 !important;
}

div[grid-theme="default"] {
    background-color: #202020;
    border: 1px solid #d4d4d4;
}


/* ------ Table Borders ------ */
#grid_studentContactInfo302988 > tbody > tr > td > div {
    background-color: #181818 !important;
}

.gridFooter {
    background-color: #202020;
}


/* ------ Calendar ------ */
.sf_maskWrap[theme="loading"] .sf_mask {
    background: none;
}

.fc td,
.fc th {
    background-color: #202020;
    color: #eeeeee;
}

.fc-widget-header,
.fc-widget-content {
    border: 1px solid #626262;
}


/* ------ Calendar popup ------ */
#calhead {
    background-color: #141414;
    border-color: #303030;
}

#calbody {
    background: #f5f9ff;
    background: -moz-linear-gradient(top,#303030 0,#141414 100%);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#303030),color-stop(100%,#141414));
    background: -webkit-linear-gradient(top,#303030 0,#141414 100%);
    background: -o-linear-gradient(top,#303030 0,#141414 100%);
    background: -ms-linear-gradient(top,#303030 0,#141414 100%);
    background: linear-gradient(top,#303030 0,#141414 100%);
    border-color: #404040;
    -moz-box-shadow: 0 0 10px 0 #111;
    -webkit-box-shadow: 0 0 10px 0 #111;
    box-shadow: 0 0 10px 0 #111;
}

#caldays span {
    background: #303030;
    background: -moz-linear-gradient(top,#303030 0,#141414 100%);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#303030),color-stop(100%,#141414));
    background: -webkit-linear-gradient(top,#303030 0,#141414 100%);
    background: -o-linear-gradient(top,#303030 0,#141414 100%);
    background: -ms-linear-gradient(top,#303030 0,#141414 100%);
    background: linear-gradient(top,#303030 0,#141414 100%);
}

a.caloff {
    background: #cfcfcf;
    background: -moz-linear-gradient(top,#404040 0,#202020 100%);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#404040),color-stop(100%,#202020));
    background: -webkit-linear-gradient(top,#404040 0,#202020 100%);
    background: -o-linear-gradient(top,#404040 0,#202020 100%);
    background: -ms-linear-gradient(top,#404040 0,#202020 100%);
    background: linear-gradient(top,#404040 0,#202020 100%);
}

a.calsunday {
    background-color: #606060;
    border-left: 1px solid transparent;
    color: #900;
}

.calweek {
    border-bottom: 1px solid #404040;
    border-top: 1px solid #303030;
}

.calweek a {
    border-left: 1px solid #303030;
    border-right: 1px solid #404040;
    color: #eeeeee;
    text-shadow: 1px 1px 1px #141414;
}


/* ------ Attendance ------ */
.fusioncharts-container > svg > g > rect {
    fill: #141414 !important;
}


/* ------ Dialog ------ */
.sf_DialogWrap {
    background-color: #181818;
    border: 1px solid #101010;
    -moz-box-shadow: 1px 2px 10px #101010;
    -webkit-box-shadow: 1px 2px 10px #101010;
    box-shadow: 1px 2px 10px #101010;
}

.sf_DialogDataWrap {
    border: 1px solid #303030;
    border-bottom-color: #303030;
}

.sf_DialogButtons {
    background-color: #101010;
    border-top: 1px solid #202020;
}

.sf_DialogTitle {
    color: #eeeeee;
}

.sf_DialogHtml {
    color: #eeeeee;
}

.sf_dialog .sf_DialogData, #msgBodyCol {
    background-color: #141414;
}

.msgActions {
    background-color: #181818;
}

.sf_message .sf_DialogHtml {
    color: #eeeeee;
}

.sf_pointLeft div.d1 {
    border-right-color: #404040;
}

.sf_pointLeft div.d2 {
    border-right-color: #303030;
}

.sf_pointLeft div.d3 {
    border-right-color: #303030;
}

.sf_pointRight div.d1 {
    border-left-color: #404040;
}

.sf_pointRight div.d2 {
    border-left-color: #303030;
}

.sf_pointRight div.d3 {
    border-left-color: #303030;
}


/* ------ Cluetip ------ */
.cluetip-default {
    background-color: transparent;
}
.cluetip-default #cluetip-outer {
    background-color: #191919;
    color: #eeeeee;
    border-radius: 5px;
}

.cluetip-default h3#cluetip-title {
    background-color: #141414;
    color: #eeeeee;
    border-radius: 5px 5px 0 0;
}

/* ------ FusionCharts ------ */
/* --- Text --- */
.fusioncharts-caption {
    color: #eeeeee !important;
    fill: #eeeeee !important;

}

/* --- Tooltip --- */
span#fusioncharts-tooltip-element > span {
	color: #ffffff !important;
    background-color: #000000 !important;
	border-color: #404040 !important;
	opacity: 0.8;
}

/* ------ Userstyles.org Settings ------ */
/*[[newIcons]]*/
/*[[visTableBorders]]*/
/*[[visUpcAbs]]*/
/*[[visMissing]]*/
/*[[visEvents]]*/

/* --- Text color setting --- */
a {
    color: /*[[primaryColor]]*/;
}

#sf_navMenu li a.sf_navMenuItem, #sf_navIcon li a.sf_navMenuItem {
    color: /*[[primaryColor]]*/;
}

#sf_StudentLabel {
    color: /*[[primaryColor]]*/;
}

a.button {
    color: /*[[primaryColor]]*/;
}

#Calendar li.calendarTitle div.title {
    color: /*[[primaryColor]]*/;
}

/* --- Border color setting --- */
tr, td, th {
	border-color: /*[[secondaryColor]]*/ !important;
}

#sf_HeaderWrap {
    border-bottom: 1px solid /*[[secondaryColor]]*/;
}

a.button {
    border-color: /*[[secondaryColor]]*/;
}

#sf_navMenu,
#sf_navIcon {
    border: 2px solid /*[[secondaryColor]]*/;
}

#sf_navMenu li, #sf_navIcon li {
    border-top: 1px solid /*[[secondaryColor]]*/;
}
  
#sf_ContentWrap.sf_showColumns #sf_ContentLeftWrap {
    border-right: 1px solid /*[[secondaryColor]]*/;
}

#sf_ContentWrap.sf_showColumns #sf_ContentRightWrap {
    border-left: 1px solid /*[[secondaryColor]]*/;
}

#sf_UtilityArea div.sf_utilityAreaBG {
    border: 1px solid /*[[secondaryColor]]*/;
    -moz-box-shadow: 0 1px 2px /*[[secondaryColor]]*/;
    -webkit-box-shadow: 0 1px 2px /*[[secondaryColor]]*/;
    box-shadow: 0 1px 2px /*[[secondaryColor]]*/;
}

#home_menuTable {
    border: 1px solid /*[[secondaryColor]]*/;
    -moz-box-shadow: 0 1px 2px /*[[secondaryColor]]*/;
    -webkit-box-shadow: 0 1px 2px /*[[secondaryColor]]*/;
    box-shadow: 0 1px 2px /*[[secondaryColor]]*/;
}

div.sfTag {
    border: 1px solid /*[[secondaryColor]]*/;
}

#Calendar li {
    border-bottom: 1px solid /*[[secondaryColor]]*/;
}

#Calendar li.calendarTitle {
    border-bottom: 2px solid /*[[secondaryColor]]*/;
}

.sf_menuWrap {
    border: 1px solid /*[[secondaryColor]]*/;
    -moz-box-shadow: 0 3px 3px /*[[secondaryColor]]*/;
    -webkit-box-shadow: 0 3px 3px /*[[secondaryColor]]*/;
    box-shadow: 0 3px 3px /*[[secondaryColor]]*/;
}

div[grid-theme="default"] {
    border: 1px solid /*[[secondaryColor]]*/;
}

body .bZe > td, #sf_OuterWrap .bZe > td, .dialog .bZe > td, td.bZe {
    border-bottom: 1px solid /*[[secondaryColor]]*/ !important;
}

div[grid-theme=default] table[grid-table=default] {
	border: none;
    border-color: /*[[secondaryColor]]*/;
}

div[grid-theme=default] table[grid-table=default]>thead>tr>th {
    border-bottom: 1px solid /*[[secondaryColor]]*/;
}

div[grid-theme="default"] table[grid-table="default"] > tbody > tr > td, div[grid-theme="default"] table[grid-table="default"] > thead > tr > th {
    border-right: 1px solid /*[[secondaryColor]]*/;
}

.eWs {
    border-right: 1px solid /*[[secondaryColor]]*/ !important;
}

#sf_OuterWrap td.cCl {
    border-bottom: 1px solid /*[[secondaryColor]]*/ !important;
}

div[grid-theme="summary"] {
    border: 1px solid /*[[secondaryColor]]*/;
}

div[grid-theme=summary] table[grid-table=summary]>thead>tr>th {
    border-bottom: 1px solid /*[[secondaryColor]]*/;
}

/* --- Scrollbar color setting --- */
.jspDrag {
    background: /*[[primaryColor]]*/;
}

/* A weird invisible hack Skyward applies where border is the same color as the odd table background (keep for now) */
div[grid-theme=default] table[grid-table=default][zebra=true]>tbody>tr.odd>td {
    border-bottom: 1px solid #161616;
}
}

