/* ==UserStyle==
@name         Money Dashboard Dark
@namespace    github.com/DaemonExMachina/userstyles
@description  Staying on top of your money is easy... *on the eyes*
@version      1.2.1
@author       DaemonExMachina <dev@daemonexmachina.com>
@license      CC-BY-SA-4.0
==/UserStyle== */

@-moz-document domain("www.moneydashboard.com") {
  /* Hero screen */

  .hero,
  .main-section,
  .main-section.grey {
    background-color : var(--bg-1);
  }

  .header-container,
  .signup-reminder {
    background-color : var(--bg-2);
  }

  .media-carousel,
  .perk, .value,
  .featured-testimonial-col.featured-content,
  .featured-testimonial-col.featured-testimonial-content {
    background-color : var(--bg-3);
  }

  .hero,
  .title-2,
  .title-4,
  .section-title,
  .backup-title,
  .footer-link:hover,
  .footer-title,
  .copyright-link {
    color : var(--text-1);
  }

  .hero-title-second,
  .app-download-text,
  .body, .navbar-link,
  .media-carousel-title,
  .section-subtitle.text-center,
  .backup-subtitle,
  .footer-link,
  .body-small {
    color : var(--text-2);
  }

  img[src='https://assets.website-files.com/5b4c81d9d95543fc4f1cc63a/5b4cb910d95543bc4a1d0027_mdb_horizontal_dark.svg'],
  img[src='https://assets.website-files.com/5b4c81d9d95543fc4f1cc63a/5b4cc4e668e07c34265e8596_mdb_icon_dark.svg'],
  .media-link img {
    filter : invert(1);
  }

  .value img {
    filter : invert(.75) hue-rotate(170deg) saturate(2);
  }

  .goal-button.goal-1 {
    color            : #e1f5fe;
    background-color : #1f688a;
  }

  .goal-button.goal-2 {
    color            : #e0f2f1;
    background-color : #1d5a57;
  }

  .goal-button.goal-3 {
    color            : #f3e5f5;
    background-color : #66336d;
  }

  .goal-button.goal-4 {
    color            : #fff3e0;
    background-color : #745e3b;
  }

  .goal-button.goal-5 {
    color            : #ffd8d7;
    background-color : #753533;
  }

  .goal-button.goal-1:hover,
  .goal-button.goal-2:hover,
  .goal-button.goal-3:hover,
  .goal-button.goal-4:hover,
  .goal-button.goal-5:hover {
    background-image      : linear-gradient(rgba(1, 1, 1, .5), rgba(1, 1, 1, .5));
    background-blend-mode : multiply;
  }

  .text-field,
  .text-field:focus {
    color            : var(--text-1);
    background-color : var(--bg-1);
    border-color     : var(--bg-3);
  }

  .text-field::placeholder {
    color : var(--text-3);
  }
}

@-moz-document domain("my.moneydashboard.com") {
  :root {
    --bg-1       : #222;
    --bg-2       : #333;
    --bg-3       : #444;
    --text-1     : #fff;
    --text-2     : #ccc;
    --text-3     : #999;
    --text-red   : #ef5350;
    --text-green : #4caf50;
  }

  html, body,
  *:not(form) > md-content.md-default-theme,
  *:not(form) > md-content {
    color            : var(--text-1);
    background-color : var(--bg-1);
  }

  md-content.md-default-theme,
  md-content,
  .page-header > .content,
  .page-header h1 {
    color : var(--text-2);
  }

  .card,
  .cardContainer {
    background-color : var(--bg-2);
  }

  md-icon,
  .button md-icon,
  .button md-icon > svg,
  .button.icon md-icon > svg,
  md-icon.button.icon,
  .page-header > .content .help-icon,
  .button img {
    fill : var(--text-3);
  }

  /****************************************************************************************/

  /* Login screen */

  .login-page__sidebar {
    color            : var(--text-1);
    background-color : var(--bg-1);
  }

  .login-page__content .subtitle {
    color : var(--text-1);
  }

  .login-page__content .inputs md-checkbox,
  .login-page__content .inputs .passwordHint,
  .regulationStatement,
  .password-creation-hints,
  .password-creation-hints__description {
    color : var(--text-3);
  }

  .login-page__content .inputs md-input-container .eye {
    filter : invert(1);
  }

  .password-creation-hints__criteria md-icon {
    fill : var(--text-3);
  }

  md-checkbox.md-default-theme:not(.md-checked) .md-icon,
  md-checkbox:not(.md-checked) .md-icon,
  .password-creation-hints {
    border-color : var(--text-3);
  }

  .cookie-control-dialog .title,
