  }

  .input-container label {
    color : var(--text-3);
  }

  .input-container input:disabled,
  [disabled] md-input-container.md-default-theme .md-input,
  [disabled] md-input-container .md-input,
  md-input-container.md-default-theme .md-input[disabled],
