  }

  /****************************************************************************************/

  /* Support dialog */

  #supportDialog .popular-articles p {
    color : var(--text-2);
  }

  #supportDialog .search.card .query {
    color : var(--text-1);
  }

  /****************************************************************************************/

  /* Dialogs */

  md-dialog .navbar {
    background-color : var(--bg-3);
  }

  md-dialog .navbar .title {
    color : var(--text-1);
  }

  md-dialog img.close-button, md-dialog img.back-button {
