/* ============================================================= */
/* SEGMENTED BUTTONS */
/* ============================================================= */

.segmented-button input[type="radio"] {
  width: 0px;
  height: 20px;
  display: none;
}

.segmented-button label {
	display: -moz-inline-box;
	-moz-box-orient: vertical;
	display: inline-block;
	vertical-align: middle;
	text-shadow: white;
	background: #c6c6c6; 
	background: -moz-linear-gradient(top,  #e8e8e8 0%, #d8d8d8 50%, #c6c6c6 51%, #c6c6c6 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#e8e8e8), color-stop(50%,#d8d8d8), color-stop(51%,#c6c6c6), 	color-stop(100%,#c6c6c6)); 
	background: -webkit-linear-gradient(top,  #e8e8e8 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: -o-linear-gradient(top,  #e8e8e8 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: -ms-linear-gradient(top,  #e8e8e8 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: linear-gradient(to bottom,  #e8e8e8 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%); 
	border: 1px solid #7c7c7c;
	color: #303030;
	padding: 3px 12px 5px 12px;
	margin: 0;
	font-size: 0.85em;
	cursor: pointer;
	-moz-border-radius: 0px;
	-webkit-border-radius: 0px;
	-o-border-radius: 0px;
	-ms-border-radius: 0px;
	border-radius: 0px;
	margin-right: -5px;
}

.segmented-button label {
	*display: inline;
}

.segmented-button label:hover {
	background: -moz-linear-gradient(top,  #e8e8e8 0%, #c6c6c6 50%, #c6c6c6 51%, #c6c6c6 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#e8e8e8), color-stop(50%,#c6c6c6), color-stop(51%,#c6c6c6), 	color-stop(100%,#c6c6c6)); 
	background: -webkit-linear-gradient(top,  #e8e8e8 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: -o-linear-gradient(top,  #e8e8e8 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: -ms-linear-gradient(top,  #e8e8e8 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: linear-gradient(to bottom,  #e8e8e8 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%); 
}

.segmented-button label:active, 
.segmented-button label.active {
	background: -moz-linear-gradient(top,  #c6c6c6 0%,#c6c6c6 50%,#c6c6c6 51%,#e8e8e8 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#c6c6c6), color-stop(50%,#c6c6c6), color-stop(51%,#c6c6c6), 	color-stop(100%,#e8e8e8)); 
	background: -webkit-linear-gradient(top,  #c6c6c6 0%,#c6c6c6 50%,#c6c6c6 51%,#e8e8e8 100%); 
	background: -o-linear-gradient(top,  #c6c6c6 0%,#c6c6c6 50%,#c6c6c6 51%,#e8e8e8 100%); 
	background: -ms-linear-gradient(top,  #c6c6c6 0%,#c6c6c6 50%,#c6c6c6 51%,#e8e8e8 100%); 
	background: linear-gradient(to bottom,  #c6c6c6 0%,#c6c6c6 50%,#c6c6c6 51%,#e8e8e8 100%); 
}

.segmented-button label:disabled, 
.segmented-button label.disabled {
	/* Disabled, white to light gray */
	background: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #ffffff), color-stop(100%, #efefef));
	background: -webkit-linear-gradient(#ffffff, #efefef);
	background: -moz-linear-gradient(#ffffff, #efefef);
	background: -o-linear-gradient(#ffffff, #efefef);
	background: -ms-linear-gradient(#ffffff, #efefef);
	background: linear-gradient(#ffffff, #efefef);
	cursor: default;
	color: #b2b2b2;
	border-color: #7c7c7c;
	-moz-box-shadow: none;
	-webkit-box-shadow: none;
	-o-box-shadow: none;
	box-shadow: none;
}

/* Set first button rounded corners on left */
.segmented-button label:first-of-type {
	-moz-border-radius-topleft: 4px;
	-webkit-border-top-left-radius: 4px;
	-o-border-top-left-radius: 4px;
	-ms-border-top-left-radius: 4px;
	border-top-left-radius: 4px;
	-moz-border-radius-bottomleft: 4px;
	-webkit-border-bottom-left-radius: 4px;
	-o-border-bottom-left-radius: 4px;
	-ms-border-bottom-left-radius: 4px;
	border-bottom-left-radius: 4px;
}

/* set last button rounded corners on right */
.segmented-button label:last-of-type {
	-moz-border-radius-topright: 4px;
	-webkit-border-top-right-radius: 4px;
	-o-border-top-right-radius: 4px;
	-ms-border-top-right-radius: 4px;
	border-top-right-radius: 4px;
	-moz-border-radius-bottomright: 4px;
	-webkit-border-bottom-right-radius: 4px;
	-o-border-bottom-right-radius: 4px;
	-ms-border-bottom-right-radius: 4px;
	border-bottom-right-radius: 4px;
}

.segmented-button input:checked + label, 
.segmented-button label.selected {
  color: #ffffff;
}

.segmented-button-blue input:checked + label {
	cursor: default;
  	background: #3c78b5; 
	background: -moz-linear-gradient(top,  #3c78b5 0%, #114070 50%, #114070 51%, #114070 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#3c78b5), color-stop(50%,#114070), color-stop(51%,#114070), color-stop(100%,#114070)); 
	background: -webkit-linear-gradient(top,  #3c78b5 0%,#114070 50%,#114070 51%,#114070 100%); 
