/* **************************
	STANDARD INPUTS
*************************** */
/* standard input field */
input[type="text"],
input[type="password"],
input[type="number"],
input[type="tel"],
input[type="date"],
input[type="time"] {
  border: 1px solid #7c7c7c;
  border-radius: 5px;
  box-shadow: inset 1px 1px 1px #c6c6c6;
  font-family: "Open Sans", Verdana, Arial, Helvetica, sans-serif;
  color: #303030;
  padding: 6px 8px;
}
input[type="text"]:focus,
input[type="password"]:focus,
textarea:focus {
  box-shadow: 0px 0px 5px #3c78b5;
  border: #3c78b5 1px solid;
}
input[type="text"]:disabled,
input[type="password"]:disabled {
  border: #d8d8d8 1px solid;
  color: #979797;
  background-color: #fff;
  font-family: "Open Sans", Verdana, Arial, Helvetica, sans-serif;
}
/* add to class name for long input fields */
input[type="text"].long,
input[type="password"].long {
  width: 200px;
}
/* Small text box, primarily for pagination */
input[type="text"].compact,
input[type="number"].compact {
  font-size: 0.75em;
  padding: 0px 1px;
  text-align: center;
  border-radius: 3px;
  width: 4em;
  color: #303030;
}
/* standard text area */
textarea {
  border: 1px solid #7c7c7c;
  padding: 4px 6px;
  border-radius: 5px;
  width: 300px;
  height: 4.5em;
  margin: 4px 4px 4px 0px;
  box-shadow: inset 1px 1px 1px #c6c6c6;
  font-family: "Open Sans", Verdana, Arial, Helvetica, sans-serif;
  color: #303030;
}
textarea.error {
  border: 1px solid #cd0000;
  background-color: #fbe8e8;
}
/* Form validation messages, add error to the class name for field that fail validation */
input[type="text"].error,
input[type="password"].error,
input[type="number"].error,
input[type="tel"].error {
  border: 1px solid #cd0000;
  background-color: #fbe8e8;
}
input[type="text"].number {
  text-align: right;
  width: 90px;
}
input[type="text"].date {
  width: 90px;
}
/* use for short text fields */
input[type="text"].short {
  width: 90px;
}
/* use for long text fields */
input[type="text"].long {
  width: 300px;
}
/* Modal */
.modal-background {
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: #000;
  opacity: 0.5;
  -webkit-opacity: 0.5;
  -moz-opacity: 0.5;
  filter: alpha(opacity=50);
  z-index: 100000;
  padding:1px;
}
.modal-content {
  background-color: white;
  border-radius: 8px;
  -webkit-border-radius: 8px;
  -moz-border-radius: 8px;
  box-shadow: 0 0 20px 0 #222;
  -webkit-box-shadow: 0 0 20px 0 #222;
  -moz-box-shadow: 0 0 20px 0 #222;
  display: none;
  position: absolute;
  z-index: 100000;
  margin: auto;
  position: absolute;
  top: 50%;
  left: 0;
  bottom: 50%;
  right: 0;
}
.modal-active {
  display: block;
}
.modal-close {
  float: right;
  margin-top: -10px;
  margin-right: -10px;
  cursor: pointer;
  color: #fff;
  border: 1px solid #aeaeae;
  border-radius: 30px;
  background: #605f61;
  font-size: 31px;
  font-weight: bold;
  display: inline-block;
  line-height: 0px;
  padding: 12px 3px;
}
.modal-close:before {
  content: "×";
}

