/* --------------------------------------- */
/* CSS ICONS                               */
/* --------------------------------------- */

.css-icons {
	position: relative;
	overflow: hidden;
	list-style: none;
	padding: 0;
	margin: 0 0 0.25em;
	display: inline-block;
}

	.css-icons a:link,
	.css-icons a:visited {
		display: block;
		border: 0;
		padding-left: 28px;
		color: #3c78b5;
	}

	.css-icons a:hover,
	.css-icons a:focus,
	.css-icons a:active {
		color: #114070;
		background: transparent;
	}

	.css-icons:before,
	.css-icons:after,
	.css-icons a:before,
	.css-icons a:after {
		content: "";
		position: absolute;
		top: 50%;
		left: 0;
	}

	.css-icons a:before,
	.css-icons a:after {
		margin: -8px 0 0;
		background: #3c78b5;
	}

	.css-icons a:hover:before,
	.css-icons a:focus:before,
	.css-icons a:active:before {
		background: #114070;
	}


/* NEXT PAGE CSS ICON
------------------------------- */
.next-page, .last-page {
	text-align: right;
	width: 15px;
	margin-left: 3px;
	vertical-align: middle;
}

	.next-page a:before {
		left: 1px;
		border: 5px solid transparent;
		border-width: 5px 7px;
		border-left-color: #3c78b5;
		margin-top: -4px;
		background: transparent;
	}

	.next-page a:after {
		left: 7px;
		border: 5px solid transparent;
		border-width: 5px 7px;
		border-left-color: #3c78b5;
		margin-top: -4px;
		background: transparent;
	}

	.next-page a:hover:before,
	.next-page a:focus:before,
	.next-page a:active:before {
		border-left-color: #114070;
		background: transparent;
	}

	.next-page a:hover:after,
	.next-page a:focus:after,
	.next-page a:active:after {
		border-left-color: #114070;
	}

	/* Disabled */
	.next-page.page-end a:before,
	.next-page.page-end a:after,
	.next-page.page-end a:hover:before,
	.next-page.page-end a:focus:before,
	.next-page.page-end a:active:before,
	.next-page.page-end a:hover:after,
	.next-page.page-end a:focus:after,
	.next-page.page-end a:active:after {
		border-left-color: #bfbfbf;
		cursor: default;
	}

/* PREVIOUS PAGE CSS ICON
------------------------------- */

.previous-page, .first-page {
	text-align: left;
	width: 15px;
	margin-right: 1px;
	vertical-align: middle;
}

	.previous-page a:before {
		left: -6px;
		border: 5px solid transparent;
