/*loading*/
#field-progress {
	position: absolute;
	top: 0;
	left: 0;
	bottom: 0;
	right: 0;
	background-color: transparent;
	z-index: 99;
	text-align:center;
}
/* Spinner */

#field-load {
	background-color: #606060;
	border: 2px solid #ffffff;
	padding: 8px 4px 8px 4px;
	border-radius: 8px;
	vertical-align: middle;
	min-height: 32px;
	box-shadow: 0px 0px 3px 3px rgba(0, 0, 0, 0.3);
	position: relative;
	min-width: 150px;
	max-width: 550px;
	opacity: 1;
	transition-property: opacity;
	transition-duration: 0.3s;
	transition-timing-function: ease;
	margin:315px auto;
	display:inline-block;
}

#field-load span {
	display: block;
	color: #e8e8e8;
	font-weight: 600;
	text-transform: uppercase;
	font-size: 0.85em;
	height: 30px;
	line-height: 30px;
	padding-left: 50px;
	padding-right: 20px;
}
/* Construct the animated circles, usable in IE11 */
#circle {
	width: 30px;
	height: 30px;
	position: relative;
	float: left;
	margin: 0 8px 0 8px;
}

#circle div {
	width: 100%;
	height: 100%;
	position: absolute;
	left: 0;
	top: 0;
}

#circle div:before {
	content: "";
	display: block;
	margin: 0 auto;
	width: 20%;
	height: 20%;
	background-color: #3ecff4;
	border-radius: 100%;
	animation: spin-circleBounceDelay 1.3s infinite ease-in-out both;
}

#circle div:nth-child(2) {
	transform: rotate(30deg);
}

#circle div:nth-child(3) {
	transform: rotate(60deg);
}

#circle div:nth-child(4) {
	transform: rotate(90deg);
}

#circle div:nth-child(5) {
	transform: rotate(120deg);
}

#circle div:nth-child(6) {
	transform: rotate(150deg);
}

#circle div:nth-child(7) {
	transform: rotate(180deg);
}

#circle div:nth-child(8) {
	transform: rotate(210deg);
}

#circle div:nth-child(9) {
	transform: rotate(240deg);
}

#circle div:nth-child(10) {
	transform: rotate(270deg);
}

#circle div:nth-child(11) {
	transform: rotate(300deg);
}

#circle div:nth-child(12) {
	transform: rotate(330deg);
}

#circle div:nth-child(2):before {
	animation-delay: -1.1s;
}

#circle div:nth-child(3):before {
	animation-delay: -1s;
}

#circle div:nth-child(4):before {
	animation-delay: -0.9s;
}

#circle div:nth-child(5):before {
	animation-delay: -0.8s;
}

#circle div:nth-child(6):before {
	animation-delay: -0.7s;
}

#circle div:nth-child(7):before {
	animation-delay: -0.6s;
}

#circle div:nth-child(8):before {
	animation-delay: -0.5s;
}

#circle div:nth-child(9):before {
	animation-delay: -0.4s;
}

#circle div:nth-child(10):before {
	animation-delay: -0.3s;
}

#circle div:nth-child(11):before {
	animation-delay: -0.2s;
}

#circle div:nth-child(12):before {
	animation-delay: -0.1s;
}

@-webkit-keyframes spin-circleBounceDelay {
	0%, 80%, 100% {
		transform: scale(0.5);
	}

	40% {
		transform: scale(1);
	}
}

@keyframes spin-circleBounceDelay {
	0%, 80%, 100% {
		transform: scale(0.5);
	}

	40% {
		transform: scale(1);
	}
}
