/* Full size one-item dropdown menu */
select[size="1"], select:not(size) {
	border: 1px solid #7c7c7c;
	padding: 5px 10px;
	background: #c6c6c6;
	background: -moz-linear-gradient(top, #f0f0f0 0%, #d8d8d8 50%, #c6c6c6 51%, #c6c6c6 100%);
